</head>
<body>
    <div id="control-panel">
        <label for="slider-n">n</label>
        <input type="range" id="slider-n" min="1" max="20" step="1" value="1">
        <input type="number" id="input-n" min="1" max="20" step="1" value="1">
    </div>

    <div id="visualization-area">
        <div id="function-plot-container">
            <canvas id="canvas-function"></canvas>
            <div id="formula-label">$$y = \frac{2}{x}$$</div>
        </div>
        <div id="sum-plot-container">
            <canvas id="canvas-sum"></canvas>
        </div>
    </div>

    <script>
        // Color array for rectangles
        const colors = [
            '#ffff80', '#98fb98', '#7fffd4', '#00ffff', '#87cefa', 
            '#6495ed', '#9370db', '#8a2be2', '#ff69b4', '#ff1493',
            '#ff7f50', '#ffa500', '#ffd700', '#adff2f', '#00ff00',
            '#00ced1', '#4169e1', '#9400d3', '#ff00ff', '#dc143c'
        ];

        // Get controls
        const sliderN = document.getElementById('slider-n');
        const inputN = document.getElementById('input-n');

        // Synchronize slider and input
        sliderN.addEventListener('input', () => {
            inputN.value = sliderN.value;
            updateVisualizations();
        });

        inputN.addEventListener('input', () => {
            sliderN.value = inputN.value;
            updateVisualizations();
        });

        // Function plot sketch
        const functionSketch = (p) => {
            let n = 1;

            p.setup = () => {
                const canvas = p.createCanvas(100, 100);
                canvas.parent('canvas-function');
                p.windowResized();
            };

            p.windowResized = () => {
                const container = document.getElementById('function-plot-container');
                p.resizeCanvas(container.offsetWidth, container.offsetHeight);
                p.redraw();
            };

            p.draw = () => {
                p.clear();
                p.background(255);

                // Set up coordinate mapping
                const xMin = 0, xMax = 10.5, yMin = 0, yMax = 2.2;
                const margin = 40;

                const mapX = (x) => p.map(x, xMin, xMax, margin, p.width - margin);
                const mapY = (y) => p.map(y, yMin, yMax, p.height - margin, margin);

                // Draw axes
                p.stroke(0);
                p.strokeWeight(2);
                p.line(mapX(0), mapY(0), mapX(xMax), mapY(0)); // X-axis
                p.line(mapX(0), mapY(0), mapX(0), mapY(yMax)); // Y-axis

                // Draw tick marks and labels
                p.strokeWeight(1);
                p.textAlign(p.CENTER, p.TOP);
                p.textSize(12);
                for (let x = 0; x <= 10; x++) {
                    p.line(mapX(x), mapY(0), mapX(x), mapY(0) + 5);
                    p.text(x, mapX(x), mapY(0) + 8);
                }

                p.textAlign(p.RIGHT, p.CENTER);
                for (let y = 0; y <= 2; y++) {
                    p.line(mapX(0) - 5, mapY(y), mapX(0), mapY(y));
                    p.text(y, mapX(0) - 8, mapY(y));
                }

                // Draw rectangles
                for (let k = 1; k <= n; k++) {
                    p.fill(colors[(k - 1) % colors.length]);
                    p.noStroke();
                    const x1 = mapX(k);
                    const x2 = mapX(k + 1);
                    const y1 = mapY(2 / k);
                    const y2 = mapY(2 / (k + 1));
                    p.rect(x1, y1, x2 - x1, y2 - y1);

                    // Draw dashed lines for k <= 3
                    if (k <= 3) {
                        p.stroke(150);
                        p.strokeWeight(1);
                        p.drawingContext.setLineDash([5, 5]);
                        p.line(mapX(k), mapY(0), mapX(k), mapY(2 / k));
                        p.drawingContext.setLineDash([]);
                    }
                }

                // Draw function y = 2/x
                p.stroke(0, 0, 255);
                p.strokeWeight(2);
                p.noFill();
                p.beginShape();
                for (let x = 0.5; x <= 10; x += 0.1) {
                    p.vertex(mapX(x), mapY(2 / x));
                }
                p.endShape();
            };

            p.updateN = (newN) => {
                n = newN;
                p.redraw();
            };
        };

        // Summation plot sketch
        const sumSketch = (p) => {
            let n = 1;

            p.setup = () => {
                const canvas = p.createCanvas(100, 100);
                canvas.parent('canvas-sum');
                p.windowResized();
            };

            p.windowResized = () => {
                const container = document.getElementById('sum-plot-container');
                p.resizeCanvas(container.offsetWidth, container.offsetHeight);
                p.redraw();
            };

            p.draw = () => {
                p.clear();
                p.background(255);

                // Set up coordinate mapping
                const xMin = 0, xMax = 1.5, yMin = 0, yMax = 2.2;
                const margin = 40;

                const mapX = (x) => p.map(x, xMin, xMax, margin, p.width - margin);
                const mapY = (y) => p.map(y, yMin, yMax, p.height - margin, margin);

                // Draw axes
                p.stroke(0);
                p.strokeWeight(2);
                p.line(mapX(0), mapY(0), mapX(xMax), mapY(0)); // X-axis
                p.line(mapX(0), mapY(0), mapX(0), mapY(yMax)); // Y-axis

                // Draw tick marks and labels
                p.strokeWeight(1);
                p.textAlign(p.CENTER, p.TOP);
                p.textSize(12);
                for (let x = 0; x <= 1; x++) {
                    p.line(mapX(x), mapY(0), mapX(x), mapY(0) + 5);
                    p.text(x, mapX(x), mapY(0) + 8);
                }

                p.textAlign(p.RIGHT, p.CENTER);
                for (let y = 0; y <= 2; y++) {
                    p.line(mapX(0) - 5, mapY(y), mapX(0), mapY(y));
                    p.text(y, mapX(0) - 8, mapY(y));
                }

                // Draw stacked rectangles
                let yStack = 0;
                for (let k = n; k >= 1; k--) {
                    const h = 2/k - 2/(k+1);
                    p.fill(colors[(k - 1) % colors.length]);
                    p.noStroke();
                    const x1 = mapX(0);
                    const x2 = mapX(1);
                    const y1 = mapY(yStack);
                    const y2 = mapY(yStack + h);
                    p.rect(x1, y2, x2 - x1, y1 - y2);
                    yStack += h;
                }
            };

            p.updateN = (newN) => {
                n = newN;
                p.redraw();
            };
        };

        // Create p5 instances
        let functionP5, sumP5;

        function initializeVisualizations() {
            functionP5 = new p5(functionSketch);
            sumP5 = new p5(sumSketch);
            functionP5.noLoop();
            sumP5.noLoop();
        }

        function updateVisualizations() {
            const n = parseInt(sliderN.value);
            if (functionP5) functionP5.updateN(n);
            if (sumP5) sumP5.updateN(n);
        }

        // Initialize when page loads
        window.addEventListener('load', () => {
            initializeVisualizations();
            updateVisualizations();
        });

        // Handle window resize
        window.addEventListener('resize', () => {
            if (functionP5) functionP5.windowResized();
            if (sumP5) sumP5.windowResized();
        });
    </script>
</body>
</html>
