            box-shadow: 2px 0 5px rgba(0,0,0,0.1);
            overflow-y: auto;
        }

        #visualization-area {
            flex: 1;
            padding: 20px;
            display: flex;
            align-items: center;
            justify-content: center;
        }

        #plot-container {
            width: 100%;
            height: 600px;
            background-color: #fff;
            box-shadow: 0 2px 5px rgba(0,0,0,0.1);
        }

        .button-group {
            margin-bottom: 25px;
            display: flex;
            gap: 10px;
        }

        .button-group button {
            flex: 1;
            padding: 10px;
