            font-size: 14px;
        }

        h3 {
            margin-top: 0;
            margin-bottom: 15px;
            color: #333;
        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <h3>Model Selection</h3>
            <div class="button-group">
                <button id="btn-monod" class="active">Monod</button>
                <button id="btn-si">substrate inhibition</button>
            </div>

            <h3>View Selection</h3>
            <div class="button-group">
                <button id="btn-bifurcation" class="active">bifurcation diagram</button>
                <button id="btn-nontrivial">nontrivial steady state</button>
            </div>

            <h3>Parameters</h3>
            <div class="slider-container">
                <label for="slider-x2f">x<sub>2f</sub></label>
                <input type="range" id="slider-x2f" min="1" max="10" step="0.1" value="4.0">
                <span id="value-x2f">4.0</span>
            </div>

            <div class="slider-container">
                <label for="slider-umax">μ<sub>max</sub></label>
                <input type="range" id="slider-umax" min="0.1" max="2" step="0.01" value="0.6">
                <span id="value-umax">0.60</span>
            </div>

            <div class="slider-container">
                <label for="slider-km">k<sub>m</sub></label>
                <input type="range" id="slider-km" min="0.01" max="1" step="0.01" value="0.12">
                <span id="value-km">0.12</span>
            </div>

            <div class="slider-container" id="k1-container" style="display: none;">
                <label for="slider-k1">k<sub>1</sub></label>
                <input type="range" id="slider-k1" min="0.1" max="2" step="0.0001" value="0.4545">
                <span id="value-k1">0.4545</span>
            </div>

            <div class="slider-container">
                <label for="slider-ds">D<sub>s</sub></label>
                <input type="range" id="slider-ds" min="0.01" max="1.0" step="0.01" value="0.6">
                <span id="value-ds">0.60</span>
            </div>
        </div>

        <div id="visualization-area">
            <div id="plot-container"></div>
        </div>
    </div>

    <script>
        // Global state
        let currentModel = 'monod';
        let currentView = 'bifurcation';

        // Parameter values
        const params = {
            x2f: 4.0,
            umax: 0.6,
            km: 0.12,
            k1: 0.4545,
            ds: 0.6
        };

        // Model functions
        function monodD(x2) {
            return params.umax * x2 / (params.km + x2);
        }

        function siD(x2) {
            return params.umax * x2 / (params.km + x2 + params.k1 * x2 * x2);
        }

        function monodMu(x2) {
            return params.umax * x2 / (params.km + x2);
        }

        function siMu(x2) {
            return params.umax * x2 / (params.km + x2 + params.k1 * x2 * x2);
        }

        function monodDDerivative(x2) {
            return params.umax * params.km / Math.pow(params.km + x2, 2);
        }

        function siDDerivative(x2) {
            const denom = params.km + x2 + params.k1 * x2 * x2;
            const numerator = params.umax * (params.km - params.k1 * x2 * x2);
            return numerator / (denom * denom);
        }

        // Numerical root finding
        function findRoots(func, target, xmin, xmax, n = 1000) {
            const roots = [];
            const dx = (xmax - xmin) / n;

            for (let i = 0; i < n; i++) {
                const x1 = xmin + i * dx;
                const x2 = x1 + dx;
                const y1 = func(x1) - target;
                const y2 = func(x2) - target;

                if (y1 * y2 < 0) {
                    // Bisection method
                    let a = x1, b = x2;
                    for (let j = 0; j < 50; j++) {
                        const mid = (a + b) / 2;
                        const ymid = func(mid) - target;
                        if (Math.abs(ymid) < 1e-10) break;
                        if (y1 * ymid < 0) {
                            b = mid;
                        } else {
                            a = mid;
                        }
                    }
                    roots.push((a + b) / 2);
                }
            }

            return roots;
        }

        function drawBifurcationDiagram() {
            const traces = [];

            // Generate x2 values
            const x2Values = [];
            for (let i = 0; i <= 200; i++) {
                x2Values.push(i * params.x2f / 200);
            }

            if (currentModel === 'monod') {
                // Monod model - always stable
                const dValues = x2Values.map(x2 => monodD(x2));
                traces.push({
                    x: dValues,
                    y: x2Values,
                    mode: 'lines',
                    line: { color: 'blue', width: 2 },
                    name: 'Stable',
                    showlegend: false
                });
            } else {
                // SI model - stable and unstable regions
                const x2Crit = Math.sqrt(params.km / params.k1);
                const stableX2 = x2Values.filter(x2 => x2 <= x2Crit);
                const unstableX2 = x2Values.filter(x2 => x2 > x2Crit);

                if (stableX2.length > 0) {
                    traces.push({
                        x: stableX2.map(x2 => siD(x2)),
                        y: stableX2,
                        mode: 'lines',
                        line: { color: 'blue', width: 2 },
                        name: 'Stable',
                        showlegend: false
                    });
                }

                if (unstableX2.length > 0) {
                    traces.push({
                        x: unstableX2.map(x2 => siD(x2)),
                        y: unstableX2,
                        mode: 'lines',
                        line: { color: 'red', width: 2, dash: 'dash' },
                        name: 'Unstable',
                        showlegend: false
                    });
                }
            }

            // Trivial steady state line
            const dBif = currentModel === 'monod' ? monodD(params.x2f) : siD(params.x2f);

            traces.push({
                x: [0, dBif],
                y: [params.x2f, params.x2f],
                mode: 'lines',
                line: { color: 'red', width: 2, dash: 'dash' },
                showlegend: false
            });

            traces.push({
                x: [dBif, 1.0],
                y: [params.x2f, params.x2f],
                mode: 'lines',
                line: { color: 'blue', width: 2 },
                showlegend: false
            });

            // Find intersection points
            const func = currentModel === 'monod' ? monodD : siD;
            const derivFunc = currentModel === 'monod' ? monodDDerivative : siDDerivative;
            const roots = findRoots(func, params.ds, 0.001, params.x2f - 0.001);

            // Plot intersection points
            for (const root of roots) {
                const isStable = derivFunc(root) > 0;
                traces.push({
                    x: [params.ds],
                    y: [root],
                    mode: 'markers',
                    marker: {
                        color: isStable ? 'magenta' : 'cyan',
                        size: 10
                    },
                    showlegend: false
                });
            }

            // Trivial solution point
            traces.push({
                x: [params.ds],
                y: [params.x2f],
                mode: 'markers',
                marker: {
                    color: 'green',
                    size: 10
                },
                showlegend: false
            });

            const layout = {
                xaxis: {
                    title: 'dilution rate',
                    range: [0, 1.0]
                },
                yaxis: {
                    title: 'X₂',
                    range: [0, 6.0]
                },
                shapes: [{
                    type: 'line',
                    x0: params.ds,
                    y0: 0,
                    x1: params.ds,
                    y1: 6.0,
                    line: {
                        color: 'gray',
                        width: 1,
                        dash: 'dot'
                    }
                }],
                margin: { l: 60, r: 30, t: 30, b: 60 }
            };

            Plotly.newPlot('plot-container', traces, layout);
        }

        function drawNontrivialStatePlot() {
            const traces = [];

            // Generate x2 values
            const x2Values = [];
            for (let i = 0; i <= 200; i++) {
                x2Values.push(i * 7 / 200);
            }

            // Plot μ(x2) curve
            const func = currentModel === 'monod' ? monodMu : siMu;
            const muValues = x2Values.map(x2 => func(x2));

            traces.push({
                x: x2Values,
                y: muValues,
                mode: 'lines',
                line: { color: 'blue', width: 2 },
                name: 'μ(x₂)',
                showlegend: false
            });

            // Horizontal line at Ds
            traces.push({
                x: [0, 7],
                y: [params.ds, params.ds],
                mode: 'lines',
                line: { color: 'gray', width: 2 },
                showlegend: false
            });

            if (currentModel === 'si') {
                // Bistability region for SI model
                const dUpper = params.umax / (1 + 2 * Math.sqrt(params.k1 * params.km));
                const dLower = siMu(params.x2f);

                traces.push({
                    x: [0, 7],
                    y: [dUpper, dUpper],
                    mode: 'lines',
                    line: { color: 'red', width: 1, dash: 'dash' },
                    showlegend: false
                });

                traces.push({
                    x: [0, 7],
                    y: [dLower, dLower],
                    mode: 'lines',
                    line: { color: 'red', width: 1, dash: 'dash' },
                    fill: 'tonexty',
                    fillcolor: 'rgba(255, 200, 150, 0.3)',
                    showlegend: false
                });
            }

            // Find intersection points
            const roots = findRoots(func, params.ds, 0.001, 7);

            // Plot intersection points
            for (const root of roots) {
                const derivative = currentModel === 'monod' ? monodDDerivative(root) : siDDerivative(root);
                const isStable = derivative > 0;
                traces.push({
                    x: [root],
                    y: [params.ds],
                    mode: 'markers',
                    marker: {
                        color: isStable ? 'magenta' : 'cyan',
                        size: 10
                    },
                    showlegend: false
                });
            }

            const layout = {
                xaxis: {
                    title: 'substrate concentration',
                    range: [0, 7]
                },
                yaxis: {
                    title: 'specific growth rate coefficient',
                    range: [0, 1]
                },
                margin: { l: 60, r: 30, t: 30, b: 60 }
            };

            Plotly.newPlot('plot-container', traces, layout);
        }

        function updatePlot() {
            if (currentView === 'bifurcation') {
                drawBifurcationDiagram();
            } else {
                drawNontrivialStatePlot();
            }
        }

        // Event listeners
        document.getElementById('btn-monod').addEventListener('click', function() {
            currentModel = 'monod';
            this.classList.add('active');
            document.getElementById('btn-si').classList.remove('active');
            document.getElementById('k1-container').style.display = 'none';
            updatePlot();
        });

        document.getElementById('btn-si').addEventListener('click', function() {
            currentModel = 'si';
            this.classList.add('active');
            document.getElementById('btn-monod').classList.remove('active');
            document.getElementById('k1-container').style.display = 'block';
            updatePlot();
        });

        document.getElementById('btn-bifurcation').addEventListener('click', function() {
            currentView = 'bifurcation';
            this.classList.add('active');
            document.getElementById('btn-nontrivial').classList.remove('active');
            updatePlot();
        });

        document.getElementById('btn-nontrivial').addEventListener('click', function() {
            currentView = 'nontrivial';
            this.classList.add('active');
            document.getElementById('btn-bifurcation').classList.remove('active');
            updatePlot();
        });

        // Slider event listeners
        document.getElementById('slider-x2f').addEventListener('input', function() {
            params.x2f = parseFloat(this.value);
            document.getElementById('value-x2f').textContent = params.x2f.toFixed(1);
            updatePlot();
        });

        document.getElementById('slider-umax').addEventListener('input', function() {
            params.umax = parseFloat(this.value);
            document.getElementById('value-umax').textContent = params.umax.toFixed(2);
            updatePlot();
        });

        document.getElementById('slider-km').addEventListener('input', function() {
            params.km = parseFloat(this.value);
            document.getElementById('value-km').textContent = params.km.toFixed(2);
            updatePlot();
        });

        document.getElementById('slider-k1').addEventListener('input', function() {
            params.k1 = parseFloat(this.value);
            document.getElementById('value-k1').textContent = params.k1.toFixed(4);
            updatePlot();
        });

        document.getElementById('slider-ds').addEventListener('input', function() {
            params.ds = parseFloat(this.value);
            document.getElementById('value-ds').textContent = params.ds.toFixed(2);
            updatePlot();
        });

        // Initial setup
        updatePlot();
    </script>
</body>
</html>
