            </div>

            <div class="slider-group">
                <label for="slider-latitude">latitude</label>
                <input type="range" id="slider-latitude" min="0" max="90" step="0.01" value="51.4789">
                <span id="value-latitude">51.48°</span>
            </div>

            <div class="slider-group">
                <label for="slider-time">time of day</label>
                <input type="range" id="slider-time" min="0" max="24" step="0.01" value="12">
                <span id="value-time">12.00</span>
            </div>
        </div>

        <div id="main-content">
            <div id="globe-container"></div>
            <div id="plots-container">
                <div id="plot-instant"></div>
                <div id="plot-legend">
                    <div class="legend-item">
                        <div class="legend-line red"></div>
                        <span>equator</span>
                    </div>
                    <div class="legend-item">
                        <div class="legend-line black"></div>
                        <span>latitude</span>
                    </div>
                    <div class="legend-item">
                        <div class="legend-line blue"></div>
                        <span>north pole</span>
                    </div>
                </div>
                <div id="plot-cumulative"></div>
            </div>
        </div>
    </div>

    <script>
        // Three.js setup
        const scene = new THREE.Scene();
        scene.background = new THREE.Color(0x87CEEB);

        const camera = new THREE.PerspectiveCamera(45, 1, 0.1, 1000);
        camera.position.set(5, 3, 5);

        const renderer = new THREE.WebGLRenderer({ antialias: true });
        const globeContainer = document.getElementById('globe-container');
        renderer.setSize(globeContainer.clientWidth, globeContainer.clientHeight);
        globeContainer.appendChild(renderer.domElement);

        const controls = new THREE.OrbitControls(camera, renderer.domElement);
        controls.enableDamping = true;
        controls.dampingFactor = 0.05;

        // Create Earth texture
        const textureCanvas = document.createElement('canvas');
        textureCanvas.width = 1024;
        textureCanvas.height = 512;
        const ctx = textureCanvas.getContext('2d');

        // Draw ocean
        ctx.fillStyle = '#4169E1';
        ctx.fillRect(0, 0, textureCanvas.width, textureCanvas.height);

        // Draw simplified continents
        ctx.fillStyle = '#228B22';
        // Africa
        ctx.beginPath();
        ctx.ellipse(550, 280, 80, 120, 0, 0, Math.PI * 2);
        ctx.fill();
        // Europe
        ctx.beginPath();
        ctx.ellipse(540, 150, 60, 40, 0, 0, Math.PI * 2);
        ctx.fill();
        // Asia
        ctx.beginPath();
        ctx.ellipse(700, 180, 120, 80, 0, 0, Math.PI * 2);
        ctx.fill();
        // Americas
        ctx.beginPath();
        ctx.ellipse(300, 256, 80, 150, 0, 0, Math.PI * 2);
        ctx.fill();
        // Australia
        ctx.beginPath();
        ctx.ellipse(800, 380, 50, 30, 0, 0, Math.PI * 2);
        ctx.fill();

        const earthTexture = new THREE.CanvasTexture(textureCanvas);

        // Create Earth
        const earthGeometry = new THREE.SphereGeometry(1, 64, 32);
        const earthMaterial = new THREE.MeshPhongMaterial({ 
            map: earthTexture,
            shininess: 10
        });
        const earth = new THREE.Mesh(earthGeometry, earthMaterial);
        scene.add(earth);

        // Add wireframe grid
        const wireframeGeometry = new THREE.SphereGeometry(1.01, 24, 12);
        const wireframeMaterial = new THREE.MeshBasicMaterial({ 
            color: 0xffffff, 
            wireframe: true,
            opacity: 0.2,
            transparent: true
        });
        const wireframe = new THREE.Mesh(wireframeGeometry, wireframeMaterial);
        scene.add(wireframe);

        // Lighting
        const ambientLight = new THREE.AmbientLight(0x404040, 0.5);
        scene.add(ambientLight);

        const sunLight = new THREE.DirectionalLight(0xffffff, 1);
        scene.add(sunLight);

        // Lines and vectors
        const equatorLine = createLatitudeLine(0, 0x00ff00, false);
        scene.add(equatorLine);

        let selectedLatLine = createLatitudeLine(51.4789, 0x0000ff, true);
        scene.add(selectedLatLine);

        // Axis arrow
        const axisArrow = new THREE.ArrowHelper(
            new THREE.Vector3(0, 1, 0),
            new THREE.Vector3(0, 0, 0),
            2,
            0x000000,
            0.3,
            0.2
        );
        scene.add(axisArrow);

        // Surface normal arrow
        let normalArrow = new THREE.ArrowHelper(
            new THREE.Vector3(1, 0, 0),
            new THREE.Vector3(1, 0, 0),
            0.5,
            0x000000,
            0.15,
            0.1
        );
        scene.add(normalArrow);

        // Sun direction arrow
        let sunArrow = new THREE.ArrowHelper(
            new THREE.Vector3(0, 0, -1),
            new THREE.Vector3(1, 0, 0),
            0.5,
            0xff0000,
            0.15,
            0.1
        );
        scene.add(sunArrow);

        function createLatitudeLine(latitude, color, dashed) {
            const latRad = latitude * Math.PI / 180;
            const radius = Math.cos(latRad);
            const y = Math.sin(latRad);

            const points = [];
            for (let i = 0; i <= 64; i++) {
                const angle = (i / 64) * Math.PI * 2;
                points.push(new THREE.Vector3(
                    radius * Math.cos(angle),
                    y,
                    radius * Math.sin(angle)
                ));
            }

            const geometry = new THREE.BufferGeometry().setFromPoints(points);
            const material = dashed ? 
                new THREE.LineDashedMaterial({ color: color, dashSize: 0.05, gapSize: 0.05 }) :
                new THREE.LineBasicMaterial({ color: color });

            const line = new THREE.Line(geometry, material);
            if (dashed) line.computeLineDistances();

            return line;
        }

        // Plotly setup
        const instantLayout = {
            title: 'energy',
            xaxis: { title: 'time (hr)', range: [0, 24] },
            yaxis: { title: 'watts/m²', range: [0, 1400] },
            showlegend: false,
            margin: { t: 40, r: 40, b: 40, l: 60 }
        };

        const cumulativeLayout = {
            title: 'energy',
            xaxis: { title: 'time (hr)', range: [0, 24] },
            yaxis: { title: 'watts/m²', range: [0, 14000] },
            showlegend: false,
            margin: { t: 40, r: 40, b: 40, l: 60 }
        };

        // Initialize plots
        Plotly.newPlot('plot-instant', [], instantLayout, {displayModeBar: false});
        Plotly.newPlot('plot-cumulative', [], cumulativeLayout, {displayModeBar: false});

        // Update function
        function update() {
            const season = parseFloat(document.getElementById('slider-season').value);
            const latitude = parseFloat(document.getElementById('slider-latitude').value);
            const timeOfDay = parseFloat(document.getElementById('slider-time').value);

            // Update display values
            document.getElementById('value-season').textContent = season.toFixed(3);
            document.getElementById('value-latitude').textContent = latitude.toFixed(2) + '°';
            document.getElementById('value-time').textContent = timeOfDay.toFixed(2);

            // Calculate solar declination
            const declination = 23.5 * Math.cos(2 * Math.PI * season);
            const declinationRad = declination * Math.PI / 180;

            // Update Earth rotation
            earth.rotation.y = (timeOfDay / 24) * 2 * Math.PI;
            wireframe.rotation.y = earth.rotation.y;

            // Update sun light direction
            sunLight.position.set(0, Math.sin(declinationRad), -Math.cos(declinationRad));
            sunLight.position.normalize();

            // Update selected latitude line
            scene.remove(selectedLatLine);
            selectedLatLine = createLatitudeLine(latitude, 0x0000ff, true);
            scene.add(selectedLatLine);

            // Update surface normal arrow
            const latRad = latitude * Math.PI / 180;
            const normalPos = new THREE.Vector3(
                Math.cos(latRad),
                Math.sin(latRad),
                0
            );
            normalArrow.position.copy(normalPos);
            normalArrow.setDirection(normalPos.clone().normalize());

            // Update sun arrow
            sunArrow.position.copy(normalPos);
            sunArrow.setDirection(sunLight.position.clone().normalize());

            // Calculate energy data
            const hours = Array.from({length: 25}, (_, i) => i);
            const energyData = {
                equator: [],
                latitude: [],
                northPole: []
            };

            const latitudes = {
                equator: 0,
                latitude: latitude,
                northPole: 90
            };

            for (const [key, lat] of Object.entries(latitudes)) {
                const latRad = lat * Math.PI / 180;

                for (let hour of hours) {
                    const HRA = (hour - 12) * 15 * Math.PI / 180;
                    const energy = 1361 * Math.max(0, 
                        Math.sin(latRad) * Math.sin(declinationRad) + 
                        Math.cos(latRad) * Math.cos(declinationRad) * Math.cos(HRA)
                    );
                    energyData[key].push(energy);
                }
            }

            // Calculate cumulative energy
            const cumulativeData = {
                equator: [0],
                latitude: [0],
                northPole: [0]
            };

            for (const key of Object.keys(energyData)) {
                for (let i = 1; i < energyData[key].length; i++) {
                    cumulativeData[key].push(
                        cumulativeData[key][i-1] + energyData[key][i]
                    );
                }
            }

            // Update plots
            const instantTraces = [
                {
                    x: hours,
                    y: energyData.equator,
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: 'red', width: 2 }
                },
                {
                    x: hours,
                    y: energyData.latitude,
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: 'black', width: 2 }
                },
                {
                    x: hours,
                    y: energyData.northPole,
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: 'blue', width: 2 }
                },
                {
                    x: [timeOfDay],
                    y: [energyData.latitude[Math.floor(timeOfDay)]],
                    type: 'scatter',
                    mode: 'markers',
                    marker: { color: 'black', size: 8 }
                }
            ];

            const cumulativeTraces = [
                {
                    x: hours,
                    y: cumulativeData.equator,
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: 'red', width: 2 }
                },
                {
                    x: hours,
                    y: cumulativeData.latitude,
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: 'black', width: 2 }
                },
                {
                    x: hours,
                    y: cumulativeData.northPole,
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: 'blue', width: 2 }
                }
            ];

            Plotly.react('plot-instant', instantTraces, instantLayout);
            Plotly.react('plot-cumulative', cumulativeTraces, cumulativeLayout);
        }

        // Event listeners
        document.getElementById('slider-season').addEventListener('input', update);
        document.getElementById('slider-latitude').addEventListener('input', update);
        document.getElementById('slider-time').addEventListener('input', update);

        // Animation loop
        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        // Handle resize
        window.addEventListener('resize', () => {
            const width = globeContainer.clientWidth;
            const height = globeContainer.clientHeight;
            camera.aspect = width / height;
            camera.updateProjectionMatrix();
            renderer.setSize(width, height);
        });

        // Initialize
        update();
        animate();
    </script>
</body>
</html>
