        function drawTriangle(data) {
            let padding = 60;

            // Calculate actual values
            let aVal = data.a.n / data.a.d;
            let bVal = data.b.n / data.b.d;

            // Calculate scale
            let scale = min(
                (width - 2 * padding) / aVal,
                (height - 2 * padding) / bVal
            );

            // Scale dimensions
            let scaledA = aVal * scale;
            let scaledB = bVal * scale;

            // Center the triangle
            let startX = (width - scaledA) / 2;
            let startY = (height - scaledB) / 2 + scaledB;

            // Draw triangle
            fill(255, 242, 229); // #FFF2E5
            stroke(0);
            strokeWeight(2);

            beginShape();
            vertex(startX, startY); // Bottom left (right angle)
            vertex(startX + scaledA, startY); // Bottom right
            vertex(startX, startY - scaledB); // Top left
            endShape(CLOSE);

            // Draw labels
            noStroke();

            // Label a (horizontal side)
            drawFraction(data.a, startX + scaledA/2, startY + 30, 0, 16);

            // Label b (vertical side)
            drawFraction(data.b, startX - 40, startY - scaledB/2, 0, 16);

            // Label c (hypotenuse)
            let hypAngle = atan2(-scaledB, scaledA);
            let hypMidX = startX + scaledA/2;
            let hypMidY = startY - scaledB/2;
            let offsetX = -sin(hypAngle) * 30;
            let offsetY = cos(hypAngle) * 30;
            drawFraction(data.c, hypMidX + offsetX, hypMidY + offsetY, hypAngle, 16);
        }

        function drawFraction(fraction, x, y, rotation, fontSize) {
            push();
            translate(x, y);
            rotate(rotation);

            textAlign(CENTER);
            textSize(fontSize);
            fill(139, 90, 43); // #8B5A2B

            // Format numbers with spaces
            let numStr = fraction.n.toString().replace(/\B(?=(\d{3})+(?!\d))/g, " ");
            let denStr = fraction.d.toString().replace(/\B(?=(\d{3})+(?!\d))/g, " ");

            // Measure text widths
            let numWidth = textWidth(numStr);
            let denWidth = textWidth(denStr);
            let lineWidth = max(numWidth, denWidth) + 10;

            // Draw numerator
            text(numStr, 0, -fontSize/2);

            // Draw fraction line
            stroke(139, 90, 43);
            strokeWeight(1);
            line(-lineWidth/2, 0, lineWidth/2, 0);

            // Draw denominator
            noStroke();
            text(denStr, 0, fontSize);

            pop();
        }

        function drawNotCongruent(n) {
            textAlign(CENTER, CENTER);
            textSize(20);
            fill(255, 0, 0); // Red
            text(n + " is not a congruent number", width/2, height/2);
        }
