    <script>
        // Global variables for Three.js scenes
        let scenePrimal, cameraPrimal, rendererPrimal, controlsPrimal;
        let sceneDual, cameraDual, rendererDual, controlsDual;
        let primalMeshes = {};
        let dualMeshes = {};

        // Initialize Three.js scenes
        function initThreeJS() {
            // Initialize primal plot
            const containerPrimal = document.getElementById('plot-primal');
            scenePrimal = new THREE.Scene();
            scenePrimal.background = new THREE.Color(0xffffff);

            cameraPrimal = new THREE.PerspectiveCamera(50, containerPrimal.clientWidth / containerPrimal.clientHeight, 0.1, 1000);
            cameraPrimal.position.set(15, 15, 15);

            rendererPrimal = new THREE.WebGLRenderer({ antialias: true });
            rendererPrimal.setSize(containerPrimal.clientWidth, containerPrimal.clientHeight);
            containerPrimal.appendChild(rendererPrimal.domElement);

            controlsPrimal = new THREE.OrbitControls(cameraPrimal, rendererPrimal.domElement);
            controlsPrimal.enableDamping = true;

            // Initialize dual plot
            const containerDual = document.getElementById('plot-dual');
            sceneDual = new THREE.Scene();
            sceneDual.background = new THREE.Color(0xffffff);

            cameraDual = new THREE.PerspectiveCamera(50, containerDual.clientWidth / containerDual.clientHeight, 0.1, 1000);
            cameraDual.position.set(15, 15, 15);

            rendererDual = new THREE.WebGLRenderer({ antialias: true });
            rendererDual.setSize(containerDual.clientWidth, containerDual.clientHeight);
            containerDual.appendChild(rendererDual.domElement);

            controlsDual = new THREE.OrbitControls(cameraDual, rendererDual.domElement);
            controlsDual.enableDamping = true;

            // Add lighting and helpers to both scenes
            [scenePrimal, sceneDual].forEach(scene => {
                scene.add(new THREE.AmbientLight(0xffffff, 0.6));
                scene.add(new THREE.DirectionalLight(0xffffff, 0.4));

                // Add axes
                const axesHelper = new THREE.AxesHelper(10);
                scene.add(axesHelper);

                // Add bounding box
                const boxGeometry = new THREE.BoxGeometry(10, 10, 10);
                const boxHelper = new THREE.BoxHelper(new THREE.Mesh(boxGeometry));
                boxHelper.material.color = new THREE.Color(0x333333);
                scene.add(boxHelper);
            });
        }

        // Get slider values
        function getValues() {
            return {
                alpha: parseFloat(document.getElementById('slider-alpha').value),
                beta: parseFloat(document.getElementById('slider-beta').value),
                w: parseFloat(document.getElementById('slider-w').value),
                r: parseFloat(document.getElementById('slider-r').value),
                Q_in: parseFloat(document.getElementById('slider-Q').value),
                C_in: parseFloat(document.getElementById('slider-C').value)
            };
        }

        // Solve primal problem
        function solvePrimal(alpha, beta, w, r, Q_in) {
            const k = (w * beta) / (r * alpha);
            const x_p = Math.pow(Q_in / Math.pow(k, beta), 1 / (alpha + beta));
            const y_p = x_p * k;
            const C_out = w * x_p + r * y_p;
            return { x: x_p, y: y_p, C: C_out };
        }

        // Solve dual problem
        function solveDual(alpha, beta, w, r, C_in) {
            const x_d = (C_in * alpha) / (w * (alpha + beta));
            const y_d = (C_in * beta) / (r * (alpha + beta));
            const Q_out = Math.pow(x_d, alpha) * Math.pow(y_d, beta);
            return { x: x_d, y: y_d, Q: Q_out };
        }

        // Update primal plot
        function updatePrimalPlot(values, primalSolution) {
            // Clear previous meshes
            Object.values(primalMeshes).forEach(mesh => scenePrimal.remove(mesh));
            primalMeshes = {};

            // Create isoquant curtain
            const isoquantGeometry = new THREE.BufferGeometry();
            const vertices = [];
            const numPoints = 50;

            for (let i = 0; i <= numPoints; i++) {
                const x = 0.1 + (9.9 * i / numPoints);
                const y = Math.pow(values.Q_in / Math.pow(x, values.alpha), 1 / values.beta);
                if (y <= 10) {
                    vertices.push(x, y, 0);
                    vertices.push(x, y, 10);
                }
            }

            isoquantGeometry.setAttribute('position', new THREE.Float32BufferAttribute(vertices, 3));
            isoquantGeometry.setIndex(createStripIndices(vertices.length / 6));
            isoquantGeometry.computeVertexNormals();

            const isoquantMaterial = new THREE.MeshBasicMaterial({ 
                color: 0xffa500, 
                side: THREE.DoubleSide,
                opacity: 0.8,
                transparent: true
            });
            primalMeshes.isoquant = new THREE.Mesh(isoquantGeometry, isoquantMaterial);
            scenePrimal.add(primalMeshes.isoquant);

            // Create isocost plane
            const planeGeometry = new THREE.PlaneGeometry(15, 15);
            const planeMaterial = new THREE.MeshBasicMaterial({ 
                color: 0x808080, 
                side: THREE.DoubleSide,
                opacity: 0.5,
                transparent: true
            });
            primalMeshes.isocost = new THREE.Mesh(planeGeometry, planeMaterial);

            // Position and rotate the plane
            const normal = new THREE.Vector3(values.w, values.r, -1).normalize();
            primalMeshes.isocost.lookAt(normal);
            primalMeshes.isocost.position.set(5, 5, primalSolution.C);
            scenePrimal.add(primalMeshes.isocost);

            // Create solution vector
            const vectorGeometry = new THREE.BufferGeometry().setFromPoints([
                new THREE.Vector3(0, 0, 0),
                new THREE.Vector3(primalSolution.x, primalSolution.y, primalSolution.C)
            ]);
            const vectorMaterial = new THREE.LineBasicMaterial({ color: 0x0000ff, linewidth: 3 });
            primalMeshes.vector = new THREE.Line(vectorGeometry, vectorMaterial);
            scenePrimal.add(primalMeshes.vector);
        }

        // Update dual plot
        function updateDualPlot(values, dualSolution) {
            // Clear previous meshes
            Object.values(dualMeshes).forEach(mesh => sceneDual.remove(mesh));
            dualMeshes = {};

            // Create production surface
            const surfaceGeometry = new THREE.ParametricGeometry((u, v, dest) => {
                const x = u * 10;
                const y = v * 10;
                const z = Math.pow(x, values.alpha) * Math.pow(y, values.beta);
                dest.set(x, y, Math.min(z, 10));
            }, 50, 50);

            const surfaceMaterial = new THREE.MeshPhongMaterial({ 
                color: 0xffa500,
                side: THREE.DoubleSide,
                shininess: 50
            });
            dualMeshes.surface = new THREE.Mesh(surfaceGeometry, surfaceMaterial);
            sceneDual.add(dualMeshes.surface);

            // Create budget curtain
            const budgetGeometry = new THREE.BufferGeometry();
            const vertices = [];

            for (let x = 0; x <= 10; x += 0.2) {
                const y = (values.C_in - values.w * x) / values.r;
                if (y >= 0 && y <= 10) {
                    vertices.push(x, y, 0);
                    vertices.push(x, y, 10);
                }
            }

            budgetGeometry.setAttribute('position', new THREE.Float32BufferAttribute(vertices, 3));
            budgetGeometry.setIndex(createStripIndices(vertices.length / 6));
            budgetGeometry.computeVertexNormals();

            const budgetMaterial = new THREE.MeshBasicMaterial({ 
                color: 0x808080, 
                side: THREE.DoubleSide,
                opacity: 0.5,
                transparent: true
            });
            dualMeshes.budget = new THREE.Mesh(budgetGeometry, budgetMaterial);
            sceneDual.add(dualMeshes.budget);

            // Create intersection curve
            const curvePoints = [];
            for (let x = 0.1; x <= 10; x += 0.1) {
                const y = (values.C_in - values.w * x) / values.r;
                if (y > 0 && y <= 10) {
                    const z = Math.pow(x, values.alpha) * Math.pow(y, values.beta);
                    if (z <= 10) {
                        curvePoints.push(new THREE.Vector3(x, y, z));
                    }
                }
            }

            if (curvePoints.length > 1) {
                const curveGeometry = new THREE.BufferGeometry().setFromPoints(curvePoints);
                const curveMaterial = new THREE.LineBasicMaterial({ color: 0xff0000, linewidth: 2 });
                dualMeshes.curve = new THREE.Line(curveGeometry, curveMaterial);
                sceneDual.add(dualMeshes.curve);
            }

            // Create optimal point marker
            const sphereGeometry = new THREE.SphereGeometry(0.15, 16, 16);
            const sphereMaterial = new THREE.MeshBasicMaterial({ color: 0x808080 });
            dualMeshes.optimalPoint = new THREE.Mesh(sphereGeometry, sphereMaterial);
            dualMeshes.optimalPoint.position.set(dualSolution.x, dualSolution.y, dualSolution.Q);
            sceneDual.add(dualMeshes.optimalPoint);
        }

        // Helper function to create strip indices
        function createStripIndices(numVertices) {
            const indices = [];
            for (let i = 0; i < numVertices - 2; i += 2) {
                indices.push(i, i + 1, i + 2);
                indices.push(i + 1, i + 3, i + 2);
            }
            return indices;
        }

        // Main update function
        function update() {
            const values = getValues();

            // Update labels
            document.getElementById('label-alpha').textContent = values.alpha.toFixed(2);
            document.getElementById('label-beta').textContent = values.beta.toFixed(2);
            document.getElementById('label-w').textContent = values.w.toFixed(2);
            document.getElementById('label-r').textContent = values.r.toFixed(2);
            document.getElementById('label-Q').textContent = values.Q_in.toFixed(1);
            document.getElementById('label-C').textContent = values.C_in.toFixed(1);

            // Solve problems
            const primalSolution = solvePrimal(values.alpha, values.beta, values.w, values.r, values.Q_in);
            const dualSolution = solveDual(values.alpha, values.beta, values.w, values.r, values.C_in);

            // Update output displays
            document.getElementById('output-primal').textContent = 
                `x = ${primalSolution.x.toFixed(2)}, y = ${primalSolution.y.toFixed(2)}, C = ${primalSolution.C.toFixed(2)}`;
            document.getElementById('output-dual').textContent = 
                `x = ${dualSolution.x.toFixed(2)}, y = ${dualSolution.y.toFixed(2)}, Q = ${dualSolution.Q.toFixed(2)}`;

            // Update plots
            updatePrimalPlot(values, primalSolution);
            updateDualPlot(values, dualSolution);
        }

        // Animation loop
        function animate() {
            requestAnimationFrame(animate);
            controlsPrimal.update();
            controlsDual.update();
            rendererPrimal.render(scenePrimal, cameraPrimal);
            rendererDual.render(sceneDual, cameraDual);
        }

        // Event listeners
        document.getElementById('slider-alpha').addEventListener('input', update);
        document.getElementById('slider-beta').addEventListener('input', update);
        document.getElementById('slider-w').addEventListener('input', update);
        document.getElementById('slider-r').addEventListener('input', update);
        document.getElementById('slider-Q').addEventListener('input', update);
        document.getElementById('slider-C').addEventListener('input', update);

        document.getElementById('btn-reset').addEventListener('click', () => {
            document.getElementById('slider-alpha').value = 0.5;
            document.getElementById('slider-beta').value = 0.5;
            document.getElementById('slider-w').value = 1.0;
            document.getElementById('slider-r').value = 1.0;
            document.getElementById('slider-Q').value = 3.0;
            document.getElementById('slider-C').value = 4.0;
            update();
        });

        document.getElementById('btn-C-from-primal').addEventListener('click', () => {
            const values = getValues();
            const primalSolution = solvePrimal(values.alpha, values.beta, values.w, values.r, values.Q_in);
            document.getElementById('slider-C').value = primalSolution.C;
            update();
        });

        document.getElementById('btn-Q-from-dual').addEventListener('click', () => {
            const values = getValues();
            const dualSolution = solveDual(values.alpha, values.beta, values.w, values.r, values.C_in);
            document.getElementById('slider-Q').value = dualSolution.Q;
            update();
        });

        // Initialize
        initThreeJS();
        update();
        animate();

        // Handle window resize
        window.addEventListener('resize', () => {
            const containerPrimal = document.getElementById('plot-primal');
            const containerDual = document.getElementById('plot-dual');

            cameraPrimal.aspect = containerPrimal.clientWidth / containerPrimal.clientHeight;
            cameraPrimal.updateProjectionMatrix();
            rendererPrimal.setSize(containerPrimal.clientWidth, containerPrimal.clientHeight);

            cameraDual.aspect = containerDual.clientWidth / containerDual.clientHeight;
            cameraDual.updateProjectionMatrix();
            rendererDual.setSize(containerDual.clientWidth, containerDual.clientHeight);
        });
    </script>
</body>
</html>
