            </div>
            <!-- Heat Load Slider -->
            <div class="control-row">
                <label for="slider-heat">heat load</label>
                <input type="range" id="slider-heat" min="0" max="10000" step="100" value="0">
                <span id="heat-value">0 kJ/hr</span>
            </div>
        </div>

        <!-- Section 2: Visualization Area -->
        <div id="visualization-area">
            <!-- p5.js canvas will be inserted here by the script -->
        </div>

    </div>

    <!-- CDN for p5.js -->
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>

    <script>
        // Global variables to store calculation results
        let calculationResults = {
            T_flash: 0,
            V_frac: 0,
            x: [0, 0, 0, 0],
            y: [0, 0, 0, 0]
        };

        // Component names
        const components = ['n-butane', 'n-pentane', 'n-heptane', 'n-octane'];

        // Antoine constants (T in K, P_sat in kPa)
        const antoine = [
            { A: 6.80165, B: 1005.157, C: -27.159 },  // n-butane
            { A: 6.86177, B: 1184.249, C: -35.223 },  // n-pentane
            { A: 6.90237, B: 1358.59, C: -51.303 },   // n-heptane
            { A: 6.92383, B: 1431.149, C: -59.501 }   // n-octane
        ];

        // Physical properties
        const properties = [
            { Cp_L: 132, deltaH_vap: 22400 },  // n-butane
            { Cp_L: 161, deltaH_vap: 25800 },  // n-pentane
            { Cp_L: 225, deltaH_vap: 31800 },  // n-heptane
            { Cp_L: 255, deltaH_vap: 34400 }   // n-octane
        ];

        // Feed conditions
        const F = 10;  // kmol/hr
        const T_F = 323.2;  // K
        const P_F = 1000;  // kPa
        const z = [0.25, 0.25, 0.25, 0.25];  // feed mole fractions

        // Calculate saturation pressure using Antoine equation
        function calcPsat(T, i) {
            return Math.pow(10, antoine[i].A - antoine[i].B / (T + antoine[i].C));
        }

        // Calculate K-values
        function calcK(T, P) {
            return z.map((_, i) => calcPsat(T, i) / P);
        }

        // Rachford-Rice equation
        function rachfordRice(V, K) {
            let sum = 0;
            for (let i = 0; i < 4; i++) {
                sum += z[i] * (K[i] - 1) / (1 + V * (K[i] - 1));
            }
            return sum;
        }

        // Solve for vapor fraction using bisection method
        function solveVaporFraction(K) {
            let V_low = 0;
            let V_high = 1;
            let V_mid;

            for (let iter = 0; iter < 50; iter++) {
                V_mid = (V_low + V_high) / 2;
                let f_mid = rachfordRice(V_mid, K);

                if (Math.abs(f_mid) < 1e-6) break;

                if (rachfordRice(V_low, K) * f_mid < 0) {
                    V_high = V_mid;
                } else {
                    V_low = V_mid;
                }
            }

            return V_mid;
        }

        // Energy balance equation
        function energyBalance(T, P, Q_hr) {
            const K = calcK(T, P);
            const V = solveVaporFraction(K);

            // Calculate mole fractions
            const x = [];
            const y = [];
            for (let i = 0; i < 4; i++) {
                x[i] = z[i] / (1 + V * (K[i] - 1));
                y[i] = K[i] * x[i];
            }

            // Energy balance
            let sensible = 0;
            let latent = 0;

            for (let i = 0; i < 4; i++) {
                sensible += z[i] * properties[i].Cp_L * (T_F - T);
                latent += y[i] * properties[i].deltaH_vap;
            }

            return Q_hr / F + sensible - V * latent;
        }

        // Main calculation function
        function calculateFlash(P_flash, Q_hr) {
            // Find flash temperature using bisection method
            let T_low = 250;  // K
            let T_high = 400;  // K
            let T_mid;

            for (let iter = 0; iter < 50; iter++) {
                T_mid = (T_low + T_high) / 2;
                let f_mid = energyBalance(T_mid, P_flash, Q_hr);

                if (Math.abs(f_mid) < 0.1) break;

                if (energyBalance(T_low, P_flash, Q_hr) * f_mid < 0) {
                    T_high = T_mid;
                } else {
                    T_low = T_mid;
                }
            }

            // Final calculations with converged temperature
            const K = calcK(T_mid, P_flash);
            const V = solveVaporFraction(K);

            const x = [];
            const y = [];
            for (let i = 0; i < 4; i++) {
                x[i] = z[i] / (1 + V * (K[i] - 1));
                y[i] = K[i] * x[i];
            }

            return {
                T_flash: T_mid,
                V_frac: V,
                x: x,
                y: y
            };
        }

        // Event handlers for sliders
        document.getElementById('slider-pressure').addEventListener('input', function() {
            const P_flash = parseFloat(this.value);
            document.getElementById('pressure-value').textContent = P_flash + ' kPa';
            updateCalculation();
        });

        document.getElementById('slider-heat').addEventListener('input', function() {
            const Q_hr = parseFloat(this.value);
            document.getElementById('heat-value').textContent = Q_hr + ' kJ/hr';
            updateCalculation();
        });

        function updateCalculation() {
            const P_flash = parseFloat(document.getElementById('slider-pressure').value);
            const Q_hr = parseFloat(document.getElementById('slider-heat').value);
            calculationResults = calculateFlash(P_flash, Q_hr);
        }

        // p5.js sketch
        function setup() {
            let canvas = createCanvas(800, 500);
            canvas.parent('visualization-area');
            textAlign(LEFT, TOP);

            // Initial calculation
            updateCalculation();
        }

        function draw() {
