            noStroke();
            textAlign(CENTER, CENTER);
            textSize(16);
            text('Flash', 400, 250);

            // Draw feed stream
            stroke(154, 205, 50);  // Light green
            strokeWeight(3);
            line(100, 250, 350, 250);
            // Arrowhead
            push();
            translate(350, 250);
            rotate(0);
            line(-10, -5, 0, 0);
            line(-10, 5, 0, 0);
            pop();

            // Draw vapor stream
            stroke(147, 112, 219);  // Purple
            line(400, 200, 400, 100);
            line(400, 100, 550, 100);
            // Arrowhead
            push();
            translate(550, 100);
            rotate(0);
            line(-10, -5, 0, 0);
            line(-10, 5, 0, 0);
            pop();

            // Draw liquid stream
            stroke(255, 165, 0);  // Orange
            line(400, 300, 400, 400);
            line(400, 400, 550, 400);
            // Arrowhead
            push();
            translate(550, 400);
            rotate(0);
            line(-10, -5, 0, 0);
            line(-10, 5, 0, 0);
            pop();

            // Draw heat input (dashed line)
            stroke(255, 0, 0);  // Red
            strokeWeight(2);
            drawingContext.setLineDash([5, 5]);
            line(300, 350, 380, 280);
            drawingContext.setLineDash([]);
            // Arrowhead
            push();
            translate(380, 280);
            rotate(atan2(-70, 80));
            line(-10, -5, 0, 0);
            line(-10, 5, 0, 0);
            pop();

            // Text labels
            noStroke();
            fill(0);
            textAlign(LEFT, TOP);
            textSize(12);

            // Feed info
            text('Feed', 50, 230);
            text('F = ' + F + ' kmol/hr', 50, 260);
            text('T = ' + T_F.toFixed(1) + ' K', 50, 275);
            text('P = ' + P_F + ' kPa', 50, 290);
            text('z₁ = ' + z[0].toFixed(2), 50, 305);
            text('z₂ = ' + z[1].toFixed(2), 50, 320);
            text('z₃ = ' + z[2].toFixed(2), 50, 335);
            text('z₄ = ' + z[3].toFixed(2), 50, 350);

            // Vapor info
            text('Vapor', 560, 80);
            text('vapor mole fraction:', 560, 100);
            text('y₁ = ' + calculationResults.y[0].toFixed(3), 560, 115);
            text('y₂ = ' + calculationResults.y[1].toFixed(3), 560, 130);
            text('y₃ = ' + calculationResults.y[2].toFixed(3), 560, 145);
            text('y₄ = ' + calculationResults.y[3].toFixed(3), 560, 160);

            // Liquid info
            text('Liquid', 560, 380);
            text('liquid mole fraction:', 560, 400);
            text('x₁ = ' + calculationResults.x[0].toFixed(3), 560, 415);
            text('x₂ = ' + calculationResults.x[1].toFixed(3), 560, 430);
            text('x₃ = ' + calculationResults.x[2].toFixed(3), 560, 445);
            text('x₄ = ' + calculationResults.x[3].toFixed(3), 560, 460);

            // Process info
            textAlign(CENTER, TOP);
            text('vapor fraction = ' + calculationResults.V_frac.toFixed(3), 400, 320);
            text('flash temperature = ' + calculationResults.T_flash.toFixed(1) + ' K', 400, 335);
            text('flash pressure = ' + document.getElementById('slider-pressure').value + ' kPa', 400, 350);

            // Heat info
            textAlign(LEFT, TOP);
            text('Q = ' + document.getElementById('slider-heat').value + ' kJ/hr', 250, 360);
        }
    </script>
</body>
</html>
