                <input type="range" id="slider-time" min="0" max="100" step="0.01" value="0">
                <span id="slider-time-value">0</span>
            </div>

            <label>initial conditions</label>

            <label for="slider-nodal-angle">nodal angle (rad)</label>
            <div class="slider-container">
                <button id="btn-nodal-angle-play" class="play-button">+</button>
                <input type="range" id="slider-nodal-angle" min="0" max="6.283" step="0.01" value="0">
                <span id="slider-nodal-angle-value">0</span>
            </div>

            <label for="slider-sun-earth-angle">Sun-Earth angle (rad)</label>
            <div class="slider-container">
                <button id="btn-sun-earth-angle-play" class="play-button">+</button>
                <input type="range" id="slider-sun-earth-angle" min="0" max="6.283" step="0.01" value="1.571">
                <span id="slider-sun-earth-angle-value">\( \frac{\pi}{2} \)</span>
            </div>

            <label for="slider-zodiac">zodiac</label>
            <div class="slider-container">
                <button id="btn-zodiac-play" class="play-button">+</button>
                <input type="range" id="slider-zodiac" min="0" max="6.283" step="0.01" value="0">
                <span id="slider-zodiac-value">0</span>
            </div>

            <label>options</label>
            <div>
                <input type="checkbox" id="checkbox-constellations" checked>
                <label for="checkbox-constellations" style="display: inline; margin: 0;">constellations</label>
            </div>
            <div>
                <input type="checkbox" id="checkbox-realistic-size">
                <label for="checkbox-realistic-size" style="display: inline; margin: 0;">realistic size</label>
            </div>
        </div>

        <div id="visualization-container">
            <canvas id="scene-canvas"></canvas>
        </div>
    </div>

    <script>
        // Initialize Three.js
        const scene = new THREE.Scene();
        const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 10000);
        const renderer = new THREE.WebGLRenderer({ canvas: document.getElementById('scene-canvas'), antialias: true });
        renderer.setSize(window.innerWidth - 300, window.innerHeight);
        renderer.shadowMap.enabled = true;
        renderer.shadowMap.type = THREE.PCFSoftShadowMap;

        // Global parameters
        const params = {
            viewMode: 'land',
            time: 0,
            nodalAngle: 0,
            sunEarthAngle: 1.571,
            zodiac: 0,
            showConstellations: true,
            realisticSize: false
        };

        const isAnimating = {
            time: false,
            nodalAngle: false,
            sunEarthAngle: false,
            zodiac: false
        };

        // Create Earth
        const earthRadius = params.realisticSize ? 1 : 3;
        const earthGeometry = new THREE.SphereGeometry(earthRadius, 32, 32);
        const earthMaterial = new THREE.MeshPhongMaterial({ color: 0x2233ff });
        const earth = new THREE.Mesh(earthGeometry, earthMaterial);
        earth.castShadow = true;
        earth.receiveShadow = true;
        scene.add(earth);

        // Earth atmosphere
        const atmosphereGeometry = new THREE.SphereGeometry(earthRadius * 1.1, 32, 32);
        const atmosphereMaterial = new THREE.MeshBasicMaterial({
            color: 0x4488ff,
            transparent: true,
            opacity: 0.3,
            blending: THREE.AdditiveBlending
        });
        const atmosphere = new THREE.Mesh(atmosphereGeometry, atmosphereMaterial);
        earth.add(atmosphere);

        // Create Moon surface for land view
        const moonSurfaceGeometry = new THREE.PlaneGeometry(1000, 1000);
        const moonSurfaceCanvas = document.createElement('canvas');
        moonSurfaceCanvas.width = 512;
        moonSurfaceCanvas.height = 512;
        const ctx = moonSurfaceCanvas.getContext('2d');

        // Generate procedural moon texture
        for (let i = 0; i < 512; i++) {
            for (let j = 0; j < 512; j++) {
                const noise = Math.random();
                const gray = Math.floor(100 + noise * 55);
                ctx.fillStyle = `rgb(${gray}, ${gray}, ${gray})`;
                ctx.fillRect(i, j, 1, 1);
            }
        }

        const moonTexture = new THREE.CanvasTexture(moonSurfaceCanvas);
        const moonSurfaceMaterial = new THREE.MeshStandardMaterial({
            map: moonTexture,
            bumpMap: moonTexture,
            bumpScale: 0.5
        });
        const moonSurface = new THREE.Mesh(moonSurfaceGeometry, moonSurfaceMaterial);
        moonSurface.rotation.x = -Math.PI / 2;
        moonSurface.position.y = -5;
        scene.add(moonSurface);

        // Create Moon sphere for space view
        const moonGeometry = new THREE.SphereGeometry(10, 32, 32);
        const moonMaterial = new THREE.MeshStandardMaterial({
            map: moonTexture,
            bumpMap: moonTexture,
            bumpScale: 0.5
        });
        const moon = new THREE.Mesh(moonGeometry, moonMaterial);
        moon.visible = false;
        scene.add(moon);

        // Create constellation group
        const constellationGroup = new THREE.Group();
        scene.add(constellationGroup);

        // Simple constellation data (example)
        const constellationData = {
            orion: {
                stars: [
                    [-50, 20, -100], [-45, 15, -100], [-40, 25, -100],
                    [-35, 20, -100], [-30, 15, -100], [-25, 25, -100],
                    [-45, 5, -100], [-35, 5, -100]
                ],
                lines: [[0, 1], [1, 2], [2, 3], [3, 4], [4, 5], [1, 6], [4, 7], [6, 7]]
            }
        };

        // Create constellation stars and lines
        for (const constellation in constellationData) {
            const data = constellationData[constellation];

            // Stars
            const starGeometry = new THREE.BufferGeometry();
            const starPositions = [];
            data.stars.forEach(pos => {
                starPositions.push(...pos);
            });
            starGeometry.setAttribute('position', new THREE.Float32BufferAttribute(starPositions, 3));
            const starMaterial = new THREE.PointsMaterial({ color: 0xffffaa, size: 2 });
            const stars = new THREE.Points(starGeometry, starMaterial);
            constellationGroup.add(stars);

            // Lines
            const lineGeometry = new THREE.BufferGeometry();
            const linePositions = [];
            data.lines.forEach(line => {
                linePositions.push(...data.stars[line[0]]);
                linePositions.push(...data.stars[line[1]]);
            });
            lineGeometry.setAttribute('position', new THREE.Float32BufferAttribute(linePositions, 3));
            const lineMaterial = new THREE.LineBasicMaterial({ color: 0xffffaa, opacity: 0.5, transparent: true });
            const lines = new THREE.LineSegments(lineGeometry, lineMaterial);
            constellationGroup.add(lines);
        }

        // Lighting
        const sunLight = new THREE.DirectionalLight(0xffffff, 1);
        sunLight.position.set(50, 50, 50);
        sunLight.castShadow = true;
        sunLight.shadow.camera.near = 0.1;
        sunLight.shadow.camera.far = 200;
        sunLight.shadow.camera.left = -50;
        sunLight.shadow.camera.right = 50;
        sunLight.shadow.camera.top = 50;
        sunLight.shadow.camera.bottom = -50;
        scene.add(sunLight);

        const ambientLight = new THREE.AmbientLight(0x404040);
        scene.add(ambientLight);

        // Camera setup
        function updateCamera() {
            if (params.viewMode === 'land') {
                camera.position.set(0, 0, 0);
                camera.lookAt(0, 50, 0);
                moonSurface.visible = true;
                moon.visible = false;
            } else {
                camera.position.set(0, 20, 50);
                camera.lookAt(0, 0, 0);
                moonSurface.visible = false;
                moon.visible = true;
            }
        }

        // Update functions
        function updateScene() {
            // Update Earth position based on time
            const earthOrbitRadius = 30;
            const angle = (params.time / 27.3) * Math.PI * 2 + params.sunEarthAngle;
            earth.position.x = Math.cos(angle) * earthOrbitRadius;
            earth.position.z = Math.sin(angle) * earthOrbitRadius;
            earth.position.y = Math.sin(params.nodalAngle) * earthOrbitRadius;

            // Update sun direction
            sunLight.position.x = -Math.cos(params.sunEarthAngle) * 100;
            sunLight.position.z = -Math.sin(params.sunEarthAngle) * 100;

            // Update constellation rotation
            constellationGroup.rotation.y = params.zodiac;

            // Update Earth size
            const scale = params.realisticSize ? 0.3 : 1;
            earth.scale.set(scale, scale, scale);

            // Update constellation visibility
            constellationGroup.visible = params.showConstellations;
        }

        // Event listeners
        document.getElementById('btn-land').addEventListener('click', () => {
            params.viewMode = 'land';
            document.getElementById('btn-land').classList.add('active');
            document.getElementById('btn-space').classList.remove('active');
            updateCamera();
        });

        document.getElementById('btn-space').addEventListener('click', () => {
            params.viewMode = 'space';
            document.getElementById('btn-space').classList.add('active');
            document.getElementById('btn-land').classList.remove('active');
            updateCamera();
        });

        // Slider event listeners
        document.getElementById('slider-time').addEventListener('input', (e) => {
            params.time = parseFloat(e.target.value);
            document.getElementById('slider-time-value').textContent = params.time.toFixed(2);
            updateScene();
        });

        document.getElementById('slider-nodal-angle').addEventListener('input', (e) => {
            params.nodalAngle = parseFloat(e.target.value);
            document.getElementById('slider-nodal-angle-value').textContent = params.nodalAngle.toFixed(2);
            updateScene();
        });

        document.getElementById('slider-sun-earth-angle').addEventListener('input', (e) => {
            params.sunEarthAngle = parseFloat(e.target.value);
            document.getElementById('slider-sun-earth-angle-value').textContent = params.sunEarthAngle.toFixed(2);
            updateScene();
        });

        document.getElementById('slider-zodiac').addEventListener('input', (e) => {
            params.zodiac = parseFloat(e.target.value);
            document.getElementById('slider-zodiac-value').textContent = params.zodiac.toFixed(2);
            updateScene();
        });

        // Play button event listeners
        document.getElementById('btn-time-play').addEventListener('click', (e) => {
            isAnimating.time = !isAnimating.time;
            e.target.classList.toggle('playing');
        });

        document.getElementById('btn-nodal-angle-play').addEventListener('click', (e) => {
            isAnimating.nodalAngle = !isAnimating.nodalAngle;
            e.target.classList.toggle('playing');
        });

        document.getElementById('btn-sun-earth-angle-play').addEventListener('click', (e) => {
            isAnimating.sunEarthAngle = !isAnimating.sunEarthAngle;
            e.target.classList.toggle('playing');
        });

        document.getElementById('btn-zodiac-play').addEventListener('click', (e) => {
            isAnimating.zodiac = !isAnimating.zodiac;
            e.target.classList.toggle('playing');
        });

        // Checkbox event listeners
        document.getElementById('checkbox-constellations').addEventListener('change', (e) => {
            params.showConstellations = e.target.checked;
            updateScene();
        });

        document.getElementById('checkbox-realistic-size').addEventListener('change', (e) => {
            params.realisticSize = e.target.checked;
            updateScene();
        });

        // Animation loop
        let lastTime = 0;
        function animate(currentTime) {
            requestAnimationFrame(animate);

            const deltaTime = (currentTime - lastTime) / 1000;
            lastTime = currentTime;

            // Animate sliders
            if (isAnimating.time) {
                const slider = document.getElementById('slider-time');
                let value = parseFloat(slider.value) + deltaTime * 5;
                if (value > slider.max) value = slider.min;
                slider.value = value;
                slider.dispatchEvent(new Event('input'));
            }

            if (isAnimating.nodalAngle) {
                const slider = document.getElementById('slider-nodal-angle');
                let value = parseFloat(slider.value) + deltaTime * 0.5;
                if (value > slider.max) value = slider.min;
                slider.value = value;
                slider.dispatchEvent(new Event('input'));
            }

            if (isAnimating.sunEarthAngle) {
                const slider = document.getElementById('slider-sun-earth-angle');
                let value = parseFloat(slider.value) + deltaTime * 0.5;
                if (value > slider.max) value = slider.min;
                slider.value = value;
                slider.dispatchEvent(new Event('input'));
            }

            if (isAnimating.zodiac) {
                const slider = document.getElementById('slider-zodiac');
                let value = parseFloat(slider.value) + deltaTime * 0.5;
                if (value > slider.max) value = slider.min;
                slider.value = value;
                slider.dispatchEvent(new Event('input'));
            }

            renderer.render(scene, camera);
        }

        // Handle window resize
        window.addEventListener('resize', () => {
            camera.aspect = (window.innerWidth - 300) / window.innerHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(window.innerWidth - 300, window.innerHeight);
        });

        // Initialize
        updateCamera();
        updateScene();
        animate(0);

        // Initialize MathJax
        window.MathJax.typeset();
    </script>
</body>
</html>
