<body>
    <div id="main-container">
        <div id="control-panel">
            <div class="slider-container">
                <label>θ<sub>0</sub></label>
                <input type="range" id="slider-theta0" min="-3.14159" max="3.14159" step="0.01" value="0.5236">
                <span id="value-theta0">π/6</span>
            </div>

            <div class="slider-container">
                <label>v<sub>0</sub></label>
                <input type="range" id="slider-v0" min="-5" max="5" step="0.1" value="0">
                <span id="value-v0">0</span>
            </div>

            <div class="slider-container">
                <label>connector length</label>
                <input type="range" id="slider-length" min="0.5" max="4" step="0.1" value="2">
                <span id="value-length">2</span>
            </div>

            <div class="animation-controls">
                <label>animate</label>
                <div class="button-group">
                    <button id="btn-play-pause">▶</button>
                    <button id="btn-reset">|<</button>
                </div>
            </div>
