    <script>
        // Physics constants
        const g = 9.8;
        const m = 1; // bob mass
        const M = 1; // support mass
        const scale = 50; // pixels per meter
        const dt = 0.02; // time step

        // State variables
        let isPlaying = false;
        let t = 0;
        let x = 0;
        let theta = 0;
        let x_dot = 0;
        let theta_dot = 0;

        // Initial values from sliders
        let theta0 = 0;
        let v0 = 0;
        let l = 2;

        // UI elements
        const sliderTheta0 = document.getElementById('slider-theta0');
        const sliderV0 = document.getElementById('slider-v0');
        const sliderLength = document.getElementById('slider-length');
        const valueTheta0 = document.getElementById('value-theta0');
        const valueV0 = document.getElementById('value-v0');
        const valueLength = document.getElementById('value-length');
        const btnPlayPause = document.getElementById('btn-play-pause');
        const btnReset = document.getElementById('btn-reset');

        // Format angle value for display
        function formatAngle(radians) {
            const piRatio = radians / Math.PI;
            const tolerance = 0.01;

            // Check common fractions of π
            const fractions = [
                {num: 1, den: 6}, {num: 1, den: 4}, {num: 1, den: 3},
                {num: 1, den: 2}, {num: 2, den: 3}, {num: 3, den: 4},
                {num: 5, den: 6}, {num: 1, den: 1}
            ];

            for (let frac of fractions) {
                for (let sign of [-1, 1]) {
                    const testValue = sign * frac.num / frac.den;
                    if (Math.abs(piRatio - testValue) < tolerance) {
                        if (testValue === 0) return "0";
                        if (testValue === 1) return "π";
                        if (testValue === -1) return "-π";
                        if (frac.num === 1) {
                            return (sign === -1 ? "-" : "") + "π/" + frac.den;
                        } else {
                            return (sign === -1 ? "-" : "") + frac.num + "π/" + frac.den;
                        }
                    }
                }
            }

            // Default to decimal
            return radians.toFixed(2);
        }

        // Reset physics to initial conditions
        function resetPhysics() {
            t = 0;
            theta = theta0;
            theta_dot = 0;
            x = 0;
            x_dot = v0;
        }

        // Slider event handlers
        sliderTheta0.addEventListener('input', function() {
            theta0 = parseFloat(this.value);
            valueTheta0.textContent = formatAngle(theta0);
            stopAnimation();
            resetPhysics();
        });

        sliderV0.addEventListener('input', function() {
            v0 = parseFloat(this.value);
            valueV0.textContent = v0.toFixed(1);
            stopAnimation();
            resetPhysics();
        });

        sliderLength.addEventListener('input', function() {
            l = parseFloat(this.value);
            valueLength.textContent = l.toFixed(1);
            stopAnimation();
            resetPhysics();
        });

        // Button event handlers
        btnPlayPause.addEventListener('click', function() {
            isPlaying = !isPlaying;
            this.textContent = isPlaying ? "||" : "▶";
        });

        btnReset.addEventListener('click', function() {
            stopAnimation();
            resetPhysics();
        });

        function stopAnimation() {
            isPlaying = false;
            btnPlayPause.textContent = "▶";
        }

        // Initialize values
        theta0 = parseFloat(sliderTheta0.value);
        v0 = parseFloat(sliderV0.value);
        l = parseFloat(sliderLength.value);
        resetPhysics();

        // p5.js sketch
        function setup() {
            let canvas = createCanvas(500, 500);
            canvas.parent('visualization');
        }

        function draw() {
            // Update physics if playing
            if (isPlaying) {
                // Calculate angular acceleration
                const sin_theta = Math.sin(theta);
                const cos_theta = Math.cos(theta);
                const denominator = l * (M + m * sin_theta * sin_theta);
                const theta_ddot = (-m * l * sin_theta * cos_theta * theta_dot * theta_dot - 
                                   g * (M + m) * sin_theta) / denominator;

                // Update angular velocity and angle
                theta_dot += theta_ddot * dt;
                theta += theta_dot * dt;

                // Update pivot velocity and position
                x_dot = v0 - (m * l * cos_theta * theta_dot) / (M + m);
                x += x_dot * dt;

                t += dt;
            }

            // Clear canvas
            background(240);

            // Calculate positions
            const px = width / 2 + x * scale;
            const py = height / 2;
            const bx = px + l * scale * Math.sin(theta);
            const by = py + l * scale * Math.cos(theta);

            // Draw connector (rod)
            stroke(0);
            strokeWeight(2);
            line(px, py, bx, by);

            // Draw pivot (red rectangle)
            fill(255, 0, 0);
            noStroke();
            rectMode(CENTER);
            rect(px, py, 20, 10);

            // Draw bob (orange circle)
            fill(255, 165, 0);
            stroke(0);
            strokeWeight(2);
            circle(bx, by, 30);
        }
    </script>
</body>
</html>
