        directionalLight1.position.set(5, 10, 5);
        scene.add(directionalLight1);

        const directionalLight2 = new THREE.DirectionalLight(0xffffff, 0.4);
        directionalLight2.position.set(-5, -10, -5);
        scene.add(directionalLight2);

        // Controls
        const controls = new THREE.OrbitControls(camera, renderer.domElement);
        controls.enableDamping = true;
        controls.dampingFactor = 0.05;

        // Sphere setup
        const sphereGeometry = new THREE.SphereGeometry(1, 64, 32);
        const originalPositions = sphereGeometry.attributes.position.array.slice();

        const sphereMaterial = new THREE.MeshStandardMaterial({
            vertexColors: true,
            side: THREE.DoubleSide
        });

        const sphere = new THREE.Mesh(sphereGeometry, sphereMaterial);
        scene.add(sphere);

        // Box helper
        const boxHelper = new THREE.BoxHelper(sphere, 0x000000);
        boxHelper.visible = false;
        scene.add(boxHelper);

        // Compass rose
        const compassGeometry = new THREE.BufferGeometry();
        const compassPoints = [];
        const compassRadius = 1.5;
        for (let i = 0; i <= 100; i++) {
            const angle = (i / 100) * Math.PI * 2;
            compassPoints.push(compassRadius * Math.cos(angle), 0, compassRadius * Math.sin(angle));
        }
        compassGeometry.setAttribute('position', new THREE.Float32BufferAttribute(compassPoints, 3));

        const compassMaterial = new THREE.LineDashedMaterial({
            color: 0x666666,
            dashSize: 0.05,
            gapSize: 0.05
        });

        const compassRose = new THREE.Line(compassGeometry, compassMaterial);
        compassRose.computeLineDistances();
        scene.add(compassRose);

        // Axis labels
        const loader = new THREE.FontLoader();
        let font;

        const labelGroup = new THREE.Group();
        scene.add(labelGroup);

        // Create simple text sprites
        function createTextSprite(text, position) {
            const canvas = document.createElement('canvas');
            const context = canvas.getContext('2d');
            canvas.width = 64;
            canvas.height = 64;

            context.font = 'Bold 40px Arial';
            context.fillStyle = 'black';
            context.textAlign = 'center';
            context.textBaseline = 'middle';
            context.fillText(text, 32, 32);

            const texture = new THREE.CanvasTexture(canvas);
            const spriteMaterial = new THREE.SpriteMaterial({ map: texture });
            const sprite = new THREE.Sprite(spriteMaterial);
            sprite.scale.set(0.3, 0.3, 1);
            sprite.position.copy(position);

            return sprite;
        }

        // Add compass labels
        const northLabel = createTextSprite('N', new THREE.Vector3(0, 0.1, -compassRadius - 0.2));
        const eastLabel = createTextSprite('E', new THREE.Vector3(compassRadius + 0.2, 0.1, 0));
        const southLabel = createTextSprite('S', new THREE.Vector3(0, 0.1, compassRadius + 0.2));
        const westLabel = createTextSprite('W', new THREE.Vector3(-compassRadius - 0.2, 0.1, 0));

        scene.add(northLabel, eastLabel, southLabel, westLabel);

        // P, T, U labels
        let pLabel, tLabel, uLabel;

        // Seismological calculations
        function deg2rad(deg) {
            return deg * Math.PI / 180;
        }

        function calculateFaultVectors(strike, dip, rake) {
            const phi = deg2rad(strike);
            const delta = deg2rad(dip);
            const lambda = deg2rad(rake);

            // Normal vector to fault plane
            const n = new THREE.Vector3(
                -Math.sin(delta) * Math.sin(phi),
                Math.sin(delta) * Math.cos(phi),
                -Math.cos(delta)
            );

            // Slip vector
            const u = new THREE.Vector3(
                Math.cos(lambda) * Math.cos(phi) + Math.cos(delta) * Math.sin(lambda) * Math.sin(phi),
                Math.cos(lambda) * Math.sin(phi) - Math.cos(delta) * Math.sin(lambda) * Math.cos(phi),
                -Math.sin(delta) * Math.sin(lambda)
            );

            // P and T axes
            const p = new THREE.Vector3().addVectors(n, u).normalize();
            const t = new THREE.Vector3().subVectors(u, n).normalize();

            // Convert from seismological to three.js coordinates
            // Seismological: x=North, y=East, z=Down
            // Three.js: x=East, y=Up, z=South
            const pThree = new THREE.Vector3(p.y, -p.z, -p.x);
            const tThree = new THREE.Vector3(t.y, -t.z, -t.x);
            const uThree = new THREE.Vector3(u.y, -u.z, -u.x);

            return { p: pThree, t: tThree, u: uThree };
        }

        function updateSphere() {
            const strike = parseFloat(document.getElementById('slider-strike').value);
            const dip = parseFloat(document.getElementById('slider-dip').value);
            const rake = parseFloat(document.getElementById('slider-slip-angle').value);
            const slipAmount = parseFloat(document.getElementById('slider-slip-amount').value);

            const { p, t, u } = calculateFaultVectors(strike, dip, rake);

            // Update vertex positions
            const positions = sphereGeometry.attributes.position.array;
            for (let i = 0; i < positions.length; i += 3) {
                const v = new THREE.Vector3(
                    originalPositions[i],
                    originalPositions[i + 1],
                    originalPositions[i + 2]
                );

                const vNorm = v.clone().normalize();
                const pDot = vNorm.dot(p);
                const tDot = vNorm.dot(t);

                const deformationFactor = 0.3 * slipAmount * (tDot * tDot - pDot * pDot);
                const newPos = v.clone().multiplyScalar(1 + deformationFactor);

                positions[i] = newPos.x;
                positions[i + 1] = newPos.y;
                positions[i + 2] = newPos.z;
            }

            sphereGeometry.attributes.position.needsUpdate = true;
            sphereGeometry.computeVertexNormals();

            // Update face colors
            const colors = [];
            const positionAttribute = sphereGeometry.attributes.position;
            const index = sphereGeometry.index;

            for (let i = 0; i < index.count; i += 3) {
                const a = index.getX(i);
                const b = index.getX(i + 1);
                const c = index.getX(i + 2);

                const va = new THREE.Vector3().fromBufferAttribute(positionAttribute, a);
                const vb = new THREE.Vector3().fromBufferAttribute(positionAttribute, b);
                const vc = new THREE.Vector3().fromBufferAttribute(positionAttribute, c);

                const centroid = new THREE.Vector3()
                    .add(va)
                    .add(vb)
                    .add(vc)
                    .divideScalar(3)
                    .normalize();

                const pDot = centroid.dot(p);
                const tDot = centroid.dot(t);

                const isCompressive = Math.abs(pDot) > Math.abs(tDot);
                const color = isCompressive ? [1, 1, 1] : [1, 0.647, 0];

                for (let j = 0; j < 3; j++) {
                    colors.push(...color);
                }
            }

            sphereGeometry.setAttribute('color', new THREE.Float32BufferAttribute(colors, 3));

            // Update P, T, U labels
            if (pLabel) labelGroup.remove(pLabel);
            if (tLabel) labelGroup.remove(tLabel);
            if (uLabel) labelGroup.remove(uLabel);

            pLabel = createTextSprite('P', p.clone().multiplyScalar(1.2));
            tLabel = createTextSprite('T', t.clone().multiplyScalar(1.2));
            uLabel = createTextSprite('U', u.clone().multiplyScalar(1.2));

            labelGroup.add(pLabel, tLabel, uLabel);

            // Update box helper
            boxHelper.update();
        }

        // Event listeners
        document.getElementById('slider-strike').addEventListener('input', function(e) {
            document.getElementById('value-strike').value = e.target.value;
            updateSphere();
        });

        document.getElementById('slider-dip').addEventListener('input', function(e) {
            document.getElementById('value-dip').value = e.target.value;
            updateSphere();
        });

        document.getElementById('slider-slip-angle').addEventListener('input', function(e) {
            document.getElementById('value-slip-angle').value = e.target.value;
            updateSphere();
        });

        document.getElementById('slider-slip-amount').addEventListener('input', function(e) {
            document.getElementById('value-slip-amount').value = e.target.value;
            updateSphere();
        });

        document.getElementById('checkbox-box').addEventListener('change', function(e) {
            boxHelper.visible = e.target.checked;
        });

        document.getElementById('btn-reset').addEventListener('click', function() {
            document.getElementById('slider-strike').value = 180;
            document.getElementById('value-strike').value = 180;
            document.getElementById('slider-dip').value = 60;
            document.getElementById('value-dip').value = 60;
            document.getElementById('slider-slip-angle').value = 90;
            document.getElementById('value-slip-angle').value = 90;
            document.getElementById('slider-slip-amount').value = 0.5;
            document.getElementById('value-slip-amount').value = 0.5;
            document.getElementById('checkbox-box').checked = false;
            boxHelper.visible = false;
            updateSphere();
        });

        // Animation loop
        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        // Initialize
        updateSphere();
        animate();
    </script>
</body>
