            color: #2c3e50;
            padding: 15px;
            background-color: #ecf0f1;
            border-radius: 5px;
        }
    </style>
</head>
<body>
    <div id="app-container">
        <div id="control-panel">
            <div class="control-group">
                <label for="slider-db-size">database size</label>
                <input type="range" id="slider-db-size" min="8" max="100" value="10" step="1">
                <div class="value-display">N=<span id="display-db-size">10</span></div>
            </div>
            <div class="control-group">
                <label for="slider-iterations">number of iterations</label>
                <input type="range" id="slider-iterations" min="0" max="20" value="2" step="1">
                <div class="value-display">k=<span id="display-iterations">2</span></div>
            </div>
        </div>
        <div id="canvas-container"></div>
        <div id="probability-display">99.9% success probability</div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script>
        let dbSizeSlider, iterationsSlider;
        let dbSizeDisplay, iterationsDisplay, probabilityDisplay;

        function setup() {
            let canvas = createCanvas(760, 600);
            canvas.parent('canvas-container');

            // Get DOM elements
            dbSizeSlider = document.getElementById('slider-db-size');
            iterationsSlider = document.getElementById('slider-iterations');
            dbSizeDisplay = document.getElementById('display-db-size');
            iterationsDisplay = document.getElementById('display-iterations');
            probabilityDisplay = document.getElementById('probability-display');

            // Add event listeners
            dbSizeSlider.addEventListener('input', updateVisualization);
            iterationsSlider.addEventListener('input', updateVisualization);

            noLoop();
            updateVisualization();
        }

        function updateVisualization() {
            // Update display values
            dbSizeDisplay.textContent = dbSizeSlider.value;
            iterationsDisplay.textContent = iterationsSlider.value;

            // Trigger redraw
            redraw();
        }

        function draw() {
            background(255);

            const N = parseInt(dbSizeSlider.value);
            const k = parseInt(iterationsSlider.value);
            const targetIndex = 6;

            // Calculate stages
            const stage1 = getInitialAmplitudes(N);
            const stage2 = applyOracle(stage1.slice(), targetIndex);
            const stage3 = applyDiffusion(stage2.slice());
            const stage4 = applyGroverIterations(getInitialAmplitudes(N), targetIndex, k);

            // Draw four stages
            const stageHeight = height / 4;
            drawStage(stage1, 0, stageHeight, "original amplitudes", targetIndex, true);
            drawStage(stage2, stageHeight, stageHeight, "after first inversion", targetIndex, false);
            drawStage(stage3, stageHeight * 2, stageHeight, "after first diffusion", targetIndex, false);
            drawStage(stage4, stageHeight * 3, stageHeight, `after ${k} iterations`, targetIndex, false);

            // Update probability display
            const probability = calculateProbability(N, k);
            probabilityDisplay.textContent = `${probability.toFixed(1)}% success probability`;
        }

        function getInitialAmplitudes(N) {
            const amplitude = 1 / Math.sqrt(N);
            return new Array(N).fill(amplitude);
        }

        function applyOracle(amplitudes, targetIndex) {
            if (targetIndex < amplitudes.length) {
                amplitudes[targetIndex] *= -1;
            }
            return amplitudes;
        }

        function applyDiffusion(amplitudes) {
            const mean = amplitudes.reduce((a, b) => a + b) / amplitudes.length;
            return amplitudes.map(a => 2 * mean - a);
        }

        function applyGroverIterations(amplitudes, targetIndex, k) {
            let result = amplitudes.slice();
            for (let i = 0; i < k; i++) {
                result = applyOracle(result, targetIndex);
                result = applyDiffusion(result);
            }
            return result;
        }

        function calculateProbability(N, k) {
            const angle = Math.asin(1 / Math.sqrt(N));
            const probability = Math.pow(Math.sin((2 * k + 1) * angle), 2) * 100;
            return probability;
        }

        function drawStage(amplitudes, yOffset, stageHeight, title, targetIndex, drawParentheses) {
            // Draw title
            push();
            textAlign(CENTER);
            textSize(14);
            fill(0);
            text(title, width / 2, yOffset + 20);
            pop();

            // Draw baseline
            const baseline = yOffset + stageHeight / 2;
            stroke(200);
            line(40, baseline, width - 40, baseline);

            // Calculate bar positions
            const margin = 60;
            const availableWidth = width - 2 * margin;
            const barSpacing = availableWidth / amplitudes.length;
            const barWidth = Math.min(barSpacing * 0.8, 20);
            const scale = 200; // Scaling factor for amplitude visualization

            // Draw parentheses if needed
            if (drawParentheses) {
                push();
                noFill();
                stroke(0);
                strokeWeight(2);

                const leftX = margin - 15;
                const rightX = width - margin + 15;
                const topY = baseline - 60;
                const bottomY = baseline + 60;

                // Left parenthesis
                arc(leftX + 10, baseline, 20, bottomY - topY, HALF_PI, PI + HALF_PI);

                // Right parenthesis
                arc(rightX - 10, baseline, 20, bottomY - topY, -HALF_PI, HALF_PI);

                pop();
            }

            // Draw amplitude bars
            noStroke();
            for (let i = 0; i < amplitudes.length; i++) {
                const x = margin + i * barSpacing + barSpacing / 2;
                const barHeight = amplitudes[i] * scale;

                // Set color
                if (i === targetIndex && i < amplitudes.length) {
                    fill(255, 165, 0); // Orange for target
                } else {
                    fill(128, 128, 128); // Grey for others
                }

                // Draw bar
                if (barHeight > 0) {
                    rect(x - barWidth / 2, baseline - barHeight, barWidth, barHeight);
                } else {
                    rect(x - barWidth / 2, baseline, barWidth, -barHeight);
                }

                // Draw state index
                push();
                textAlign(CENTER);
                textSize(10);
                fill(100);
                text(i, x, baseline + 15);
                pop();
            }
        }
    </script>
</body>
</html>
