<body>
    <!-- Main container -->
    <div id="main-container">
        <!-- Left column: Control Panel -->
        <div id="control-panel">
            <div class="control-group">
                <label for="slider-volatility">relative volatility</label>
                <input type="range" id="slider-volatility" min="1.1" max="10" step="0.1" value="2.5">
                <button id="btn-volatility-plus">+</button>
            </div>
            <div class="control-group">
                <label for="slider-distillate">distillate specification</label>
                <input type="range" id="slider-distillate" min="0.51" max="0.99" step="0.01" value="0.95">
                <button id="btn-distillate-plus">+</button>
            </div>
            <div class="control-group">
                <label for="slider-bottom">bottom specification</label>
                <input type="range" id="slider-bottom" min="0.01" max="0.49" step="0.01" value="0.05">
                <button id="btn-bottom-plus">+</button>
            </div>
            <div class="control-group">
                <label for="slider-feed-comp">feed composition</label>
                <input type="range" id="slider-feed-comp" min="0.02" max="0.98" step="0.01" value="0.50">
                <button id="btn-feed-comp-plus">+</button>
            </div>
            <div class="control-group">
                <label for="slider-feed-quality">feed quality</label>
                <input type="range" id="slider-feed-quality" min="0.0" max="2.0" step="0.05" value="1.0">
                <button id="btn-feed-quality-plus">+</button>
            </div>
        </div>

        <!-- Right column: Visualization Area -->
        <div id="visualization-area">
            <p id="stages-count-display">The number of stages is 8.</p>
            <div id="canvas-container"></div>
        </div>
    </div>

    <script>
        let canvas;
        const margin = 50;

        function setup() {
            const containerWidth = document.getElementById('canvas-container').offsetWidth;
            const containerHeight = document.getElementById('canvas-container').offsetHeight;
            const canvasSize = Math.min(containerWidth, containerHeight) - 40;

            canvas = createCanvas(canvasSize, canvasSize);
            canvas.parent('canvas-container');
            noLoop();

            // Add event listeners
            document.querySelectorAll('input[type="range"]').forEach(slider => {
                slider.addEventListener('input', handleSliderChange);
            });

            document.querySelectorAll('button').forEach(button => {
                button.addEventListener('click', handleButtonClick);
            });

            drawDiagram();
        }

        function handleSliderChange(event) {
            enforceConstraints();
            drawDiagram();
        }

        function handleButtonClick(event) {
            const buttonId = event.target.id;
            const sliderId = buttonId.replace('btn-', 'slider-').replace('-plus', '');
            const slider = document.getElementById(sliderId);

            const currentValue = parseFloat(slider.value);
            const step = parseFloat(slider.step);
            const maxValue = parseFloat(slider.max);

            if (currentValue + step <= maxValue) {
                slider.value = currentValue + step;
                slider.dispatchEvent(new Event('input'));
            }
        }

        function enforceConstraints() {
            const bottomSpec = parseFloat(document.getElementById('slider-bottom').value);
            const feedComp = parseFloat(document.getElementById('slider-feed-comp').value);
            const distillateSpec = parseFloat(document.getElementById('slider-distillate').value);

            const feedSlider = document.getElementById('slider-feed-comp');
            const step = parseFloat(feedSlider.step);

            // Enforce bottom < feed < distillate
            if (feedComp <= bottomSpec) {
                feedSlider.value = bottomSpec + step;
            }
            if (feedComp >= distillateSpec) {
                feedSlider.value = distillateSpec - step;
            }

            // Update min and max dynamically
            feedSlider.min = bottomSpec + step;
            feedSlider.max = distillateSpec - step;
        }

        function drawDiagram() {
            // Read inputs
            const alpha = parseFloat(document.getElementById('slider-volatility').value);
            const xD = parseFloat(document.getElementById('slider-distillate').value);
            const xB = parseFloat(document.getElementById('slider-bottom').value);
            const xF = parseFloat(document.getElementById('slider-feed-comp').value);
            const q = parseFloat(document.getElementById('slider-feed-quality').value);

            // Clear canvas
            background(255);

            // Draw axes
            stroke(128); // Gray
            strokeWeight(2);
            line(margin, height - margin, width - margin, height - margin); // X-axis
            line(margin, margin, margin, height - margin); // Y-axis

            // Draw tick marks and labels
            textAlign(CENTER, TOP);
            fill(128);
            noStroke();
            for (let i = 0; i <= 10; i++) {
                const value = i / 10;
                const x = map(value, 0, 1, margin, width - margin);
                const y = map(value, 0, 1, height - margin, margin);

                // X-axis ticks
                stroke(128);
                line(x, height - margin, x, height - margin + 5);
                noStroke();
                text(value.toFixed(1), x, height - margin + 10);

                // Y-axis ticks
                stroke(128);
                line(margin - 5, y, margin, y);
                noStroke();
                textAlign(RIGHT, CENTER);
                text(value.toFixed(1), margin - 10, y);
            }

            // Draw equilibrium curve (Red)
            stroke(255, 0, 0);
            strokeWeight(2);
            noFill();
            beginShape();
            for (let x = 0; x <= 1; x += 0.01) {
                const y = (alpha * x) / (1 + (alpha - 1) * x);
                const px = map(x, 0, 1, margin, width - margin);
                const py = map(y, 0, 1, height - margin, margin);
                vertex(px, py);
            }
            endShape();

            // Draw diagonal line (Green)
            stroke(34, 139, 34);
            line(margin, height - margin, width - margin, margin);

            // Calculate intersection point
            let xInt, yInt;
            if (q === 1) {
                // Vertical feed line
                xInt = xF;
                yInt = (alpha * xInt) / (1 + (alpha - 1) * xInt);
            } else if (q === 0) {
                // Horizontal feed line
                yInt = xF;
                // Solve for x: y = alpha*x/(1+(alpha-1)*x)
                xInt = yInt / (alpha - (alpha - 1) * yInt);
            } else {
                // General case
                const m = q / (q - 1);
                // Solve intersection numerically
                let x = xF;
                for (let i = 0; i < 100; i++) {
                    const yLine = xF + m * (x - xF);
                    const yEquil = (alpha * x) / (1 + (alpha - 1) * x);
                    const error = yLine - yEquil;
                    if (Math.abs(error) < 0.0001) break;
                    x -= error * 0.1;
                }
                xInt = x;
                yInt = xF + m * (xInt - xF);
            }

            // Draw operating lines
            // Rectifying line (Cyan)
            stroke(0, 255, 255);
            line(
                map(xD, 0, 1, margin, width - margin),
                map(xD, 0, 1, height - margin, margin),
                map(xInt, 0, 1, margin, width - margin),
                map(yInt, 0, 1, height - margin, margin)
            );

            // Stripping line (Magenta)
            stroke(255, 0, 255);
            line(
                map(xB, 0, 1, margin, width - margin),
                map(xB, 0, 1, height - margin, margin),
                map(xInt, 0, 1, margin, width - margin),
                map(yInt, 0, 1, height - margin, margin)
            );

            // Draw feed line (Dark Blue)
            stroke(0, 0, 205);
            if (q === 1) {
                // Vertical line
                line(
                    map(xF, 0, 1, margin, width - margin),
                    map(xF, 0, 1, height - margin, margin),
                    map(xInt, 0, 1, margin, width - margin),
                    map(yInt, 0, 1, height - margin, margin)
                );
            } else {
                // General case
                line(
                    map(xF, 0, 1, margin, width - margin),
                    map(xF, 0, 1, height - margin, margin),
                    map(xInt, 0, 1, margin, width - margin),
                    map(yInt, 0, 1, height - margin, margin)
                );
            }

            // Calculate and draw stages
            stroke(0); // Black
            strokeWeight(1);
            let stageCount = 0;
            let x = xD;
            let y = xD;

            while (x > xB) {
                // Horizontal line to equilibrium curve
                let xNew = x;
                for (let i = 0; i < 100; i++) {
                    const yEquil = (alpha * xNew) / (1 + (alpha - 1) * xNew);
                    if (Math.abs(yEquil - y) < 0.0001) break;
                    xNew -= (yEquil - y) * 0.1;
                }

                line(
                    map(x, 0, 1, margin, width - margin),
                    map(y, 0, 1, height - margin, margin),
                    map(xNew, 0, 1, margin, width - margin),
                    map(y, 0, 1, height - margin, margin)
                );

                x = xNew;
                stageCount++;

                if (x <= xB) break;

                // Vertical line to operating line
                let yNew;
                if (x > xInt) {
                    // Use rectifying line
                    const mRect = (yInt - xD) / (xInt - xD);
                    yNew = xD + mRect * (x - xD);
                } else {
                    // Use stripping line
                    const mStrip = (yInt - xB) / (xInt - xB);
                    yNew = xB + mStrip * (x - xB);
                }

                line(
                    map(x, 0, 1, margin, width - margin),
                    map(y, 0, 1, height - margin, margin),
                    map(x, 0, 1, margin, width - margin),
                    map(yNew, 0, 1, height - margin, margin)
                );

                y = yNew;
            }

            // Update stage count display
            document.getElementById('stages-count-display').textContent = 
                `The number of stages is ${stageCount}.`;
        }
    </script>
</body>
</html>
