<body>
    <!-- Main container for the demo -->
    <div id="demo-container">

        <!-- Section for user controls -->
        <div id="control-panel">
            <label for="slider-month">month</label>
            <input type="range" id="slider-month" min="0" max="11" step="1" value="0">

            <label for="perspective-buttons">perspective</label>
            <div id="perspective-buttons">
                <button id="btn-2d" class="active">2D</button>
                <button id="btn-3d">3D</button>
            </div>
        </div>

        <!-- Section for the visualization canvas -->
        <div id="visualization-area">
            <canvas id="main-canvas"></canvas>
        </div>

    </div>

    <!-- CDN Includes -->
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/three@0.128.0/examples/js/controls/OrbitControls.js"></script>

    <script>
        // Global variables
        let currentMonth = new Date().getMonth();
        let current2D = true;
        let p5Instance = null;
        let threeScene = null;
        let threeRenderer = null;
        let threeCamera = null;
        let threeControls = null;
        let threeEarth = null;
        let threeMagnetosphere = null;
        let animationId = null;

        // Set initial slider value
        document.getElementById('slider-month').value = currentMonth;

        // Button event listeners
        document.getElementById('btn-2d').addEventListener('click', function() {
            if (current2D) return;
            current2D = true;
            document.getElementById('btn-2d').classList.add('active');
            document.getElementById('btn-3d').classList.remove('active');
            stop3D();
            start2D();
        });

        document.getElementById('btn-3d').addEventListener('click', function() {
            if (!current2D) return;
            current2D = false;
            document.getElementById('btn-3d').classList.add('active');
            document.getElementById('btn-2d').classList.remove('active');
            stop2D();
            start3D();
        });

        // Slider event listener
        document.getElementById('slider-month').addEventListener('input', function(e) {
            currentMonth = parseInt(e.target.value);
            if (current2D && p5Instance) {
                p5Instance.redraw();
            } else if (!current2D && threeEarth) {
                updateEarthPosition();
            }
        });

        // 2D View using p5.js
        function start2D() {
            const sketch = (p) => {
                p.setup = function() {
                    const container = document.getElementById('visualization-area');
                    const canvas = p.createCanvas(container.offsetWidth, container.offsetHeight);
                    canvas.parent('visualization-area');
                    canvas.id('main-canvas');
                    p.noLoop();
                };

                p.draw = function() {
                    p.background(0);
                    p.translate(p.width / 2, p.height / 2);

                    // Calculate tilt based on month
                    const tilt = 23.5 * Math.cos(2 * Math.PI * currentMonth / 12);
                    const tiltRad = p.radians(tilt);

                    // Save original state
                    p.push();

                    // Draw solar wind (static, doesn't rotate)
                    p.stroke(153, 0, 0);
                    p.strokeWeight(2);
                    for (let y = -p.height/2; y < p.height/2; y += 30) {
                        p.line(-p.width/2, y, -100, y);
                        // Arrowheads
                        p.push();
                        p.translate(-100, y);
                        p.line(0, 0, -10, -5);
                        p.line(0, 0, -10, 5);
                        p.pop();
                    }

                    // Rotate entire system except solar wind
                    p.rotate(tiltRad);

                    // Draw bow shock
                    p.noFill();
                    p.stroke(255, 165, 0);
                    p.strokeWeight(3);
                    p.beginShape();
                    for (let angle = -p.PI/2; angle <= p.PI/2; angle += 0.1) {
                        const r = 150 / (1 + 0.5 * p.cos(angle));
                        const x = -r * p.cos(angle);
                        const y = r * p.sin(angle);
                        p.vertex(x, y);
                    }
                    p.endShape();

                    // Draw magnetopause
                    p.stroke(0, 255, 255);
                    p.strokeWeight(2);
                    p.beginShape();
                    for (let angle = -p.PI/2; angle <= p.PI/2; angle += 0.1) {
                        const r = 120 / (1 + 0.6 * p.cos(angle));
                        const x = -r * p.cos(angle);
                        const y = r * p.sin(angle);
                        p.vertex(x, y);
                    }
                    p.endShape();

                    // Draw magnetic field lines
                    p.stroke(128, 128, 128);
                    p.strokeWeight(1);
                    p.noFill();

                    // Closed field lines
                    for (let i = 1; i <= 3; i++) {
                        p.beginShape();
                        for (let angle = 0; angle <= p.TWO_PI; angle += 0.1) {
                            const r = 30 * i;
                            const x = r * p.cos(angle);
                            const y = r * p.sin(angle) * 0.7;
                            p.vertex(x, y);
                        }
                        p.endShape();
                    }

                    // Open field lines (tail)
                    for (let y = -60; y <= 60; y += 30) {
                        p.beginShape();
                        p.vertex(0, y);
                        p.bezierVertex(100, y * 1.2, 200, y * 1.5, p.width/2, y * 2);
                        p.endShape();
                    }

                    // Draw magnetotail
                    p.fill(0, 0, 139, 100);
                    p.noStroke();
                    p.beginShape();
                    p.vertex(50, -60);
                    p.vertex(p.width/2, -120);
                    p.vertex(p.width/2, 120);
                    p.vertex(50, 60);
                    p.endShape(p.CLOSE);

                    // Draw polar cusps
                    p.fill(240, 230, 140);
                    p.noStroke();
                    // North cusp
                    p.beginShape();
                    p.vertex(-30, -50);
                    p.vertex(-60, -70);
                    p.vertex(-40, -40);
                    p.endShape(p.CLOSE);
                    // South cusp
                    p.beginShape();
                    p.vertex(-30, 50);
                    p.vertex(-60, 70);
                    p.vertex(-40, 40);
                    p.endShape(p.CLOSE);

                    // Draw Van Allen belts
                    p.noStroke();
                    // Inner belt
                    p.fill(0, 100, 0, 150);
                    p.arc(0, 0, 80, 80, -p.PI/4, p.PI/4);
                    // Outer belt
                    p.fill(50, 205, 50, 100);
                    p.arc(0, 0, 120, 120, -p.PI/3, p.PI/3);

                    // Draw Earth
                    p.fill(65, 105, 225);
                    p.noStroke();
                    p.circle(0, 0, 60);

                    // Draw some landmasses
                    p.fill(34, 139, 34);
                    p.ellipse(-10, -10, 20, 15);
                    p.ellipse(15, 5, 15, 20);

                    p.pop();
                };

                p.windowResized = function() {
                    const container = document.getElementById('visualization-area');
                    p.resizeCanvas(container.offsetWidth, container.offsetHeight);
                };
            };

            p5Instance = new p5(sketch);
            p5Instance.redraw();
        }

        // 3D View using three.js
        function start3D() {
            const container = document.getElementById('visualization-area');
            const width = container.offsetWidth;
            const height = container.offsetHeight;

            // Scene setup
            threeScene = new THREE.Scene();
            threeScene.background = new THREE.Color(0x000000);

            // Camera
            threeCamera = new THREE.PerspectiveCamera(75, width / height, 0.1, 10000);
            threeCamera.position.set(300, 200, 400);

            // Renderer
            threeRenderer = new THREE.WebGLRenderer({ canvas: document.getElementById('main-canvas') });
            threeRenderer.setSize(width, height);

            // Controls
            threeControls = new THREE.OrbitControls(threeCamera, threeRenderer.domElement);
            threeControls.enableDamping = true;

            // Lights
            const ambientLight = new THREE.AmbientLight(0x404040);
            threeScene.add(ambientLight);

            const sunLight = new THREE.PointLight(0xffffff, 2);
            sunLight.position.set(0, 0, 0);
            threeScene.add(sunLight);

            // Sun
            const sunGeometry = new THREE.SphereGeometry(30, 32, 32);
            const sunMaterial = new THREE.MeshBasicMaterial({ color: 0xFFFF00 });
            const sun = new THREE.Mesh(sunGeometry, sunMaterial);
            threeScene.add(sun);

            // Earth's orbit
            const orbitCurve = new THREE.EllipseCurve(
                -50, 0,  // Center offset (Sun at focus)
                250, 200,  // xRadius, yRadius
                0, 2 * Math.PI,  // Start angle, end angle
                false,  // clockwise
                0  // Rotation
            );
            const orbitPoints = orbitCurve.getPoints(100);
            const orbitGeometry = new THREE.BufferGeometry().setFromPoints(orbitPoints);
            const orbitMaterial = new THREE.LineBasicMaterial({ color: 0xFFFFFF });
            const orbit = new THREE.Line(orbitGeometry, orbitMaterial);
            orbit.rotation.x = -Math.PI / 2;
            threeScene.add(orbit);

            // Earth
            const earthGeometry = new THREE.SphereGeometry(10, 32, 32);
            const earthMaterial = new THREE.MeshStandardMaterial({ color: 0x4169E1 });
            threeEarth = new THREE.Mesh(earthGeometry, earthMaterial);
            threeScene.add(threeEarth);

            // Earth's axis
            const axisDirection = new THREE.Vector3(0, 1, 0);
            const axisOrigin = new THREE.Vector3(0, 0, 0);
            const axisLength = 25;
            const axisColor = 0x00FF00;
            const earthAxis = new THREE.ArrowHelper(axisDirection, axisOrigin, axisLength, axisColor);
            threeEarth.add(earthAxis);

            // Solar wind arrows from Sun
            for (let i = 0; i < 8; i++) {
                const angle = (i / 8) * 2 * Math.PI;
                const direction = new THREE.Vector3(Math.cos(angle), 0, Math.sin(angle));
                const origin = new THREE.Vector3(30 * direction.x, 0, 30 * direction.z);
                const windArrow = new THREE.ArrowHelper(direction, origin, 50, 0xFF0000);
                threeScene.add(windArrow);
            }

            // Magnetosphere
            const magnetospherePoints = [];
            for (let angle = 0; angle <= Math.PI; angle += 0.1) {
                const r = 20 / (1 + 0.6 * Math.cos(angle));
                magnetospherePoints.push(new THREE.Vector2(r * Math.sin(angle), -r * Math.cos(angle)));
            }
            const magnetosphereGeometry = new THREE.LatheGeometry(magnetospherePoints, 32);
            const magnetosphereMaterial = new THREE.MeshStandardMaterial({
                color: 0x0000FF,
                transparent: true,
                opacity: 0.4,
                side: THREE.DoubleSide
            });
            threeMagnetosphere = new THREE.Mesh(magnetosphereGeometry, magnetosphereMaterial);
            threeMagnetosphere.rotation.z = Math.PI / 2;
            threeEarth.add(threeMagnetosphere);

            updateEarthPosition();
            animate3D();
        }

        function updateEarthPosition() {
            if (!threeEarth) return;

            // Calculate Earth's position on orbit
            const angle = (currentMonth / 12) * 2 * Math.PI;
            const a = 250;  // Semi-major axis
            const b = 200;  // Semi-minor axis
            const c = Math.sqrt(a * a - b * b);  // Focus offset

            const x = a * Math.cos(angle) - c;
            const z = b * Math.sin(angle);

            threeEarth.position.set(x, 0, z);

            // Update Earth's tilt
            const tilt = 23.5 * Math.PI / 180;
            threeEarth.rotation.z = tilt;
            threeEarth.rotation.y = angle;

            // Orient magnetosphere tail away from Sun
            const toSun = new THREE.Vector3(-x, 0, -z).normalize();
            const quaternion = new THREE.Quaternion();
            quaternion.setFromUnitVectors(new THREE.Vector3(1, 0, 0), toSun);
            threeMagnetosphere.quaternion.copy(quaternion);
        }

        function animate3D() {
            animationId = requestAnimationFrame(animate3D);
            threeControls.update();
            threeRenderer.render(threeScene, threeCamera);
        }

        function stop2D() {
            if (p5Instance) {
                p5Instance.remove();
                p5Instance = null;
            }
        }

        function stop3D() {
            if (animationId) {
                cancelAnimationFrame(animationId);
                animationId = null;
            }
            if (threeRenderer) {
                threeRenderer.dispose();
                threeRenderer = null;
            }
            threeScene = null;
            threeCamera = null;
            threeControls = null;
            threeEarth = null;
            threeMagnetosphere = null;
        }

        // Handle window resize
        window.addEventListener('resize', function() {
            const container = document.getElementById('visualization-area');
            const width = container.offsetWidth;
            const height = container.offsetHeight;

            if (current2D && p5Instance) {
                p5Instance.windowResized();
            } else if (!current2D && threeRenderer) {
                threeCamera.aspect = width / height;
                threeCamera.updateProjectionMatrix();
                threeRenderer.setSize(width, height);
            }
        });

        // Initialize with 2D view
        start2D();
    </script>
</body>
</html>
