    <script>
        // Lanczos approximation for Gamma function
        function gamma(z) {
            const g = 7;
            const c = [0.99999999999980993, 676.5203681218851, -1259.1392167224028,
                      771.32342877765313, -176.61502916214059, 12.507343278686905,
                      -0.13857109526572012, 9.9843695780195716e-6, 1.5056327351493116e-7];

            if (z < 0.5) return Math.PI / (Math.sin(Math.PI * z) * gamma(1 - z));

            z -= 1;
            let x = c[0];
            for (let i = 1; i < g + 2; i++) {
                x += c[i] / (z + i);
            }

            const t = z + g + 0.5;
            const sqrt2Pi = Math.sqrt(2 * Math.PI);
            return sqrt2Pi * Math.pow(t, z + 0.5) * Math.exp(-t) * x;
        }

        // Gamma PDF function
        function gammaPDF(x, alpha, beta) {
            if (x <= 0) return 0;
            return (Math.pow(x, alpha - 1) * Math.exp(-x / beta)) / (Math.pow(beta, alpha) * gamma(alpha));
        }

        // Generate plot data
        function generatePlotData(alpha, beta) {
            const xValues = [];
            const pdfValues = [];
            const cdfValues = [];

            // Generate x values
            for (let x = 0; x <= 25; x += 0.1) {
                xValues.push(x);
            }

            // Calculate PDF values
            for (let i = 0; i < xValues.length; i++) {
                pdfValues.push(gammaPDF(xValues[i], alpha, beta));
            }

            // Calculate CDF values using trapezoidal integration
            cdfValues.push(0);
            for (let i = 1; i < xValues.length; i++) {
                const dx = xValues[i] - xValues[i-1];
                const trapezoidArea = dx * (pdfValues[i] + pdfValues[i-1]) / 2;
                cdfValues.push(cdfValues[i-1] + trapezoidArea);
            }

            return { xValues, pdfValues, cdfValues };
        }

        // Initialize plot
        function initializePlot() {
            const alpha = parseFloat(document.getElementById('slider-alpha').value);
            const beta = parseFloat(document.getElementById('slider-beta').value);
            const mean = alpha * beta;

            const { xValues, pdfValues, cdfValues } = generatePlotData(alpha, beta);
            const meanPDF = gammaPDF(mean, alpha, beta);

            const pdfTrace = {
                x: xValues,
                y: pdfValues,
                type: 'scatter',
                mode: 'lines',
                line: { color: 'blue', width: 2.5 },
                name: 'PDF'
            };

            const cdfTrace = {
                x: xValues,
                y: cdfValues,
                type: 'scatter',
                mode: 'lines',
                line: { color: '#FF4500', width: 2.5 },
                name: 'CDF'
            };

            const layout = {
                xaxis: { range: [0, 25], title: '' },
                yaxis: { range: [0, 1.1], title: '' },
                showlegend: false,
                margin: { l: 50, r: 50, t: 20, b: 40 },
                shapes: [{
                    type: 'line',
                    x0: mean,
                    y0: 0,
                    x1: mean,
                    y1: meanPDF,
                    line: { color: 'red', width: 3 }
                }]
            };

            Plotly.newPlot('plot-container', [pdfTrace, cdfTrace], layout, {responsive: true});
        }

        // Update all displays and calculations
        function updateDisplay() {
            const alpha = parseFloat(document.getElementById('slider-alpha').value);
            const beta = parseFloat(document.getElementById('slider-beta').value);

            // Update parameter displays
            document.getElementById('display-alpha').textContent = alpha.toFixed(3);
            document.getElementById('display-beta').textContent = beta.toFixed(3);

            // Calculate statistics
            const mean = alpha * beta;
            const variance = alpha * beta * beta;

            // Update statistics displays
            document.getElementById('mean-value').textContent = mean.toFixed(3);
            document.getElementById('variance-value').textContent = variance.toFixed(3);

            // Update MGF formula
            document.getElementById('mgf-formula').innerHTML = 
                `$$(1 - \\beta t)^{-\\alpha} = \\frac{1}{(1 - ${beta.toFixed(3)} t)^{${alpha.toFixed(3)}}}$$`;
            MathJax.typeset();

            // Update plot
            const { xValues, pdfValues, cdfValues } = generatePlotData(alpha, beta);
            const meanPDF = gammaPDF(mean, alpha, beta);

            Plotly.update('plot-container', 
                {
