<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Ternary Distillation Diagram</title>
    <style>
        body {
            margin: 0;
            padding: 0;
            display: flex;
            justify-content: center;
            align-items: center;
            min-height: 100vh;
            background-color: #f0f0f0;
            font-family: Arial, sans-serif;
        }
        #canvas-container {
            border: 2px solid #333;
            box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
        }
    </style>
</head>
<body>
    <div id="canvas-container">
        <canvas id="visualization-canvas"></canvas>
    </div>

    <script src="https://cdn.jsdelivr.net/npm/p5@1.4.0/lib/p5.js"></script>
    <script>
        let locator = { x: 0.2, y: 0.22 };
        let isDragging = false;

        const canvasWidth = 650;
        const canvasHeight = 650;
        const margin = 60;
        const plotWidth = canvasWidth - 2 * margin;
        const plotHeight = canvasHeight - 2 * margin;

        // Azeotrope positions
        const A1 = { x: 0.655, y: 0 };
        const A2 = { x: 0, y: 0.795 };
        const A3 = { x: 0.605, y: 0.345 };
        const A4 = { x: 0.26, y: 0.335 };

        // Distillation boundary path: A2 -> A4 -> A1
        const boundaryPath = [A2, A4, A1];

        function setup() {
            let canvas = createCanvas(canvasWidth, canvasHeight);
            canvas.parent('canvas-container');
        }

        function dataToPixel(dataX, dataY) {
            const pixelX = margin + dataX * plotWidth;
            const pixelY = margin + plotHeight - dataY * plotHeight;
            return { x: pixelX, y: pixelY };
        }

        function pixelToData(pixelX, pixelY) {
            const dataX = (pixelX - margin) / plotWidth;
            const dataY = (margin + plotHeight - pixelY) / plotHeight;
            return { x: dataX, y: dataY };
        }

        function getVector(x, y) {
            const A3 = {x: 0.605, y: 0.345}; // Unstable node (repeller)
            const M = {x: 0, y: 0};         // Stable node (attractor)

            // Vector from repeller to current point
            let v_repel_x = x - A3.x;
            let v_repel_y = y - A3.y;
            // Vector from current point to attractor
            let v_attract_x = M.x - x;
            let v_attract_y = M.y - y;

            // Normalize influence by distance squared, add epsilon to avoid division by zero
            let dist_repel_sq = v_repel_x * v_repel_x + v_repel_y * v_repel_y + 0.01;
            let dist_attract_sq = v_attract_x * v_attract_x + v_attract_y * v_attract_y + 0.01;

            // Weighted sum of influences
            let dx = (v_repel_x / dist_repel_sq) * 0.05 + (v_attract_x / dist_attract_sq) * 0.03;
            let dy = (v_repel_y / dist_repel_sq) * 0.05 + (v_attract_y / dist_attract_sq) * 0.03;

            // Add a small rotational field to shape the curves
            dx += -0.1 * y;
            dy += 0.1 * x;

            return { dx, dy };
        }

        function calculateDistillationLine(startX, startY) {
            const stepSize = 0.005;
            const maxSteps = 250;

            // Forward integration
            let forwardPoints = [];
            let x = startX;
            let y = startY;

            for (let i = 0; i < maxSteps; i++) {
                if (x < 0 || y < 0 || x + y > 1) break;
                forwardPoints.push({ x, y });

                const vector = getVector(x, y);
                x += vector.dx * stepSize;
                y += vector.dy * stepSize;
            }

            // Backward integration
            let backwardPoints = [];
            x = startX;
            y = startY;

            for (let i = 0; i < maxSteps; i++) {
                if (x < 0 || y < 0 || x + y > 1) break;
                backwardPoints.unshift({ x, y });

                const vector = getVector(x, y);
                x -= vector.dx * stepSize;
                y -= vector.dy * stepSize;
            }

            // Combine points
            return [...backwardPoints, ...forwardPoints];
        }

        function closestPointOnSegment(p, a, b) {
            const dx = b.x - a.x;
            const dy = b.y - a.y;
            const t = Math.max(0, Math.min(1, ((p.x - a.x) * dx + (p.y - a.y) * dy) / (dx * dx + dy * dy)));
            return { x: a.x + t * dx, y: a.y + t * dy };
        }

        function constrainToBoundary(x, y) {
            let closestPoint = null;
            let minDist = Infinity;

            for (let i = 0; i < boundaryPath.length - 1; i++) {
                const p = closestPointOnSegment({ x, y }, boundaryPath[i], boundaryPath[i + 1]);
                const dist = Math.sqrt((p.x - x) ** 2 + (p.y - y) ** 2);

                if (dist < minDist) {
                    minDist = dist;
                    closestPoint = p;
                }
            }

            return closestPoint || { x, y };
        }

        function draw() {
            background(255);

            // Draw axes
            stroke(0);
            strokeWeight(1);
            line(margin, margin + plotHeight, margin + plotWidth, margin + plotHeight); // X-axis
            line(margin, margin, margin, margin + plotHeight); // Y-axis

            // Draw hypotenuse
            const p1 = dataToPixel(1, 0);
            const p2 = dataToPixel(0, 1);
            line(p1.x, p1.y, p2.x, p2.y);

            // Draw ticks and labels
            textAlign(CENTER, TOP);
            fill(0);
            noStroke();
            textSize(12);

            for (let i = 0; i <= 5; i++) {
                const val = i * 0.2;

                // X-axis ticks and labels
                const xPos = dataToPixel(val, 0);
                stroke(0);
                line(xPos.x, xPos.y, xPos.x, xPos.y + 5);
                noStroke();
                text(val.toFixed(1), xPos.x, xPos.y + 10);

                // Y-axis ticks and labels
                const yPos = dataToPixel(0, val);
                stroke(0);
                line(yPos.x - 5, yPos.y, yPos.x, yPos.y);
                noStroke();
                textAlign(RIGHT, CENTER);
                text(val.toFixed(1), yPos.x - 10, yPos.y);
            }

            // Draw component labels
            fill(255, 0, 0);
            textAlign(CENTER, TOP);
            textSize(14);
            const methanolPos = dataToPixel(0, 0);
            text("methanol", methanolPos.x, methanolPos.y + 25);

            const chloroformPos = dataToPixel(1, 0);
            text("chloroform", chloroformPos.x, chloroformPos.y + 25);

            const acetonePos = dataToPixel(0, 1);
            textAlign(LEFT, CENTER);
            text("acetone", acetonePos.x + 10, acetonePos.y);

            // Draw azeotrope points
            fill(153, 0, 0);
            noStroke();

            const azeotropes = [
                { point: A1, label: "A₁" },
                { point: A2, label: "A₂" },
                { point: A3, label: "A₃" },
                { point: A4, label: "A₄" }
            ];

            for (let az of azeotropes) {
                const pos = dataToPixel(az.point.x, az.point.y);
                circle(pos.x, pos.y, 12);

                fill(0);
                textAlign(CENTER, CENTER);
                textSize(12);
                text(az.label, pos.x, pos.y - 15);
                fill(153, 0, 0);
            }

            // Draw distillation line
            const distillationLine = calculateDistillationLine(locator.x, locator.y);

            if (distillationLine.length > 2) {
                noFill();
                stroke(0, 0, 255);
                strokeWeight(2);
                beginShape();

                for (let point of distillationLine) {
                    const pos = dataToPixel(point.x, point.y);
                    curveVertex(pos.x, pos.y);
                }

                endShape();
            }

            // Draw locator
            const locatorPos = dataToPixel(locator.x, locator.y);
            fill(255);
            stroke(0);
            strokeWeight(2);
            circle(locatorPos.x, locatorPos.y, 16);
        }

        function mousePressed() {
            const locatorPos = dataToPixel(locator.x, locator.y);
            const dist = Math.sqrt((mouseX - locatorPos.x) ** 2 + (mouseY - locatorPos.y) ** 2);

            if (dist < 15) {
                isDragging = true;
            }
        }

        function mouseDragged() {
            if (isDragging) {
                const dataPos = pixelToData(mouseX, mouseY);
                const constrained = constrainToBoundary(dataPos.x, dataPos.y);
                locator.x = constrained.x;
                locator.y = constrained.y;
            }
        }

        function mouseReleased() {
            isDragging = false;
        }
    </script>
</body>
</html>
