</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <div>lengths of semi-axes</div>
            <div id="slider-group-a">
                <label for="slider-a">a</label>
                <input type="range" id="slider-a" min="0.1" max="5" step="0.1" value="1.5">
                <input type="number" id="input-a" min="0.1" max="5" step="0.1" value="1.5">
            </div>
            <div id="slider-group-b">
                <label for="slider-b">b</label>
                <input type="range" id="slider-b" min="0.1" max="5" step="0.1" value="1.5">
                <input type="number" id="input-b" min="0.1" max="5" step="0.1" value="1.5">
            </div>
            <div id="slider-group-c">
                <label for="slider-c">c</label>
                <input type="range" id="slider-c" min="0.1" max="5" step="0.1" value="1.0">
                <input type="number" id="input-c" min="0.1" max="5" step="0.1" value="1.0">
            </div>
        </div>
        <div id="canvas-container"></div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/three@0.128.0/examples/js/controls/OrbitControls.js"></script>

    <script>
        // Global variables
        let scene, camera, renderer, controls;
        let paraboloidMesh, wireframeMesh;
        let a = 1.5, b = 1.5, c = 1.0;

        // Initialize the scene
        function init() {
            // Create scene
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0x000000);

            // Create camera
            const container = document.getElementById('canvas-container');
            const width = container.clientWidth;
            const height = container.clientHeight;
            camera = new THREE.PerspectiveCamera(45, width / height, 0.1, 1000);
            camera.position.set(5, 5, 5);
            camera.lookAt(0, 0, 0);

            // Create renderer
            renderer = new THREE.WebGLRenderer({ antialias: true });
            renderer.setSize(width, height);
            container.appendChild(renderer.domElement);

            // Create controls
            controls = new THREE.OrbitControls(camera, renderer.domElement);
            controls.enableDamping = true;
            controls.dampingFactor = 0.05;

            // Add lighting
            const ambientLight = new THREE.AmbientLight(0x404040);
            scene.add(ambientLight);

            const directionalLight = new THREE.DirectionalLight(0xffffff, 1);
            directionalLight.position.set(5, 10, 5);
            scene.add(directionalLight);

            // Add coordinate axes
            addCoordinateAxes();

            // Create initial paraboloid
            updateParaboloid();

            // Handle window resize
            window.addEventListener('resize', onWindowResize);
        }

        // Add coordinate axes
        function addCoordinateAxes() {
            const axisLength = 5;
            const axisColor = 0x888888;

            // X-axis
            const xGeometry = new THREE.BufferGeometry();
            xGeometry.setAttribute('position', new THREE.Float32BufferAttribute([
                -axisLength, 0, 0,
                axisLength, 0, 0
            ], 3));
            const xMaterial = new THREE.LineBasicMaterial({ color: axisColor });
            const xAxis = new THREE.Line(xGeometry, xMaterial);
            scene.add(xAxis);

            // Y-axis
            const yGeometry = new THREE.BufferGeometry();
            yGeometry.setAttribute('position', new THREE.Float32BufferAttribute([
                0, -axisLength, 0,
                0, axisLength, 0
            ], 3));
            const yMaterial = new THREE.LineBasicMaterial({ color: axisColor });
            const yAxis = new THREE.Line(yGeometry, yMaterial);
            scene.add(yAxis);

            // Z-axis
            const zGeometry = new THREE.BufferGeometry();
            zGeometry.setAttribute('position', new THREE.Float32BufferAttribute([
                0, 0, -axisLength,
                0, 0, axisLength
            ], 3));
            const zMaterial = new THREE.LineBasicMaterial({ color: axisColor });
            const zAxis = new THREE.Line(zGeometry, zMaterial);
            scene.add(zAxis);
        }

        // Create elliptic paraboloid geometry
        function createParaboloidGeometry() {
            const range = 2.0;
            const slices = 50;
            const stacks = 50;

            const geometry = new THREE.ParametricGeometry(
                function(u, v, target) {
                    const x = a * v * range * Math.cos(u * 2 * Math.PI);
                    const y = b * v * range * Math.sin(u * 2 * Math.PI);
                    const z = c * (v * range) * (v * range);
                    target.set(x, y, z);
                },
                slices,
                stacks
            );

            // Add vertex colors based on height
            const colors = [];
            const positions = geometry.attributes.position.array;
            const maxZ = c * range * range;

            for (let i = 0; i < positions.length; i += 3) {
                const z = positions[i + 2];
                const normalizedZ = z / maxZ;

                // Interpolate between yellow/orange and red/orange
                const color1 = new THREE.Color(0xFFD700); // Bright yellow/orange
                const color2 = new THREE.Color(0xFF4500); // Dark orange/red
                const color = color1.lerp(color2, normalizedZ);

                colors.push(color.r, color.g, color.b);
            }

            geometry.setAttribute('color', new THREE.Float32BufferAttribute(colors, 3));

            return geometry;
        }

        // Update the paraboloid mesh
        function updateParaboloid() {
            // Remove existing meshes
            if (paraboloidMesh) {
                scene.remove(paraboloidMesh);
                paraboloidMesh.geometry.dispose();
                paraboloidMesh.material.dispose();
            }
            if (wireframeMesh) {
                scene.remove(wireframeMesh);
                wireframeMesh.geometry.dispose();
                wireframeMesh.material.dispose();
            }

            // Create new geometry
            const geometry = createParaboloidGeometry();

            // Create solid mesh
            const solidMaterial = new THREE.MeshPhongMaterial({
                vertexColors: true,
                side: THREE.DoubleSide
            });
            paraboloidMesh = new THREE.Mesh(geometry, solidMaterial);
            scene.add(paraboloidMesh);

            // Create wireframe mesh
            const wireframeMaterial = new THREE.MeshBasicMaterial({
                wireframe: true,
                color: 0xcccccc
            });
            wireframeMesh = new THREE.Mesh(geometry, wireframeMaterial);
            scene.add(wireframeMesh);
        }

        // Animation loop
        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        // Handle window resize
        function onWindowResize() {
            const container = document.getElementById('canvas-container');
            const width = container.clientWidth;
            const height = container.clientHeight;

            camera.aspect = width / height;
            camera.updateProjectionMatrix();
            renderer.setSize(width, height);
        }

        // Setup slider and input synchronization
        function setupControls() {
            // Slider-input pairs
            const pairs = [
                { slider: 'slider-a', input: 'input-a', variable: 'a' },
                { slider: 'slider-b', input: 'input-b', variable: 'b' },
                { slider: 'slider-c', input: 'input-c', variable: 'c' }
            ];

            pairs.forEach(pair => {
                const slider = document.getElementById(pair.slider);
                const input = document.getElementById(pair.input);

                // Sync slider to input
                slider.addEventListener('input', function() {
                    input.value = this.value;
                    window[pair.variable] = parseFloat(this.value);
                    updateParaboloid();
                });

                // Sync input to slider
                input.addEventListener('input', function() {
                    slider.value = this.value;
                    window[pair.variable] = parseFloat(this.value);
                    updateParaboloid();
                });
            });
        }

        // Initialize everything when the page loads
        window.addEventListener('load', function() {
            init();
            setupControls();
            animate();
        });
    </script>
</body>
</html>
