            scene.add(ambientLight);

            const directionalLight = new THREE.DirectionalLight(0xffffff, 0.5);
            directionalLight.position.set(1, 1, 1);
            scene.add(directionalLight);

            // Add axes helper
            const axesHelper = new THREE.AxesHelper(5);
            scene.add(axesHelper);

            // Handle window resize
            window.addEventListener('resize', onWindowResize);
        }

        function onWindowResize() {
            camera.aspect = window.innerWidth / window.innerHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(window.innerWidth, window.innerHeight - 120);
        }

        // Populate m selectors based on j values
        function populateMSelector(jValue, selectorId, currentM) {
            const selector = document.getElementById(selectorId);
            selector.innerHTML = '';

            for (let m = -jValue; m <= jValue; m += 1) {
                const button = document.createElement('button');
                button.dataset.value = m;
                button.textContent = m >= 0 ? m.toString() : m.toString();
                if (Math.abs(m - currentM) < 0.01) {
                    button.classList.add('selected');
                }
                selector.appendChild(button);
            }
        }

        // Populate j selector based on j1 and j2
        function populateJSelector() {
            const selector = document.getElementById('j-selector');
            selector.innerHTML = '';

            const jMin = Math.abs(j1 - j2);
            const jMax = j1 + j2;

            for (let jVal = jMin; jVal <= jMax; jVal += 1) {
                const button = document.createElement('button');
                button.dataset.value = jVal;
                button.textContent = jVal % 1 === 0 ? jVal.toString() : jVal.toFixed(1);
                if (Math.abs(jVal - j) < 0.01) {
                    button.classList.add('selected');
                }
                selector.appendChild(button);
            }
        }

        // Create vector arrow
        function createVector(origin, direction, length, color) {
            const dir = direction.clone().normalize();
            const arrowHelper = new THREE.ArrowHelper(dir, origin, length, color, length * 0.3, length * 0.2);
            return arrowHelper;
        }

        // Create cone
        function createCone(height, angle, color, opacity = 0.3) {
            const radius = height * Math.tan(angle);
            const geometry = new THREE.ConeGeometry(radius, height, 32, 1, true);
            const material = new THREE.MeshPhongMaterial({
                color: color,
                transparent: true,
                opacity: opacity,
                side: THREE.DoubleSide
            });
            const cone = new THREE.Mesh(geometry, material);
            cone.position.z = height / 2;
            return cone;
        }

        // Clear visualization
        function clearVisualization() {
            if (vectorJ) scene.remove(vectorJ);
            if (vectorJ1) scene.remove(vectorJ1);
            if (vectorJ2) scene.remove(vectorJ2);
            if (coneJ) scene.remove(coneJ);
            if (coneJ1) scene.remove(coneJ1);
            if (coneJ2) scene.remove(coneJ2);
        }

        // Update visualization
        function updateVisualization() {
            clearVisualization();

            const L_J1 = Math.sqrt(j1 * (j1 + 1));
            const L_J2 = Math.sqrt(j2 * (j2 + 1));
            const L_J = Math.sqrt(j * (j + 1));

            const m = m1 + m2;

            // Check if coupled model applies
            const isCoupled = (j >= j1 && j >= j2);

            if (isCoupled) {
                // Coupled model
                const theta_J = Math.acos(m / L_J);

                // J vector (precesses around Z)
                const dirJ = new THREE.Vector3(
                    Math.sin(theta_J) * Math.cos(phi_z),
                    Math.sin(theta_J) * Math.sin(phi_z),
                    Math.cos(theta_J)
                );
                vectorJ = createVector(new THREE.Vector3(0, 0, 0), dirJ, L_J, 0x000000);
                scene.add(vectorJ);

                // J cone
                coneJ = createCone(L_J, theta_J, 0x000000);
                scene.add(coneJ);

                // Calculate angles for J1 and J2 relative to J using law of cosines
                const cos_theta_1 = (L_J * L_J + L_J1 * L_J1 - L_J2 * L_J2) / (2 * L_J * L_J1);
                const cos_theta_2 = (L_J * L_J + L_J2 * L_J2 - L_J1 * L_J1) / (2 * L_J * L_J2);
                const theta_1 = Math.acos(cos_theta_1);
                const theta_2 = Math.acos(cos_theta_2);

                // Create J1 and J2 in J's reference frame
                const perpJ = new THREE.Vector3(1, 0, 0).cross(dirJ).normalize();
                if (perpJ.length() < 0.01) {
                    perpJ.set(0, 1, 0).cross(dirJ).normalize();
                }

                const rotAxis = perpJ.clone().cross(dirJ).normalize();

                // J1 vector
                const dirJ1Local = dirJ.clone().multiplyScalar(Math.cos(theta_1))
                    .add(perpJ.clone().multiplyScalar(Math.sin(theta_1) * Math.cos(phi_j)))
                    .add(rotAxis.clone().multiplyScalar(Math.sin(theta_1) * Math.sin(phi_j)));
                vectorJ1 = createVector(new THREE.Vector3(0, 0, 0), dirJ1Local, L_J1, 0xff0000);
                scene.add(vectorJ1);

                // J2 vector (must complete the triangle)
                const dirJ2 = dirJ.clone().multiplyScalar(L_J).sub(dirJ1Local.clone().multiplyScalar(L_J1)).normalize();
                vectorJ2 = createVector(dirJ1Local.clone().multiplyScalar(L_J1), dirJ2, L_J2, 0x0000ff);
                scene.add(vectorJ2);

                // Cones for J1 and J2
                coneJ1 = createCone(L_J1, theta_1, 0xff0000);
                coneJ1.lookAt(dirJ);
                coneJ1.position.set(0, 0, 0);
                scene.add(coneJ1);

                coneJ2 = createCone(L_J2, theta_2, 0x0000ff);
                coneJ2.lookAt(dirJ);
                coneJ2.position.copy(dirJ1Local.clone().multiplyScalar(L_J1));
                scene.add(coneJ2);

            } else {
                // Uncoupled model
                const theta_1 = Math.acos(m1 / L_J1);
                const theta_2 = Math.acos(m2 / L_J2);

                // J1 vector
                const dirJ1 = new THREE.Vector3(
