                    Math.sin(theta_2) * Math.sin(phi_z + Math.PI/3),
                    Math.cos(theta_2)
                );
                vectorJ2 = createVector(new THREE.Vector3(0, 0, 0), dirJ2, L_J2, 0x0000ff);
                scene.add(vectorJ2);

                // Cones
                coneJ1 = createCone(L_J1, theta_1, 0xff0000);
                scene.add(coneJ1);

                coneJ2 = createCone(L_J2, theta_2, 0x0000ff);
                scene.add(coneJ2);
            }
        }

        // Animation loop
        function animate() {
            animationId = requestAnimationFrame(animate);

            if (isAnimating) {
                phi_z += 0.02;
                phi_j += 0.03;
                updateVisualization();
            }

            controls.update();
            renderer.render(scene, camera);
        }

        // Event handlers
        document.getElementById('j1-selector').addEventListener('click', (e) => {
            if (e.target.tagName === 'BUTTON') {
                document.querySelectorAll('#j1-selector button').forEach(b => b.classList.remove('selected'));
