                j = Math.min(j1 + j2, Math.max(Math.abs(j1 - j2), j));
                resetAnimation();
                updateVisualization();
            }
        });

        document.getElementById('j2-selector').addEventListener('click', (e) => {
            if (e.target.tagName === 'BUTTON') {
                document.querySelectorAll('#j2-selector button').forEach(b => b.classList.remove('selected'));
                e.target.classList.add('selected');
                j2 = parseFloat(e.target.dataset.value);
                m2 = Math.min(j2, Math.max(-j2, m2));
                populateMSelector(j2, 'm2-selector', m2);
                populateJSelector();
                j = Math.min(j1 + j2, Math.max(Math.abs(j1 - j2), j));
                resetAnimation();
                updateVisualization();
            }
        });

        document.getElementById('m1-selector').addEventListener('click', (e) => {
            if (e.target.tagName === 'BUTTON') {
                document.querySelectorAll('#m1-selector button').forEach(b => b.classList.remove('selected'));
                e.target.classList.add('selected');
                m1 = parseFloat(e.target.dataset.value);
                resetAnimation();
                updateVisualization();
            }
        });

        document.getElementById('m2-selector').addEventListener('click', (e) => {
            if (e.target.tagName === 'BUTTON') {
                document.querySelectorAll('#m2-selector button').forEach(b => b.classList.remove('selected'));
                e.target.classList.add('selected');
                m2 = parseFloat(e.target.dataset.value);
                resetAnimation();
                updateVisualization();
            }
        });

        document.getElementById('j-selector').addEventListener('click', (e) => {
            if (e.target.tagName === 'BUTTON') {
                document.querySelectorAll('#j-selector button').forEach(b => b.classList.remove('selected'));
                e.target.classList.add('selected');
                j = parseFloat(e.target.dataset.value);
                resetAnimation();
                updateVisualization();
            }
        });

        document.getElementById('btn-play-pause').addEventListener('click', () => {
            isAnimating = !isAnimating;
            document.getElementById('btn-play-pause').textContent = isAnimating ? '❚❚' : '▶';
        });

        document.getElementById('btn-reset').addEventListener('click', () => {
            resetAnimation();
        });

        function resetAnimation() {
            isAnimating = false;
            phi_z = 0;
            phi_j = 0;
            document.getElementById('btn-play-pause').textContent = '▶';
            updateVisualization();
        }

        // Initialize
        initThreeJS();
        populateMSelector(j1, 'm1-selector', m1);
        populateMSelector(j2, 'm2-selector', m2);
        populateJSelector();
        updateVisualization();
        animate();
    </script>
