</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <label for="steps-slider">steps</label>
            <input type="range" id="steps-slider" min="0" max="100" value="61">
            <span id="steps-value">+ 61</span>
            <label>box</label>
            <button id="btn-none">None</button>
            <button id="btn-box">Box</button>
            <button id="btn-scattered" class="active">Scattered</button>
        </div>

        <div id="viz-container">
            <div id="recursive-container">
                <h3>Recursive</h3>
                <div id="canvas-recursive"></div>
            </div>
            <div id="iterative-container">
                <h3>Iterative</h3>
                <div id="canvas-iterative"></div>
            </div>
        </div>
    </div>

    <script>
        // Constants
        const GRID_SIZE = 10;
        const CELL_SIZE = 20;
        const CANVAS_SIZE = GRID_SIZE * CELL_SIZE + 1;
        const START_X = 4;
        const START_Y = 4;

        // Cell states
        const EMPTY = 0;
        const OBSTACLE = 1;
        const FILLED = 2;

        // Global variables
        let grid = [];
        let recursiveHistory = [];
        let iterativeHistory = [];
        let currentMode = 'scattered';

        // Slider elements
        const slider = document.getElementById('steps-slider');
        const sliderValue = document.getElementById('steps-value');

        // Button elements
        const btnNone = document.getElementById('btn-none');
        const btnBox = document.getElementById('btn-box');
        const btnScattered = document.getElementById('btn-scattered');

        // Initialize grid
        function createGrid() {
            grid = [];
            for (let i = 0; i < GRID_SIZE; i++) {
                grid[i] = [];
                for (let j = 0; j < GRID_SIZE; j++) {
                    grid[i][j] = EMPTY;
                }
            }
        }

        // Apply obstacle patterns
        function applyObstacles(mode) {
            createGrid();

            if (mode === 'box') {
                for (let i = 0; i < GRID_SIZE; i++) {
                    grid[0][i] = OBSTACLE;
                    grid[GRID_SIZE-1][i] = OBSTACLE;
                    grid[i][0] = OBSTACLE;
                    grid[i][GRID_SIZE-1] = OBSTACLE;
                }
            } else if (mode === 'scattered') {
                // Use fixed seed for consistent pattern
                let seed = 42;
                for (let i = 0; i < GRID_SIZE; i++) {
                    for (let j = 0; j < GRID_SIZE; j++) {
                        if (i !== START_Y || j !== START_X) {
                            seed = (seed * 9301 + 49297) % 233280;
                            if ((seed / 233280) < 0.25) {
                                grid[i][j] = OBSTACLE;
                            }
                        }
                    }
                }
            }
        }

        // Recursive flood fill
        function floodFillRecursive(x, y, visited) {
            if (x < 0 || x >= GRID_SIZE || y < 0 || y >= GRID_SIZE) return;
            if (grid[y][x] === OBSTACLE) return;
            if (visited[y][x]) return;

            visited[y][x] = true;
            recursiveHistory.push([x, y]);

            // North, West, South, East
            floodFillRecursive(x, y - 1, visited);
            floodFillRecursive(x - 1, y, visited);
            floodFillRecursive(x, y + 1, visited);
            floodFillRecursive(x + 1, y, visited);
        }

        // Iterative flood fill (BFS)
        function floodFillIterative() {
            const visited = Array(GRID_SIZE).fill(false).map(() => Array(GRID_SIZE).fill(false));
            const queue = [[START_X, START_Y]];
            visited[START_Y][START_X] = true;

            while (queue.length > 0) {
                const [x, y] = queue.shift();
                iterativeHistory.push([x, y]);

                // Check all four neighbors
                const neighbors = [
                    [x, y - 1], // North
                    [x - 1, y], // West
                    [x, y + 1], // South
                    [x + 1, y]  // East
                ];

                for (const [nx, ny] of neighbors) {
                    if (nx >= 0 && nx < GRID_SIZE && ny >= 0 && ny < GRID_SIZE &&
                        grid[ny][nx] !== OBSTACLE && !visited[ny][nx]) {
                        visited[ny][nx] = true;
                        queue.push([nx, ny]);
                    }
                }
            }
        }

        // Run both algorithms
        function runAlgorithms() {
            recursiveHistory = [];
            iterativeHistory = [];

            // Run recursive
            const visitedRecursive = Array(GRID_SIZE).fill(false).map(() => Array(GRID_SIZE).fill(false));
            floodFillRecursive(START_X, START_Y, visitedRecursive);

            // Run iterative
            floodFillIterative();

            // Update slider max
            const maxSteps = Math.max(recursiveHistory.length, iterativeHistory.length);
            slider.max = maxSteps;
        }

        // P5.js sketch for recursive
        const recursiveSketch = (p) => {
            p.setup = () => {
                p.createCanvas(CANVAS_SIZE, CANVAS_SIZE);
                p.noLoop();
            };

            p.drawGrid = (steps) => {
                p.background(255);
                p.stroke(0);
                p.strokeWeight(1);

                // Draw grid lines
                for (let i = 0; i <= GRID_SIZE; i++) {
                    p.line(i * CELL_SIZE, 0, i * CELL_SIZE, CANVAS_SIZE);
                    p.line(0, i * CELL_SIZE, CANVAS_SIZE, i * CELL_SIZE);
                }

                // Draw obstacles
                p.fill(0);
                p.noStroke();
                for (let i = 0; i < GRID_SIZE; i++) {
                    for (let j = 0; j < GRID_SIZE; j++) {
                        if (grid[i][j] === OBSTACLE) {
                            p.rect(j * CELL_SIZE + 1, i * CELL_SIZE + 1, CELL_SIZE - 1, CELL_SIZE - 1);
                        }
                    }
                }

                // Draw filled cells
                p.fill(0, 0, 255);
                for (let i = 0; i < Math.min(steps, recursiveHistory.length); i++) {
                    const [x, y] = recursiveHistory[i];
                    p.rect(x * CELL_SIZE + 1, y * CELL_SIZE + 1, CELL_SIZE - 1, CELL_SIZE - 1);
                }
            };
        };

        // P5.js sketch for iterative
        const iterativeSketch = (p) => {
            p.setup = () => {
                p.createCanvas(CANVAS_SIZE, CANVAS_SIZE);
                p.noLoop();
            };

            p.drawGrid = (steps) => {
                p.background(255);
                p.stroke(0);
                p.strokeWeight(1);

                // Draw grid lines
                for (let i = 0; i <= GRID_SIZE; i++) {
                    p.line(i * CELL_SIZE, 0, i * CELL_SIZE, CANVAS_SIZE);
                    p.line(0, i * CELL_SIZE, CANVAS_SIZE, i * CELL_SIZE);
                }

                // Draw obstacles
                p.fill(0);
                p.noStroke();
                for (let i = 0; i < GRID_SIZE; i++) {
                    for (let j = 0; j < GRID_SIZE; j++) {
                        if (grid[i][j] === OBSTACLE) {
                            p.rect(j * CELL_SIZE + 1, i * CELL_SIZE + 1, CELL_SIZE - 1, CELL_SIZE - 1);
                        }
                    }
                }

                // Draw filled cells
                p.fill(0, 0, 255);
                for (let i = 0; i < Math.min(steps, iterativeHistory.length); i++) {
                    const [x, y] = iterativeHistory[i];
                    p.rect(x * CELL_SIZE + 1, y * CELL_SIZE + 1, CELL_SIZE - 1, CELL_SIZE - 1);
                }
            };
        };

        // Create p5 instances
        let recursiveP5 = new p5(recursiveSketch, 'canvas-recursive');
        let iterativeP5 = new p5(iterativeSketch, 'canvas-iterative');

        // Update visualization
        function updateVisualization() {
            const steps = parseInt(slider.value);
            sliderValue.textContent = `+ ${steps}`;
            recursiveP5.drawGrid(steps);
            iterativeP5.drawGrid(steps);
        }

        // Reset simulation
        function resetSimulation(mode) {
            currentMode = mode;
            applyObstacles(mode);
            runAlgorithms();
            slider.value = mode === 'scattered' ? 61 : 0;
            updateVisualization();

            // Update button states
            document.querySelectorAll('button').forEach(btn => btn.classList.remove('active'));
            if (mode === 'none') btnNone.classList.add('active');
            else if (mode === 'box') btnBox.classList.add('active');
            else if (mode === 'scattered') btnScattered.classList.add('active');
        }

        // Event listeners
        slider.addEventListener('input', updateVisualization);
        btnNone.addEventListener('click', () => resetSimulation('none'));
        btnBox.addEventListener('click', () => resetSimulation('box'));
        btnScattered.addEventListener('click', () => resetSimulation('scattered'));

        // Initialize
        resetSimulation('scattered');
    </script>
</body>
</html>
