        // Initialize button styles
        updateButtonStyles();

        // p5.js sketch
        function setup() {
            let canvas = createCanvas(1200, 350);
            canvas.parent('canvas-container');
            noLoop();
        }

        function draw() {
            background(255);

            const data = examples[currentExample - 1];
            const original = data.original;

            // Get unique triplets
            const uniqueTriplets = new Set();
            for (let i = 0; i < original.length; i += 3) {
                const triplet = original.slice(i, i + 3).join('');
                uniqueTriplets.add(triplet);
            }
            const sortedTriplets = Array.from(uniqueTriplets).sort();

            // Calculate layout parameters
            const bitSize = 20;
            const bitGap = 2;
            const tripletGap = 8;
            const topRowY = 50;
            const bottomRowY = 200;
            const labelHeight = 30;
            const startX = 50;

            // Calculate positions for top row
            const topRowPositions = [];
            let currentX = startX;
            for (let i = 0; i < original.length; i += 3) {
                topRowPositions.push({
                    x: currentX,
                    centerX: currentX + (bitSize * 3 + bitGap * 2) / 2
                });
                currentX += bitSize * 3 + bitGap * 2 + tripletGap;
            }

            // Calculate positions for bottom row
            const bottomRowPositions = [];
            const keyBlockPositions = {};
            currentX = startX;

            // Key blocks
            for (const triplet of sortedTriplets) {
                const blockWidth = 20 + bitSize * 3 + bitGap * 2; // marker + 3 bits
                keyBlockPositions[triplet] = {
                    x: currentX,
                    width: blockWidth,
                    centerX: currentX + blockWidth / 2
                };
                currentX += blockWidth + 15;
            }

            // Codeword blocks
            for (let i = 0; i < original.length; i += 3) {
                const triplet = original.slice(i, i + 3).join('');
                const encoding = fullEncodingMap[triplet];
                const codewordWidth = encoding.codeword.length * bitSize + (encoding.codeword.length - 1) * bitGap;

                bottomRowPositions.push({
                    x: currentX,
                    width: codewordWidth,
                    centerX: currentX + codewordWidth / 2,
                    triplet: triplet,
                    codeword: encoding.codeword,
                    label: encoding.label
                });
                currentX += codewordWidth + 15;
            }

            // Draw top row
            for (let i = 0; i < original.length; i++) {
                const tripletIndex = Math.floor(i / 3);
                const bitIndex = i % 3;
                const x = topRowPositions[tripletIndex].x + bitIndex * (bitSize + bitGap);

                fill(original[i] === 1 ? 0 : 211);
                rect(x, topRowY, bitSize, bitSize);
            }

            // Draw bottom row - key blocks
            for (const triplet of sortedTriplets) {
                const pos = keyBlockPositions[triplet];
                const encoding = fullEncodingMap[triplet];

                // Draw marker
                fill(0);
                textAlign(CENTER, CENTER);
                textSize(16);
                text('>', pos.x + 10, bottomRowY + bitSize / 2);

                // Draw triplet bits
                const tripletBits = triplet.split('').map(b => parseInt(b));
                for (let i = 0; i < 3; i++) {
                    fill(tripletBits[i] === 1 ? 0 : 211);
                    rect(pos.x + 20 + i * (bitSize + bitGap), bottomRowY, bitSize, bitSize);
                }

                // Draw label
                drawLabel(pos.x, bottomRowY + bitSize + 5, pos.width, encoding.label);
            }

            // Draw bottom row - codeword blocks
            for (const pos of bottomRowPositions) {
                // Draw codeword bits
                for (let i = 0; i < pos.codeword.length; i++) {
                    fill(pos.codeword[i] === 1 ? 0 : 211);
                    rect(pos.x + i * (bitSize + bitGap), bottomRowY, bitSize, bitSize);
                }

                // Draw label
                drawLabel(pos.x, bottomRowY + bitSize + 5, pos.width, pos.label);
            }

            // Draw arrows
            stroke(128);
            strokeWeight(2);
            for (let i = 0; i < topRowPositions.length; i++) {
                const topPos = topRowPositions[i];
                const bottomPos = bottomRowPositions[i];

                // Draw line
                line(topPos.centerX, topRowY + bitSize, bottomPos.centerX, bottomRowY - 5);

                // Draw arrowhead
                push();
                translate(bottomPos.centerX, bottomRowY - 5);
                rotate(PI / 2);
                fill(128);
                noStroke();
                triangle(0, 0, -5, -8, 5, -8);
                pop();
            }

            // Draw label for unique triplets
            stroke(128);
            strokeWeight(2);
            for (const triplet of sortedTriplets) {
                const keyPos = keyBlockPositions[triplet];
                const tripletIndices = [];

                // Find all occurrences of this triplet
                for (let i = 0; i < original.length; i += 3) {
                    const currentTriplet = original.slice(i, i + 3).join('');
                    if (currentTriplet === triplet) {
                        tripletIndices.push(i / 3);
                    }
                }

                // Draw lines from key block to each occurrence
                for (const idx of tripletIndices) {
                    const topPos = topRowPositions[idx];
                    line(keyPos.centerX, bottomRowY - 5, topPos.centerX, topRowY + bitSize);
                }
            }
        }

        function drawLabel(x, y, width, label) {
            fill(224);
            stroke(0);
            strokeWeight(1);

            // Draw tab shape
            beginShape();
            vertex(x - 5, y);
            vertex(x + width + 5, y);
            vertex(x + width, y + labelHeight);
            vertex(x, y + labelHeight);
            closePath();
            endShape();

            // Draw label text
            fill(0);
            noStroke();
            textAlign(CENTER, CENTER);
            textSize(12);
            text(label, x + width / 2, y + labelHeight / 2);
        }
    </script>
</body>
