        #canvas-container {
            display: flex;
            justify-content: center;
            align-items: center;
        }
    </style>
</head>
<body>
    <h1>Angle Bisectors on the Circumcircle</h1>
    <p>Extend the angle bisectors of the triangle ABC to meet the circumcircle at A', B' and C'. Then AA' ⊥ B'C', BB' ⊥ A'C', and CC' ⊥ A'B'.</p>
    <div id="canvas-container"></div>

    <script>
        let angleA = 7 * Math.PI / 6; // 210 degrees
        let angleB = 11 * Math.PI / 6; // 330 degrees
        let angleC = Math.PI / 2; // 90 degrees

        let dragging = null;
        const centerX = 250;
        const centerY = 250;
        const radius = 200;
        const pointRadius = 6;
        const tolerance = 15;

        function setup() {
            let canvas = createCanvas(500, 500);
            canvas.parent('canvas-container');
            canvas.id('p5-canvas');
        }

        function draw() {
            background('#F0F0F0');

            // Calculate positions of A, B, C
            let ax = centerX + radius * cos(angleA);
            let ay = centerY + radius * sin(angleA);
            let bx = centerX + radius * cos(angleB);
            let by = centerY + radius * sin(angleB);
            let cx = centerX + radius * cos(angleC);
            let cy = centerY + radius * sin(angleC);

            // Calculate positions of A', B', C' using vector averaging
            let angleAprime = atan2(sin(angleB) + sin(angleC), cos(angleB) + cos(angleC));
            let angleBprime = atan2(sin(angleA) + sin(angleC), cos(angleA) + cos(angleC));
            let angleCprime = atan2(sin(angleA) + sin(angleB), cos(angleA) + cos(angleB));

            let apx = centerX + radius * cos(angleAprime);
            let apy = centerY + radius * sin(angleAprime);
            let bpx = centerX + radius * cos(angleBprime);
            let bpy = centerY + radius * sin(angleBprime);
            let cpx = centerX + radius * cos(angleCprime);
            let cpy = centerY + radius * sin(angleCprime);

            // Draw circumcircle
            noFill();
            stroke('#6A5ACD');
            strokeWeight(4);
            circle(centerX, centerY, radius * 2);

            // Draw triangle ABC
            stroke('#6A5ACD');
            strokeWeight(4);
            line(ax, ay, bx, by);
            line(bx, by, cx, cy);
            line(cx, cy, ax, ay);

            // Draw extended bisectors and triangle A'B'C'
            stroke('#FFA500');
            strokeWeight(4);
            line(ax, ay, apx, apy);
            line(bx, by, bpx, bpy);
            line(cx, cy, cpx, cpy);
            line(apx, apy, bpx, bpy);
            line(bpx, bpy, cpx, cpy);
            line(cpx, cpy, apx, apy);

            // Draw points
            strokeWeight(1);
            stroke('#000000');

            // Draw A, B, C with crosshairs
            drawInteractivePoint(ax, ay);
            drawInteractivePoint(bx, by);
            drawInteractivePoint(cx, cy);

            // Draw A', B', C'
            fill('#FFA500');
            circle(apx, apy, pointRadius * 2);
            circle(bpx, bpy, pointRadius * 2);
            circle(cpx, cpy, pointRadius * 2);

            // Draw labels
            noStroke();
            fill('#000000');
            textSize(16);
            textAlign(CENTER, CENTER);

            // Offset labels radially outward
            let labelOffset = 20;
            text('A', ax + labelOffset * cos(angleA), ay + labelOffset * sin(angleA));
            text('B', bx + labelOffset * cos(angleB), by + labelOffset * sin(angleB));
            text('C', cx + labelOffset * cos(angleC), cy + labelOffset * sin(angleC));
            text("A'", apx + labelOffset * cos(angleAprime), apy + labelOffset * sin(angleAprime));
            text("B'", bpx + labelOffset * cos(angleBprime), bpy + labelOffset * sin(angleBprime));
            text("C'", cpx + labelOffset * cos(angleCprime), cpy + labelOffset * sin(angleCprime));
        }

        function drawInteractivePoint(x, y) {
            fill('#FFA500');
            circle(x, y, pointRadius * 2);
            stroke('#000000');
            strokeWeight(1);
            line(x - pointRadius, y, x + pointRadius, y);
            line(x, y - pointRadius, x, y + pointRadius);
        }

        function mousePressed() {
            let ax = centerX + radius * cos(angleA);
            let ay = centerY + radius * sin(angleA);
            let bx = centerX + radius * cos(angleB);
            let by = centerY + radius * sin(angleB);
            let cx = centerX + radius * cos(angleC);
            let cy = centerY + radius * sin(angleC);

            if (dist(mouseX, mouseY, ax, ay) < tolerance) {
                dragging = 'A';
            } else if (dist(mouseX, mouseY, bx, by) < tolerance) {
                dragging = 'B';
            } else if (dist(mouseX, mouseY, cx, cy) < tolerance) {
                dragging = 'C';
            }
        }

        function mouseDragged() {
            if (dragging) {
                let angle = atan2(mouseY - centerY, mouseX - centerX);
                if (dragging === 'A') {
                    angleA = angle;
                } else if (dragging === 'B') {
                    angleB = angle;
                } else if (dragging === 'C') {
                    angleC = angle;
                }
            }
        }

        function mouseReleased() {
            dragging = null;
        }
    </script>
</body>
</html>
