    <style>
        body {
            margin: 0;
            padding: 20px;
            font-family: Arial, sans-serif;
            display: flex;
            justify-content: center;
            align-items: center;
            min-height: 100vh;
            background-color: #f5f5f5;
        }

        #container {
            display: flex;
            flex-direction: column;
            align-items: center;
            gap: 20px;
        }

        #controls {
            background-color: #f0f0f0;
            padding: 15px 30px;
            border: 1px solid #ddd;
            border-radius: 5px;
            display: flex;
            align-items: center;
            gap: 15px;
        }

        #controls label {
            font-weight: bold;
        }

        #slider-steps {
            width: 200px;
        }

        #canvas-container {
            border: 1px solid #ccc;
        }
    </style>
</head>
<body>
    <div id="container">
        <div id="controls">
            <label for="slider-steps">carbon cycle steps</label>
            <input type="range" id="slider-steps" min="0" max="7" value="0" step="1">
            <span id="slider-value">0</span>
        </div>
        <div id="canvas-container"></div>
    </div>

    <script>
        let slider;
        let sliderValue = 0;
        let hoverDescription = null;
        let processLabels = [];

        const tooltipDescriptions = {
            "Photosynthesis": "Plants use sunlight, water, and carbon dioxide to create food, releasing oxygen.",
            "Cellular Respiration": "Animals and plants break down sugars for energy, releasing carbon dioxide.",
            "Organisms' Waste\nand Decay": "Decomposers break down dead organic matter, returning carbon to the soil and atmosphere.",
            "Fossils and\nFossil Fuels": "Over millions of years, buried organic matter can turn into fossil fuels like coal and oil.",
            "Fossil Fuel\nExtraction": "Humans extract fossil fuels from the ground for energy.",
            "Fossil Fuel\nEmissions": "Burning fossil fuels releases large amounts of carbon dioxide into the atmosphere.",
            "CO2 Diffusion": "Carbon dioxide dissolves into and is released from bodies of water like oceans."
        };

        function setup() {
            let canvas = createCanvas(600, 500);
            canvas.parent('canvas-container');

            slider = select('#slider-steps');
            slider.input(() => {
                sliderValue = slider.value();
                select('#slider-value').html(sliderValue);
            });
        }

        function draw() {
            background(255);
            processLabels = [];

            // Draw static scene
            drawStaticScene();

            // Draw dynamic elements based on slider value
            if (sliderValue >= 1) drawCellularRespiration();
            if (sliderValue >= 2) drawOrganismsWasteDecay();
            if (sliderValue >= 3) drawFossilsFuels();
            if (sliderValue >= 4) drawPhotosynthesis();
            if (sliderValue >= 5) drawFossilFuelEmissions();
            if (sliderValue >= 6) drawFossilFuelExtraction();
            if (sliderValue >= 7) drawCO2Diffusion();

            // Check hover and draw tooltip
            checkHover();
            if (hoverDescription) {
                drawTooltip();
            }
        }

        function drawStaticScene() {
            // Sky
            fill('#4169E1');
            noStroke();
            rect(0, 0, width, 300);

            // Sun
            fill('#FFD700');
            circle(500, 80, 60);

            // Cloud
            fill(255);
            ellipse(300, 60, 80, 40);
            ellipse(320, 50, 60, 30);
            ellipse(280, 50, 60, 30);

            // Grass
            fill('#228B22');
            rect(0, 200, width, 150);

            // Soil
            fill('#8B4513');
            rect(0, 350, width, 150);

            // Water
            fill('#0000CD');
            beginShape();
            vertex(450, 250);
            vertex(600, 250);
            vertex(600, 500);
            vertex(450, 500);
            bezierVertex(450, 400, 430, 350, 450, 250);
            endShape(CLOSE);

            // Trees
            // Tree 1
            fill('#8B4513');
            rect(280, 200, 40, 80);
            fill('#006400');
            ellipse(300, 180, 100, 80);

            // Tree 2
            fill('#8B4513');
            rect(380, 220, 30, 60);
            fill('#006400');
            ellipse(395, 200, 80, 70);

            // Sheep
            fill(255);
            ellipse(200, 250, 60, 40); // body
            ellipse(180, 240, 30, 30); // head
            ellipse(190, 260, 20, 20); // wool
            ellipse(210, 260, 20, 20); // wool
            fill(0);
            circle(175, 238, 4); // eye

            // Factory
            fill('#696969');
            rect(460, 200, 80, 100);
            fill('#999999');
            rect(520, 150, 20, 50);
            rect(515, 140, 30, 15);

            // Fossil deposits
            fill(0);
            circle(220, 420, 15);
            circle(240, 440, 12);
            circle(200, 430, 10);
            circle(450, 420, 15);
            circle(470, 440, 12);
        }

        function drawArrow(x1, y1, x2, y2) {
            stroke(0);
            strokeWeight(2);
            line(x1, y1, x2, y2);

            // Calculate arrow head
            let angle = atan2(y2 - y1, x2 - x1);
            let arrowLength = 10;

            push();
            translate(x2, y2);
            rotate(angle);
            fill(0);
            noStroke();
            triangle(0, 0, -arrowLength, -arrowLength/2, -arrowLength, arrowLength/2);
            pop();
        }

        function drawProcessLabel(x, y, label) {
            fill(0);
            noStroke();
            textAlign(CENTER);
            textSize(12);
            text(label, x, y);

            // Calculate bounding box for hover detection
            let lines = label.split('\n');
            let lineHeight = 14;
            let maxWidth = 0;
            for (let line of lines) {
                maxWidth = max(maxWidth, textWidth(line));
            }

            processLabels.push({
                x: x - maxWidth/2,
                y: y - lineHeight,
                width: maxWidth,
                height: lineHeight * lines.length,
                label: label,
                description: tooltipDescriptions[label]
            });
        }

        function drawPhotosynthesis() {
            drawArrow(350, 80, 300, 180);
            drawProcessLabel(360, 120, "Photosynthesis");
        }

        function drawCellularRespiration() {
            drawArrow(200, 240, 250, 180);
            drawProcessLabel(150, 180, "Cellular Respiration");
        }

        function drawOrganismsWasteDecay() {
            drawArrow(220, 280, 220, 350);
            drawProcessLabel(240, 320, "Organisms' Waste\nand Decay");
        }

        function drawFossilsFuels() {
            drawArrow(220, 370, 220, 440);
            drawProcessLabel(240, 440, "Fossils and\nFossil Fuels");
        }

        function drawFossilFuelEmissions() {
            drawArrow(480, 200, 430, 120);
            drawProcessLabel(440, 150, "Fossil Fuel\nEmissions");
        }

        function drawFossilFuelExtraction() {
            drawArrow(450, 420, 480, 280);
            drawProcessLabel(400, 350, "Fossil Fuel\nExtraction");
        }

        function drawCO2Diffusion() {
            drawArrow(520, 250, 550, 400);
            drawArrow(570, 400, 540, 250);
            drawProcessLabel(480, 420, "CO2 Diffusion");
        }

        function checkHover() {
            hoverDescription = null;
            for (let label of processLabels) {
                if (mouseX >= label.x && mouseX <= label.x + label.width &&
                    mouseY >= label.y && mouseY <= label.y + label.height) {
                    hoverDescription = label.description;
                    break;
                }
            }
        }

        function drawTooltip() {
            let tooltipWidth = textWidth(hoverDescription) + 20;
            let tooltipHeight = 40;
            let tooltipX = mouseX + 10;
            let tooltipY = mouseY - 30;

            // Keep tooltip within canvas bounds
            if (tooltipX + tooltipWidth > width) {
                tooltipX = mouseX - tooltipWidth - 10;
            }
            if (tooltipY < 0) {
                tooltipY = mouseY + 10;
            }

            // Draw tooltip background
            fill(0, 0, 0, 150);
            noStroke();
            rect(tooltipX, tooltipY, tooltipWidth, tooltipHeight, 5);

            // Draw tooltip text
            fill(255);
            textAlign(LEFT);
            textSize(12);
            text(hoverDescription, tooltipX + 10, tooltipY + 25);
        }
    </script>
</body>
</html>
