            <!-- Acceleration Toggle -->
            <label>acceleration</label>
            <div id="toggle-acceleration" class="toggle-switch">
                <input type="radio" id="radio-acceleration-off" name="acceleration" value="off">
                <label for="radio-acceleration-off">off</label>
                <input type="radio" id="radio-acceleration-on" name="acceleration" value="on" checked>
                <label for="radio-acceleration-on">on</label>
            </div>

            <!-- Coordinate System Selector -->
            <label for="select-coords">coordinate system</label>
            <select id="select-coords">
                <option value="rectangular">rectangular</option>
                <option value="normal-tangential">normal-tangential</option>
                <option value="polar">polar</option>
            </select>

            <!-- Instantaneous Circle Toggle -->
            <label>instantaneous circle</label>
            <div id="toggle-circle" class="toggle-switch">
                <input type="radio" id="radio-circle-off" name="circle" value="off" checked>
                <label for="radio-circle-off">off</label>
                <input type="radio" id="radio-circle-on" name="circle" value="on">
                <label for="radio-circle-on">on</label>
            </div>
        </div>

        <!-- Right: Visualization Canvas -->
        <div id="canvas-container">
            <!-- p5.js canvas will be inserted here -->
        </div>
    </div>

    <script>
        // Global state
        let state = {
            time: 0.4,
            showPosition: false,
            showVelocity: false,
            showAcceleration: true,
            coordinateSystem: 'rectangular',
            showCircle: false
        };

        // Control references
        const timeSlider = document.getElementById('slider-time');
        const resetBtn = document.getElementById('btn-reset-time');
        const positionRadios = document.getElementsByName('position');
        const velocityRadios = document.getElementsByName('velocity');
        const accelerationRadios = document.getElementsByName('acceleration');
        const coordSelect = document.getElementById('select-coords');
        const circleRadios = document.getElementsByName('circle');

        // Event listeners
        timeSlider.addEventListener('input', (e) => {
            state.time = parseFloat(e.target.value);
            redraw();
        });

        resetBtn.addEventListener('click', () => {
            state.time = 0.4;
            timeSlider.value = 0.4;
            redraw();
        });

        positionRadios.forEach(radio => {
            radio.addEventListener('change', (e) => {
                state.showPosition = e.target.value === 'on';
                redraw();
            });
        });

        velocityRadios.forEach(radio => {
            radio.addEventListener('change', (e) => {
                state.showVelocity = e.target.value === 'on';
                redraw();
            });
        });

        accelerationRadios.forEach(radio => {
            radio.addEventListener('change', (e) => {
                state.showAcceleration = e.target.value === 'on';
                redraw();
            });
        });

        coordSelect.addEventListener('change', (e) => {
            state.coordinateSystem = e.target.value;
            redraw();
        });

        circleRadios.forEach(radio => {
            radio.addEventListener('change', (e) => {
                state.showCircle = e.target.value === 'on';
                redraw();
            });
        });

        // p5.js sketch
        function setup() {
            const canvas = createCanvas(800, 600);
            canvas.parent('canvas-container');
            textAlign(CENTER, CENTER);
        }

        function draw() {
            background(250);

            // Transform to center and scale
            translate(width / 2, height / 2);
            scale(150, -150); // Flip y-axis and scale

            // Draw axes
            strokeWeight(1/150);
            stroke(0);
            line(-3, 0, 3, 0);
            line(0, -3, 0, 3);

            // Draw tick marks and labels
            push();
            scale(1, -1); // Flip back for text
            textSize(12/150);
            fill(0);
            for (let i = -2; i <= 2; i++) {
                if (i !== 0) {
                    // X-axis ticks
                    line(i, -0.05, i, 0.05);
                    text(i, i, -0.2);
                    // Y-axis ticks
                    line(-0.05, i, 0.05, i);
                    text(i, -0.2, i);
                }
            }
            // Axis labels
            text("x(t)", 2.5, -0.2);
            text("y(t)", -0.2, 2.5);
            pop();

            // Draw lemniscate curve
            stroke(0, 0, 255);
            strokeWeight(2/150);
            noFill();
            beginShape();
            for (let t = 0; t <= TWO_PI; t += 0.01) {
                let x = cos(t);
                let y = sin(2 * t);
                vertex(x, y);
            }
            endShape();

            // Calculate current position and kinematics
            let t = state.time;
            let x = cos(t);
            let y = sin(2 * t);
            let vx = -sin(t);
            let vy = 2 * cos(2 * t);
            let ax = -cos(t);
            let ay = -4 * sin(2 * t);

            // Draw point
            fill(0);
            noStroke();
            circle(x, y, 0.05);

            // Draw position vector
            if (state.showPosition) {
                stroke(0, 100, 0);
                strokeWeight(2/150);
                drawArrow(0, 0, x, y);
            }

            // Draw velocity vector
            if (state.showVelocity) {
                stroke(139, 0, 0);
                strokeWeight(2/150);
                drawArrow(x, y, x + vx * 0.3, y + vy * 0.3);
            }

            // Draw acceleration vector
            if (state.showAcceleration) {
                stroke(139, 0, 139);
                strokeWeight(2/150);
                drawArrow(x, y, x + ax * 0.15, y + ay * 0.15);
            }

            // Draw basis vectors
            stroke(0);
            strokeWeight(1/150);
            if (state.coordinateSystem === 'rectangular') {
                drawDashedArrow(x, y, x + 0.4, y);
                drawDashedArrow(x, y, x, y + 0.4);
                push();
                scale(1, -1);
                fill(0);
                textSize(14/150);
                text('î', x + 0.5, -y);
                text('ĵ', x, -(y + 0.5));
                pop();
            } else if (state.coordinateSystem === 'normal-tangential') {
                let v_mag = sqrt(vx * vx + vy * vy);
                let et_x = vx / v_mag;
                let et_y = vy / v_mag;
                let en_x = -et_y;
                let en_y = et_x;
                drawDashedArrow(x, y, x + et_x * 0.4, y + et_y * 0.4);
                drawDashedArrow(x, y, x + en_x * 0.4, y + en_y * 0.4);
                push();
                scale(1, -1);
                fill(0);
                textSize(14/150);
                text('êₜ', x + et_x * 0.5, -(y + et_y * 0.5));
                text('êₙ', x + en_x * 0.5, -(y + en_y * 0.5));
                pop();
            } else if (state.coordinateSystem === 'polar') {
                let r_mag = sqrt(x * x + y * y);
                let er_x = x / r_mag;
                let er_y = y / r_mag;
                let etheta_x = -er_y;
                let etheta_y = er_x;
                drawDashedArrow(x, y, x + er_x * 0.4, y + er_y * 0.4);
                drawDashedArrow(x, y, x + etheta_x * 0.4, y + etheta_y * 0.4);
                push();
                scale(1, -1);
                fill(0);
                textSize(14/150);
                text('êᵣ', x + er_x * 0.5, -(y + er_y * 0.5));
                text('êθ', x + etheta_x * 0.5, -(y + etheta_y * 0.5));
                pop();
            }

            // Draw instantaneous circle
            if (state.showCircle) {
                let v_mag = sqrt(vx * vx + vy * vy);
                let cross = vx * ay - vy * ax;
                if (abs(cross) > 0.001) {
                    let rho = pow(v_mag, 3) / abs(cross);
                    let et_x = vx / v_mag;
                    let et_y = vy / v_mag;
                    let en_x = -et_y;
                    let en_y = et_x;
                    if (cross < 0) {
                        en_x = -en_x;
                        en_y = -en_y;
                    }
                    let cx = x + rho * en_x;
                    let cy = y + rho * en_y;

                    stroke(128);
                    strokeWeight(1/150);
                    noFill();
                    drawDashedCircle(cx, cy, rho);
                }
            }
        }

        function drawArrow(x1, y1, x2, y2) {
            line(x1, y1, x2, y2);
            let angle = atan2(y2 - y1, x2 - x1);
            let arrowSize = 0.08;
            push();
            translate(x2, y2);
            rotate(angle);
            line(0, 0, -arrowSize, arrowSize/2);
            line(0, 0, -arrowSize, -arrowSize/2);
            pop();
        }

        function drawDashedArrow(x1, y1, x2, y2) {
            drawDashedLine(x1, y1, x2, y2);
            let angle = atan2(y2 - y1, x2 - x1);
            let arrowSize = 0.08;
            push();
            translate(x2, y2);
            rotate(angle);
            line(0, 0, -arrowSize, arrowSize/2);
            line(0, 0, -arrowSize, -arrowSize/2);
            pop();
        }

        function drawDashedLine(x1, y1, x2, y2) {
            let d = dist(x1, y1, x2, y2);
            let dashLength = 0.05;
            let steps = floor(d / dashLength);
            for (let i = 0; i < steps; i += 2) {
                let t1 = i / steps;
                let t2 = min((i + 1) / steps, 1);
                line(lerp(x1, x2, t1), lerp(y1, y2, t1), 
                     lerp(x1, x2, t2), lerp(y1, y2, t2));
            }
        }

        function drawDashedCircle(cx, cy, r) {
            let segments = 60;
            let dashLength = TWO_PI / segments;
            for (let i = 0; i < segments; i += 2) {
                let a1 = i * dashLength;
                let a2 = (i + 1) * dashLength;
                arc(cx, cy, r * 2, r * 2, a1, a2);
            }
        }
    </script>
</body>
