<body>
    <div class="main-container">
        <div class="control-panel">
            <button id="btn-reset">×</button>

            <div class="control-group">
                <label>amplitude</label>
                <input type="range" id="slider-amplitude" min="0.1" max="1.0" step="0.001" value="0.3">
                <span id="amplitude-value">0.300</span>
            </div>

            <div class="control-group">
                <label>stiffness</label>
                <input type="range" id="slider-stiffness" min="0.1" max="10.0" step="0.01" value="1.0">
                <span id="stiffness-value">1.00</span>
            </div>

            <div class="control-group">
                <label>mass</label>
                <input type="range" id="slider-mass" min="0.1" max="10.0" step="0.01" value="1.0">
                <span id="mass-value">1.00</span>
            </div>

            <div class="time-controls">
                <label>time</label>
                <button id="btn-step-back">◀</button>
                <button id="btn-play-pause">▶</button>
                <button id="btn-step-forward">▶|</button>
            </div>
        </div>

        <div class="visualization-container">
            <div id="plot-canvas-container"></div>
            <div id="animation-canvas-container"></div>
        </div>
    </div>

    <script>
        // State variables
        let amplitude = 0.3;
        let stiffness = 1.0;
        let mass = 1.0;
        let time = 0;
        let isPlaying = false;
        let angularFrequency = Math.sqrt(stiffness / mass);
        let positionHistory = [];

        // UI elements
        const amplitudeSlider = document.getElementById('slider-amplitude');
        const stiffnessSlider = document.getElementById('slider-stiffness');
        const massSlider = document.getElementById('slider-mass');
        const amplitudeValue = document.getElementById('amplitude-value');
        const stiffnessValue = document.getElementById('stiffness-value');
        const massValue = document.getElementById('mass-value');
        const playPauseBtn = document.getElementById('btn-play-pause');
        const stepBackBtn = document.getElementById('btn-step-back');
        const stepForwardBtn = document.getElementById('btn-step-forward');
        const resetBtn = document.getElementById('btn-reset');

        // p5.js instances
        let plotSketch;
        let animationSketch;

        // Constants
        const TIME_STEP = 0.05;
        const MANUAL_STEP = 0.1;
        const PLOT_COLOR = '#6363B2';

        // Plot canvas sketch
        const plotCanvas = function(p) {
            let xMax = 25;

            p.setup = function() {
                const container = document.getElementById('plot-canvas-container');
                const canvas = p.createCanvas(container.offsetWidth, 300);
                canvas.parent('plot-canvas-container');
            };

            p.draw = function() {
                p.background(255);

                // Extend x-axis if needed
                if (time > xMax - 5) {
                    xMax = time + 10;
                }

                // Draw axes
                p.stroke(0);
                p.strokeWeight(1);
                const originY = p.height / 2;
                p.line(0, originY, p.width, originY); // x-axis
                p.line(30, 0, 30, p.height); // y-axis

                // Draw grid and labels
                p.stroke(200);
                p.strokeWeight(0.5);

                // Y-axis grid and labels
                for (let y = -1; y <= 1; y += 0.5) {
                    const py = p.map(y, -1, 1, p.height - 20, 20);
                    p.line(30, py, p.width, py);
                    if (Math.abs(y) === 1 || y === 0) {
                        p.noStroke();
                        p.fill(0);
                        p.textAlign(p.RIGHT, p.CENTER);
                        p.text(y.toFixed(0), 25, py);
                        p.stroke(200);
                    }
                }

                // X-axis grid and labels
                for (let x = 0; x <= xMax; x += 5) {
                    const px = p.map(x, 0, xMax, 30, p.width - 10);
                    p.line(px, 0, px, p.height);
                    p.noStroke();
                    p.fill(0);
                    p.textAlign(p.CENTER, p.TOP);
                    p.text(x, px, originY + 5);
                    p.stroke(200);
                }

                // Draw waveform
                if (positionHistory.length > 1) {
                    p.stroke(PLOT_COLOR);
                    p.strokeWeight(2);
                    p.noFill();
                    p.beginShape();
                    for (let point of positionHistory) {
                        const x = p.map(point.time, 0, xMax, 30, p.width - 10);
                        const y = p.map(point.position, -1, 1, p.height - 20, 20);
                        p.vertex(x, y);
                    }
                    p.endShape();
                }
            };

            p.windowResized = function() {
                const container = document.getElementById('plot-canvas-container');
                p.resizeCanvas(container.offsetWidth, 300);
            };
        };

        // Animation canvas sketch
        const animationCanvas = function(p) {
            p.setup = function() {
                const container = document.getElementById('animation-canvas-container');
                const canvas = p.createCanvas(container.offsetWidth, 300);
                canvas.parent('animation-canvas-container');
            };

            p.draw = function() {
                p.background(255);

                const centerX = p.width / 2;
                const anchorY = 30;

                // Draw axis
                p.stroke(0);
                p.strokeWeight(1);
                p.line(20, 0, 20, p.height);

                // Draw axis labels
                p.noStroke();
                p.fill(0);
                p.textAlign(p.RIGHT, p.CENTER);
                for (let y = -1; y <= 1; y++) {
                    const py = p.map(y, -1.2, 1.2, p.height - 20, 20);
                    p.text(y, 15, py);
                }

                // Calculate current position
                const currentY = amplitude * Math.sin(angularFrequency * time);
                const blockY = p.map(currentY, -1.2, 1.2, p.height - 20, 20);

                // Draw anchor point
                p.stroke(0);
                p.strokeWeight(3);
                p.line(centerX - 15, anchorY, centerX + 15, anchorY);

                // Draw spring
                p.stroke(0);
                p.strokeWeight(1);
                const springBottom = blockY - 20;
                const springLength = springBottom - anchorY;
                const numZigs = 15;
                const zigHeight = springLength / numZigs;
                const zigWidth = 15;

                p.noFill();
                p.beginShape();
                p.vertex(centerX, anchorY);
                for (let i = 0; i < numZigs; i++) {
                    const y1 = anchorY + i * zigHeight;
                    const y2 = anchorY + (i + 0.5) * zigHeight;
                    const y3 = anchorY + (i + 1) * zigHeight;

                    if (i === 0) {
                        p.vertex(centerX, y1);
                    }
                    p.vertex(centerX + zigWidth * (i % 2 === 0 ? 1 : -1), y2);
                    p.vertex(centerX, y3);
                }
                p.endShape();

                // Draw mass block
                p.fill(PLOT_COLOR);
                p.noStroke();
                p.rectMode(p.CENTER);
                p.rect(centerX, blockY, 40, 40);
            };

            p.windowResized = function() {
                const container = document.getElementById('animation-canvas-container');
                p.resizeCanvas(container.offsetWidth, 300);
            };
        };

        // Initialize p5 sketches
        window.addEventListener('load', () => {
            plotSketch = new p5(plotCanvas);
            animationSketch = new p5(animationCanvas);
        });

        // Helper functions
        function updateSimulation() {
            angularFrequency = Math.sqrt(stiffness / mass);
            const position = amplitude * Math.sin(angularFrequency * time);
            positionHistory.push({ time: time, position: position });
        }

        function resetSimulation() {
            time = 0;
            positionHistory = [];
            isPlaying = false;
            playPauseBtn.textContent = '▶';
        }

        // Event listeners
        amplitudeSlider.addEventListener('input', (e) => {
            amplitude = parseFloat(e.target.value);
            amplitudeValue.textContent = amplitude.toFixed(3);
            resetSimulation();
            updateSimulation();
        });

        stiffnessSlider.addEventListener('input', (e) => {
            stiffness = parseFloat(e.target.value);
            stiffnessValue.textContent = stiffness.toFixed(2);
            resetSimulation();
            updateSimulation();
        });

        massSlider.addEventListener('input', (e) => {
            mass = parseFloat(e.target.value);
            massValue.textContent = mass.toFixed(2);
            resetSimulation();
            updateSimulation();
        });

        playPauseBtn.addEventListener('click', () => {
            isPlaying = !isPlaying;
            playPauseBtn.textContent = isPlaying ? '||' : '▶';
        });

        stepBackBtn.addEventListener('click', () => {
            if (!isPlaying && time > 0) {
                time = Math.max(0, time - MANUAL_STEP);
                positionHistory = positionHistory.filter(p => p.time <= time);
                updateSimulation();
            }
        });

        stepForwardBtn.addEventListener('click', () => {
            if (!isPlaying) {
                time += MANUAL_STEP;
                updateSimulation();
            }
        });

        resetBtn.addEventListener('click', () => {
            // Reset sliders
            amplitudeSlider.value = 0.3;
            stiffnessSlider.value = 1.0;
            massSlider.value = 1.0;

            // Reset displayed values
            amplitudeValue.textContent = '0.300';
            stiffnessValue.textContent = '1.00';
            massValue.textContent = '1.00';

            // Reset state
            amplitude = 0.3;
            stiffness = 1.0;
            mass = 1.0;
            resetSimulation();
            updateSimulation();
        });

        // Animation loop
        function animate() {
            if (isPlaying) {
                time += TIME_STEP;
                updateSimulation();
            }
            requestAnimationFrame(animate);
        }

        // Initialize
        updateSimulation();
        animate();
    </script>
</body>
</html>
