        <!-- Right column: Visualization Area -->
        <div id="visualization-area">
            <div id="fit-results">\(a_{Ar} = 4,345.\) \(r^2 = 1.000\)</div>
            <div id="plot-container"></div>
        </div>
    </div>

    <!-- CDN Scripts -->
    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
    <script id="MathJax-script" async src="https://cdn.jsdelivr.net/npm/mathjax@3/es5/tex-mml-chtml.js"></script>

    <script>
        // Configure MathJax
        window.MathJax = {
            tex: {
                inlineMath: [['\\(', '\\)']],
                displayMath: [['\\[', '\\]']]
            }
        };

        // Wait for MathJax to load
        document.addEventListener("DOMContentLoaded", function() {
            setTimeout(initialize, 500);
        });

        function initialize() {
            // Add event listeners
            document.getElementById('btn-model-ep').addEventListener('click', () => handleModelButton('ep'));
            document.getElementById('btn-model-ar').addEventListener('click', () => handleModelButton('ar'));
            document.getElementById('btn-tmax-low').addEventListener('click', () => handleTmaxButton('low'));
            document.getElementById('btn-tmax-high').addEventListener('click', () => handleTmaxButton('high'));

            document.getElementById('slider-n').addEventListener('input', update);
            document.getElementById('slider-tmin').addEventListener('input', update);
            document.getElementById('slider-tmax').addEventListener('input', update);
            document.getElementById('slider-tref').addEventListener('input', update);
            document.getElementById('slider-param').addEventListener('input', update);

            // Initial update
            update();
        }

        function handleModelButton(model) {
            document.getElementById('btn-model-ep').classList.toggle('active', model === 'ep');
            document.getElementById('btn-model-ar').classList.toggle('active', model === 'ar');
            update();
        }

        function handleTmaxButton(type) {
            document.getElementById('btn-tmax-low').classList.toggle('active', type === 'low');
            document.getElementById('btn-tmax-high').classList.toggle('active', type === 'high');

            const tmaxSlider = document.getElementById('slider-tmax');
            if (type === 'low') {
                tmaxSlider.max = 1200;
                if (parseInt(tmaxSlider.value) > 1200) {
                    tmaxSlider.value = 1200;
                }
            } else {
                tmaxSlider.max = 6000;
            }
            update();
        }

        function update() {
            // Read current state
            const isEP = document.getElementById('btn-model-ep').classList.contains('active');
            const n = parseInt(document.getElementById('slider-n').value);
            let tmin = parseInt(document.getElementById('slider-tmin').value);
            let tmax = parseInt(document.getElementById('slider-tmax').value);
            let tref = parseInt(document.getElementById('slider-tref').value);
            const param = parseInt(document.getElementById('slider-param').value);

            // Enforce constraints
            if (tmin >= tmax) {
                tmin = tmax - 1;
                document.getElementById('slider-tmin').value = tmin;
            }
            if (tref < tmin) {
                tref = tmin;
                document.getElementById('slider-tref').value = tref;
            }
            if (tref > tmax) {
                tref = tmax;
                document.getElementById('slider-tref').value = tref;
            }

            // Update labels
            document.getElementById('label-n').textContent = n;
            document.getElementById('label-tmin').textContent = tmin + '.';
            document.getElementById('label-tmax').textContent = tmax + '.';
            document.getElementById('label-tref').textContent = tref + '.';
            document.getElementById('label-param-value').textContent = param + '.';

            // Update parameter label
            const paramLabel = document.getElementById('label-param-name');
            paramLabel.innerHTML = isEP ? '\\(a_{EP}\\)' : '\\(a_{Ar}\\)';
            MathJax.typeset([paramLabel]);

            // Generate data
            const temperatures = [];
            const rates = [];
            const trefK = tref + 273.15;

            for (let i = 0; i < n; i++) {
                const t = tmin + (tmax - tmin) * i / (n - 1);
                const tK = t + 273.15;
                temperatures.push(t);

                let y;
                if (isEP) {
                    // Eyring-Polanyi model
                    y = (tK / trefK) * Math.exp(param * (1/trefK - 1/tK));
                } else {
                    // Arrhenius model
                    y = Math.exp(param * (1/trefK - 1/tK));
                }
                rates.push(y);
            }

            // Fit data using the other model
            const fitResult = fitModel(temperatures, rates, tref, !isEP);

            // Update fit results
            const fitModelName = isEP ? 'Ar' : 'EP';
            const fitResultsDiv = document.getElementById('fit-results');
            fitResultsDiv.innerHTML = `\\(a_{${fitModelName}} = ${fitResult.a.toLocaleString()}.\\) \\(r^2 = ${fitResult.r2.toFixed(3)}\\)`;
            MathJax.typeset([fitResultsDiv]);

            // Draw plot
            const dataModelName = isEP ? 'Eyring-Polanyi' : 'Arrhenius';
            const fitModelFullName = isEP ? 'Arrhenius' : 'Eyring-Polanyi';
            drawPlot(temperatures, rates, tmin, tmax, tref, fitResult.a, !isEP, 
                    `${fitModelFullName} model fit to ${dataModelName} data`);
        }

        function fitModel(temperatures, rates, tref, useEP) {
            const trefK = tref + 273.15;
            let bestA = 0;
            let minRSS = Infinity;

            // Grid search for best parameter
            for (let a = 0; a <= 10000; a += 1) {
                let rss = 0;
                for (let i = 0; i < temperatures.length; i++) {
                    const tK = temperatures[i] + 273.15;
                    let yPred;
                    if (useEP) {
                        yPred = (tK / trefK) * Math.exp(a * (1/trefK - 1/tK));
                    } else {
                        yPred = Math.exp(a * (1/trefK - 1/tK));
                    }
                    rss += Math.pow(rates[i] - yPred, 2);
                }
                if (rss < minRSS) {
                    minRSS = rss;
                    bestA = a;
                }
            }

            // Calculate R-squared
            const meanRate = rates.reduce((sum, r) => sum + r, 0) / rates.length;
            const ssTot = rates.reduce((sum, r) => sum + Math.pow(r - meanRate, 2), 0);
            const r2 = 1 - (minRSS / ssTot);

            return { a: bestA, r2: r2 };
        }

        function drawPlot(temperatures, rates, tmin, tmax, tref, fitA, useEP, title) {
            // Data points trace
            const trace1 = {
                x: temperatures,
                y: rates,
                mode: 'markers',
                type: 'scatter',
                name: 'Data',
                marker: {
                    color: 'green',
                    size: 10,
                    line: {
                        color: 'blue',
                        width: 2
                    }
                }
            };

            // Fit curve trace
            const fitX = [];
            const fitY = [];
            const trefK = tref + 273.15;

            for (let i = 0; i < 200; i++) {
                const t = tmin + (tmax - tmin) * i / 199;
                const tK = t + 273.15;
                fitX.push(t);

                let y;
                if (useEP) {
                    y = (tK / trefK) * Math.exp(fitA * (1/trefK - 1/tK));
                } else {
                    y = Math.exp(fitA * (1/trefK - 1/tK));
                }
                fitY.push(y);
            }

            const trace2 = {
                x: fitX,
                y: fitY,
                mode: 'lines',
                type: 'scatter',
                name: 'Fit',
                line: {
                    color: 'green',
                    width: 2
                }
            };

            const layout = {
                title: title,
                xaxis: {
                    title: 'T °C'
                },
                yaxis: {
                    title: 'k(T) / k(T<sub>ref</sub>)'
                }
            };

            Plotly.newPlot('plot-container', [trace1, trace2], layout);
        }
    </script>
</body>
</html>
