        <div id="canvas-container"></div>
    </div>

    <script>
        // State variables
        let dimension = '2D';
        let algorithm = 'prim';
        let animationState = 'stopped';
        let points = [];
        let edges = [];
        let mstEdges = [];
        let currentStep = 0;
        let frameCounter = 0;
        let canvas;

        // UI Elements
        const btn2d = document.getElementById('btn-2d');
        const btn3d = document.getElementById('btn-3d');
        const sliderSeed = document.getElementById('slider-seed');
        const seedValue = document.getElementById('seed-value');
        const sliderNumber = document.getElementById('slider-number');
        const numberValue = document.getElementById('number-value');
        const btnPrim = document.getElementById('btn-prim');
        const btnKruskal = document.getElementById('btn-kruskal');
        const btnPlay = document.getElementById('btn-play');
        const btnPause = document.getElementById('btn-pause');
        const btnReset = document.getElementById('btn-reset');

        // Event listeners
        btn2d.addEventListener('click', () => setDimension('2D'));
        btn3d.addEventListener('click', () => setDimension('3D'));
        btnPrim.addEventListener('click', () => setAlgorithm('prim'));
        btnKruskal.addEventListener('click', () => setAlgorithm('kruskal'));
        btnPlay.addEventListener('click', play);
        btnPause.addEventListener('click', pause);
        btnReset.addEventListener('click', reset);

        sliderSeed.addEventListener('input', (e) => {
            seedValue.textContent = e.target.value;
            fullReset();
        });

        sliderNumber.addEventListener('input', (e) => {
            numberValue.textContent = e.target.value;
            fullReset();
        });

        function setDimension(dim) {
            dimension = dim;
            btn2d.classList.toggle('active', dim === '2D');
            btn3d.classList.toggle('active', dim === '3D');
            fullReset();
        }

        function setAlgorithm(alg) {
            algorithm = alg;
            btnPrim.classList.toggle('active', alg === 'prim');
            btnKruskal.classList.toggle('active', alg === 'kruskal');
            partialReset();
        }

        function play() {
            animationState = 'playing';
            btnPlay.disabled = true;
            btnPause.disabled = false;
        }

        function pause() {
            animationState = 'paused';
            btnPlay.disabled = false;
            btnPause.disabled = true;
        }

        function fullReset() {
            animationState = 'stopped';
            currentStep = 0;
            frameCounter = 0;
            btnPlay.disabled = false;
            btnPause.disabled = true;
            generatePoints();
            calculateMST();
        }

        function partialReset() {
            animationState = 'stopped';
            currentStep = 0;
            frameCounter = 0;
            btnPlay.disabled = false;
            btnPause.disabled = true;
            calculateMST();
        }

        function reset() {
            partialReset();
        }

        function generatePoints() {
            randomSeed(parseInt(sliderSeed.value));
            points = [];
            const n = parseInt(sliderNumber.value);

            for (let i = 0; i < n; i++) {
                if (dimension === '2D') {
                    points.push(createVector(
                        random(50, width - 50),
                        random(50, height - 50)
                    ));
                } else {
                    points.push(createVector(
                        random(-200, 200),
                        random(-200, 200),
                        random(-200, 200)
                    ));
                }
            }
        }

        function calculateMST() {
            edges = [];
            mstEdges = [];

            // Create all edges with weights
            for (let i = 0; i < points.length; i++) {
                for (let j = i + 1; j < points.length; j++) {
                    edges.push({
                        from: i,
                        to: j,
                        weight: p5.Vector.dist(points[i], points[j])
                    });
                }
            }

            if (algorithm === 'prim') {
                calculatePrimMST();
            } else {
                calculateKruskalMST();
            }
        }

        function calculatePrimMST() {
            const n = points.length;
            const inMST = new Array(n).fill(false);
            const key = new Array(n).fill(Infinity);
            const parent = new Array(n).fill(-1);

            key[0] = 0;

            for (let count = 0; count < n - 1; count++) {
                let u = -1;
                for (let v = 0; v < n; v++) {
                    if (!inMST[v] && (u === -1 || key[v] < key[u])) {
                        u = v;
                    }
                }

                inMST[u] = true;

                if (parent[u] !== -1) {
                    mstEdges.push({
                        from: parent[u],
                        to: u,
                        weight: key[u]
                    });
                }

                for (let v = 0; v < n; v++) {
                    if (!inMST[v]) {
                        const weight = p5.Vector.dist(points[u], points[v]);
                        if (weight < key[v]) {
                            key[v] = weight;
                            parent[v] = u;
                        }
                    }
                }
            }
        }

        function calculateKruskalMST() {
            edges.sort((a, b) => a.weight - b.weight);

            const parent = Array.from({length: points.length}, (_, i) => i);

            function find(i) {
                if (parent[i] !== i) {
                    parent[i] = find(parent[i]);
                }
                return parent[i];
            }

            function union(x, y) {
                parent[find(x)] = find(y);
            }

            for (const edge of edges) {
                if (find(edge.from) !== find(edge.to)) {
                    mstEdges.push(edge);
                    union(edge.from, edge.to);

                    if (mstEdges.length === points.length - 1) {
                        break;
                    }
                }
            }
        }

        // p5.js sketch
        function setup() {
            const container = document.getElementById('canvas-container');
            canvas = createCanvas(container.offsetWidth, container.offsetHeight);
            canvas.parent('canvas-container');

            fullReset();
        }

        function draw() {
            background(224);

            if (dimension === '3D') {
                push();
                translate(width / 2, height / 2);
                rotateX(frameCount * 0.01);
                rotateY(frameCount * 0.01);

                // Draw wireframe cube
                stroke(200);
                strokeWeight(1);
                noFill();
                box(400);

                // Draw points
                noStroke();
                fill(0, 0, 255);
                for (const point of points) {
                    push();
                    translate(point.x, point.y, point.z);
                    sphere(5);
                    pop();
                }

                // Draw MST edges
                stroke(255, 0, 0);
                strokeWeight(2);
                for (let i = 0; i < currentStep && i < mstEdges.length; i++) {
                    const edge = mstEdges[i];
                    const p1 = points[edge.from];
                    const p2 = points[edge.to];
                    line(p1.x, p1.y, p1.z, p2.x, p2.y, p2.z);
                }

                pop();
            } else {
                // Draw points
                noStroke();
                fill(0, 0, 255);
                for (const point of points) {
                    circle(point.x, point.y, 10);
                }

                // Draw MST edges
                stroke(255, 0, 0);
                strokeWeight(2);
                for (let i = 0; i < currentStep && i < mstEdges.length; i++) {
                    const edge = mstEdges[i];
                    const p1 = points[edge.from];
                    const p2 = points[edge.to];
                    line(p1.x, p1.y, p2.x, p2.y);
                }
            }

            // Animation logic
            if (animationState === 'playing') {
                frameCounter++;
                if (frameCounter % 10 === 0) {
                    currentStep++;
                    if (currentStep >= mstEdges.length) {
                        animationState = 'stopped';
                        btnPlay.disabled = true;
                        btnPause.disabled = true;
                    }
                }
            }
        }

        function windowResized() {
            const container = document.getElementById('canvas-container');
            resizeCanvas(container.offsetWidth, container.offsetHeight);
            if (dimension === '2D') {
                fullReset();
            }
        }
    </script>
</body>
</html>
