<body>
    <div id="main-container">
        <div id="decorative-plus-btn">+</div>

        <div id="control-panel">
            <div>
                <label for="select-method">method</label>
                <select id="select-method">
                    <option value="estimation">estimation</option>
                    <option value="place values">place values</option>
                </select>
            </div>

            <hr>

            <div>
                <label>first number</label>
                <div class="slider-container">
                    <label for="slider-num1">numerical value</label>
                    <input type="range" id="slider-num1" min="0" max="20" step="0.1" value="1.1">
                    <span class="decorative-plus">+</span>
                </div>
            </div>

            <hr>

            <div>
                <label>multiply by</label>
                <div class="toggle-container">
                    <label>integers only</label>
                    <button id="btn-int-yes" class="active">yes</button>
                    <button id="btn-int-no" class="inactive">no</button>
                </div>
                <div class="slider-container">
                    <label for="slider-num2">numerical value</label>
                    <input type="range" id="slider-num2" min="0" max="20" step="1" value="2">
                    <span class="decorative-plus">+</span>
                </div>
            </div>
        </div>

        <div id="output-display">
            <p>start with: 1.1 × 2</p>
            <p>convert to: 11. × 2. = 22.</p>
            <p>estimate as: 1 × 2 = 2</p>
            <p>result is: 2.2</p>
        </div>
    </div>

    <script>
        // Helper functions
        function getIntegerRepresentation(num) {
            const str = num.toString();
            return str.replace('.', '');
        }

        function getPowerOfTen(num) {
            const str = num.toString();
            const decimalIndex = str.indexOf('.');
            if (decimalIndex === -1) return 1;
            const decimalPlaces = str.length - decimalIndex - 1;
            return Math.pow(10, decimalPlaces);
        }

        function isInteger(num) {
            return num % 1 === 0;
        }

        // Main update function
        function updateDisplay() {
            const method = document.getElementById('select-method').value;
            const num1 = parseFloat(document.getElementById('slider-num1').value);
            const num2 = parseFloat(document.getElementById('slider-num2').value);
            const outputDiv = document.getElementById('output-display');

            // Clear previous content
            outputDiv.innerHTML = '';

            const result = num1 * num2;

            if (method === 'estimation') {
                // Estimation method
                const num1Int = getIntegerRepresentation(num1);
                const num2Int = getIntegerRepresentation(num2);
                const productOfInts = parseInt(num1Int) * parseInt(num2Int);
                const roundedNum1 = Math.round(num1);
                const roundedNum2 = Math.round(num2);
                const productOfRounded = roundedNum1 * roundedNum2;

                outputDiv.innerHTML = `
                    <p>start with: ${num1} × ${num2}</p>
                    <p>convert to: ${num1Int}. × ${num2Int}. = ${productOfInts}.</p>
                    <p>estimate as: ${roundedNum1} × ${roundedNum2} = ${productOfRounded}</p>
                    <p>result is: ${result}</p>
                `;
            } else {
                // Place values method
                const num1Int = getIntegerRepresentation(num1);
                const num2Int = getIntegerRepresentation(num2);
                const num1Factor = getPowerOfTen(num1);
                const num2Factor = getPowerOfTen(num2);
                const productOfInts = parseInt(num1Int) * parseInt(num2Int);
                const productOfFactors = num1Factor * num2Factor;

                outputDiv.innerHTML = `<p>start with: ${num1} × ${num2}</p>`;

                if (isInteger(num1) && isInteger(num2)) {
                    // Both are integers
                    outputDiv.innerHTML += `<p>= ${result}</p>`;
                } else if (isInteger(num1)) {
                    // Only num1 is integer
                    outputDiv.innerHTML += `
                        <p>rewrite as: ${num1} × (${num2Int}. ÷ ${num2Factor})</p>
                        <p>= ${productOfInts}. ÷ ${num2Factor}</p>
                        <p>= ${result}</p>
                    `;
                } else if (isInteger(num2)) {
                    // Only num2 is integer
                    outputDiv.innerHTML += `
                        <p>rewrite as: (${num1Int}. ÷ ${num1Factor}) × ${num2}</p>
                        <p>= ${productOfInts}. ÷ ${num1Factor}</p>
                        <p>= ${result}</p>
                    `;
                } else {
                    // Both have decimals
                    outputDiv.innerHTML += `
                        <p>rewrite as: (${num1Int}. ÷ ${num1Factor}) × (${num2Int}. ÷ ${num2Factor})</p>
                        <p>= (${num1Int}. × ${num2Int}.) ÷ (${num1Factor} × ${num2Factor})</p>
                        <p>= ${productOfInts}. ÷ ${productOfFactors}</p>
                        <p>= ${result}</p>
                    `;
                }
            }
        }

        // Event listeners
        document.getElementById('select-method').addEventListener('change', updateDisplay);
        document.getElementById('slider-num1').addEventListener('input', updateDisplay);
        document.getElementById('slider-num2').addEventListener('input', updateDisplay);

        document.getElementById('btn-int-yes').addEventListener('click', function() {
            const slider2 = document.getElementById('slider-num2');
            slider2.step = '1';
            slider2.value = Math.round(slider2.value);

            this.className = 'active';
            document.getElementById('btn-int-no').className = 'inactive';

            updateDisplay();
        });

        document.getElementById('btn-int-no').addEventListener('click', function() {
            const slider2 = document.getElementById('slider-num2');
            slider2.step = '0.1';

            this.className = 'active';
            document.getElementById('btn-int-yes').className = 'inactive';

            updateDisplay();
        });

        // Initialize display
        updateDisplay();
    </script>
</body>
</html>
