            margin: 0;
            font-size: 16px;
            font-weight: bold;
        }
    </style>
</head>
<body>
    <div id="demo-container">
        <div id="control-panel">
            <select id="select-vibration-mode">
                <option value="symmetric-stretch">symmetric stretch</option>
                <option value="asymmetric-stretch">stretch (triply degenerate)</option>
                <option value="bending-e">bend (doubly degenerate)</option>
                <option value="bending-f2">bend (triply degenerate)</option>
                <option value="ir-spectrum">IR spectrum</option>
            </select>
            <span>run</span>
            <button id="btn-play">►</button>
            <button id="btn-pause">||</button>
            <button id="btn-reset">|◄</button>
        </div>

        <div id="visualization-area">
            <div id="three-js-canvas-container"></div>
            <div id="plotly-chart-container"></div>
        </div>

        <div id="description-area">
            <p id="label-left" style="color: blue;">stretching A₁ Raman active 2916 cm⁻¹</p>
            <p id="label-right" style="color: blue;">no change in the molecular dipole moment</p>
        </div>
    </div>

    <script>
        // Global variables
        let renderer, moleculeScene, vectorScene, moleculeCamera, vectorCamera;
        let moleculeControls, vectorControls;
        let carbon, hydrogens = [], bonds = [], dipoleArrows = [], netDipoleArrow;
        let animationId = null;
        let startTime = null;
        let isPaused = false;
        let pausedTime = 0;
        let plotlyRendered = false;

        // Mode configurations
        const modeConfigs = {
            'symmetric-stretch': {
                leftText: 'stretching A₁ Raman active 2916 cm⁻¹',
                rightText: 'no change in the molecular dipole moment',
                color: 'blue',
                frequency: 2916,
                irActive: false
            },
            'asymmetric-stretch': {
                leftText: 'stretching F₂ IR active 3019 cm⁻¹',
                rightText: 'change in the molecular dipole moment',
                color: 'red',
                frequency: 3019,
                irActive: true
            },
            'bending-e': {
                leftText: 'bending E Raman active 1534 cm⁻¹',
                rightText: 'no change in the molecular dipole moment',
                color: 'blue',
                frequency: 1534,
                irActive: false
            },
            'bending-f2': {
                leftText: 'bending F₂ IR active 1306 cm⁻¹',
                rightText: 'change in the molecular dipole moment',
                color: 'red',
                frequency: 1306,
                irActive: true
            }
        };

        // Initialize Three.js scenes
        function initThreeJS() {
            const container = document.getElementById('three-js-canvas-container');
            const width = container.clientWidth;
            const height = container.clientHeight;

            // Renderer
            renderer = new THREE.WebGLRenderer({ antialias: true });
            renderer.setSize(width, height);
            renderer.setPixelRatio(window.devicePixelRatio);
            renderer.setClearColor(0xffffff);
            container.appendChild(renderer.domElement);

            // Scenes
            moleculeScene = new THREE.Scene();
            vectorScene = new THREE.Scene();

            // Cameras
            moleculeCamera = new THREE.PerspectiveCamera(45, width/2/height, 0.1, 1000);
            moleculeCamera.position.set(5, 5, 5);
            moleculeCamera.lookAt(0, 0, 0);

            vectorCamera = new THREE.PerspectiveCamera(45, width/2/height, 0.1, 1000);
            vectorCamera.position.set(5, 5, 5);
            vectorCamera.lookAt(0, 0, 0);

            // Controls
            moleculeControls = new THREE.OrbitControls(moleculeCamera, renderer.domElement);
            vectorControls = new THREE.OrbitControls(vectorCamera, renderer.domElement);

            // Lights
            const ambientLight = new THREE.AmbientLight(0xffffff, 0.6);
            const directionalLight = new THREE.DirectionalLight(0xffffff, 0.4);
            directionalLight.position.set(5, 5, 5);

            moleculeScene.add(ambientLight.clone());
            moleculeScene.add(directionalLight.clone());
            vectorScene.add(ambientLight.clone());
            vectorScene.add(directionalLight.clone());

            // Create molecule
            createMolecule();
            createDipoleVectors();

            // Handle resize
            window.addEventListener('resize', onWindowResize);
        }

        function createMolecule() {
            // Carbon atom
            const carbonGeometry = new THREE.SphereGeometry(0.3, 32, 32);
            const carbonMaterial = new THREE.MeshLambertMaterial({ color: 0x000000 });
            carbon = new THREE.Mesh(carbonGeometry, carbonMaterial);
            moleculeScene.add(carbon);

            // Hydrogen atoms (tetrahedral positions)
            const hydrogenGeometry = new THREE.SphereGeometry(0.2, 32, 32);
            const hydrogenMaterial = new THREE.MeshLambertMaterial({ color: 0xe0e0ff });

            const positions = [
                new THREE.Vector3(1, 1, 1).normalize().multiplyScalar(1.5),
                new THREE.Vector3(1, -1, -1).normalize().multiplyScalar(1.5),
                new THREE.Vector3(-1, 1, -1).normalize().multiplyScalar(1.5),
                new THREE.Vector3(-1, -1, 1).normalize().multiplyScalar(1.5)
            ];

            positions.forEach((pos, i) => {
                const hydrogen = new THREE.Mesh(hydrogenGeometry, hydrogenMaterial);
                hydrogen.position.copy(pos);
                hydrogen.userData.initialPosition = pos.clone();
                hydrogen.userData.index = i;
                hydrogens.push(hydrogen);
                moleculeScene.add(hydrogen);

                // Create bond
                const bondGeometry = new THREE.CylinderGeometry(0.05, 0.05, 1);
                const bondMaterial = new THREE.MeshBasicMaterial({ color: 0x808080 });
                const bond = new THREE.Mesh(bondGeometry, bondMaterial);
                bond.userData.hydrogenIndex = i;
                bonds.push(bond);
                moleculeScene.add(bond);
            });

            updateBonds();
        }

        function createDipoleVectors() {
            // Create four dipole arrows
            for (let i = 0; i < 4; i++) {
                const arrow = new THREE.ArrowHelper(
                    new THREE.Vector3(0, 1, 0),
                    new THREE.Vector3(0, 0, 0),
                    1,
                    0x0000ff,
                    0.3,
                    0.3
                );
                dipoleArrows.push(arrow);
                vectorScene.add(arrow);
            }

            // Create net dipole arrow (red)
            netDipoleArrow = new THREE.ArrowHelper(
                new THREE.Vector3(0, 1, 0),
                new THREE.Vector3(0, 0, 0),
                1,
                0xff0000,
                0.3,
                0.3
            );
            netDipoleArrow.visible = false;
            vectorScene.add(netDipoleArrow);
        }

        function updateBonds() {
            bonds.forEach((bond, i) => {
                const hydrogen = hydrogens[i];
                const direction = hydrogen.position.clone().normalize();
                const distance = hydrogen.position.length();

                bond.position.copy(hydrogen.position).multiplyScalar(0.5);
                bond.scale.y = distance;
                bond.lookAt(carbon.position);
                bond.rotateX(Math.PI / 2);
            });
        }

        function updateDipoleVectors() {
            let netDipole = new THREE.Vector3(0, 0, 0);

            hydrogens.forEach((hydrogen, i) => {
                const direction = hydrogen.position.clone().normalize();
                const magnitude = hydrogen.position.length() - hydrogen.userData.initialPosition.length();
                const dipole = direction.clone().multiplyScalar(magnitude * 2);

                dipoleArrows[i].position.set(0, 0, 0);
                dipoleArrows[i].setDirection(direction);
                dipoleArrows[i].setLength(Math.abs(magnitude) * 2 + 0.5, 0.2, 0.2);
                dipoleArrows[i].setColor(magnitude > 0 ? 0x0000ff : 0xff0000);

                netDipole.add(dipole);
            });

            // Update net dipole arrow
            const netMagnitude = netDipole.length();
            if (netMagnitude > 0.01) {
                netDipoleArrow.visible = true;
                netDipoleArrow.position.set(0, 0, 0);
                netDipoleArrow.setDirection(netDipole.normalize());
                netDipoleArrow.setLength(netMagnitude * 2, 0.3, 0.3);
            } else {
                netDipoleArrow.visible = false;
            }
        }

        function animate(timestamp) {
            if (!startTime) startTime = timestamp;
            const elapsed = (timestamp - startTime - pausedTime) / 1000;

            const mode = document.getElementById('select-vibration-mode').value;
            const amplitude = 0.3;
            const omega = 4;
            const phase = omega * elapsed;

            hydrogens.forEach((hydrogen, i) => {
                const initialPos = hydrogen.userData.initialPosition;

                if (mode === 'symmetric-stretch') {
                    // All hydrogens move radially in phase
                    const displacement = initialPos.clone().normalize().multiplyScalar(amplitude * Math.sin(phase));
                    hydrogen.position.copy(initialPos).add(displacement);
                } else if (mode === 'asymmetric-stretch') {
                    // Asymmetric stretch pattern
                    let factor = 0;
                    if (i === 0) factor = 1;
                    else if (i === 1) factor = -1;
                    else if (i === 2) factor = 0.5;
                    else if (i === 3) factor = -0.5;

                    const displacement = initialPos.clone().normalize().multiplyScalar(amplitude * Math.sin(phase) * factor);
                    hydrogen.position.copy(initialPos).add(displacement);
                } else if (mode === 'bending-e') {
                    // Bending mode E - doubly degenerate
                    const tangent = new THREE.Vector3().crossVectors(initialPos, new THREE.Vector3(0, 1, 0)).normalize();
                    let factor = (i === 0 || i === 1) ? 1 : -1;
                    const displacement = tangent.multiplyScalar(amplitude * Math.sin(phase) * factor);
                    hydrogen.position.copy(initialPos).add(displacement);
                } else if (mode === 'bending-f2') {
                    // Bending mode F2 - triply degenerate
                    const axis = new THREE.Vector3(1, 1, 0).normalize();
                    const tangent = new THREE.Vector3().crossVectors(initialPos, axis).normalize();
                    let factor = (i % 2 === 0) ? 1 : -1;
                    const displacement = tangent.multiplyScalar(amplitude * Math.sin(phase) * factor);
                    hydrogen.position.copy(initialPos).add(displacement);
                }
            });

            updateBonds();
            updateDipoleVectors();
            render();

            animationId = requestAnimationFrame(animate);
        }

        function render() {
            const width = renderer.domElement.width;
            const height = renderer.domElement.height;

            // Left viewport - molecule
            renderer.setViewport(0, 0, width/2, height);
            renderer.setScissor(0, 0, width/2, height);
            renderer.setScissorTest(true);
            renderer.render(moleculeScene, moleculeCamera);

            // Right viewport - vectors
            renderer.setViewport(width/2, 0, width/2, height);
            renderer.setScissor(width/2, 0, width/2, height);
            renderer.render(vectorScene, vectorCamera);
        }

        function onWindowResize() {
            const container = document.getElementById('three-js-canvas-container');
            const width = container.clientWidth;
            const height = container.clientHeight;

            moleculeCamera.aspect = (width/2) / height;
            moleculeCamera.updateProjectionMatrix();
            vectorCamera.aspect = (width/2) / height;
            vectorCamera.updateProjectionMatrix();

            renderer.setSize(width, height);
        }

        function resetAnimation() {
            if (animationId) {
                cancelAnimationFrame(animationId);
                animationId = null;
            }
            startTime = null;
            pausedTime = 0;
            isPaused = false;

            // Reset positions
            hydrogens.forEach((hydrogen) => {
                hydrogen.position.copy(hydrogen.userData.initialPosition);
            });
            updateBonds();
            updateDipoleVectors();
            render();
        }

        function createIRSpectrum() {
            const x = [];
            const y = [];

            // Generate wavenumber range
            for (let i = 400; i <= 4000; i += 5) {
                x.push(i);

                // Baseline with noise
                let transmittance = 0.95 + (Math.random() - 0.5) * 0.02;

                // Peak at 1306 cm⁻¹ (F2 bend)
                const peak1 = 0.7 * Math.exp(-Math.pow(i - 1306, 2) / 10000);

                // Peak at 3019 cm⁻¹ (F2 stretch)
                const peak2 = 0.75 * Math.exp(-Math.pow(i - 3019, 2) / 15000);

                // Add fine structure
                for (let j = -50; j <= 50; j += 10) {
                    if (Math.abs(j) > 10) {
                        transmittance -= 0.05 * Math.exp(-Math.pow(i - 1306 - j, 2) / 100) * (Math.random() * 0.5 + 0.5);
                        transmittance -= 0.05 * Math.exp(-Math.pow(i - 3019 - j, 2) / 100) * (Math.random() * 0.5 + 0.5);
                    }
                }

                transmittance -= peak1 + peak2;
                y.push(Math.max(0.1, Math.min(1, transmittance)));
            }

            const data = [{
                x: x,
                y: y,
                type: 'scatter',
                mode: 'lines',
                line: { color: 'black', width: 2 }
            }];

            const layout = {
                title: 'Methane IR Spectrum',
                xaxis: {
                    title: 'wavenumber (cm⁻¹)',
                    range: [400, 4000]
                },
                yaxis: {
                    title: 'Transmittance',
                    range: [0, 1.1]
                },
                annotations: [
                    {
                        x: 1306,
                        y: 0.2,
                        xref: 'x',
                        yref: 'y',
                        text: 'ν₄ F₂ 1306 cm⁻¹',
                        showarrow: true,
                        arrowhead: 2,
                        ax: -50,
                        ay: -40,
                        font: { color: 'red', size: 14 }
                    },
                    {
                        x: 3019,
                        y: 0.2,
                        xref: 'x',
                        yref: 'y',
                        text: 'ν₃ F₂ 3019 cm⁻¹',
                        showarrow: true,
                        arrowhead: 2,
                        ax: 50,
                        ay: -40,
                        font: { color: 'red', size: 14 }
                    }
                ]
            };

            Plotly.newPlot('plotly-chart-container', data, layout);
            plotlyRendered = true;
        }

        // Event listeners
        document.getElementById('select-vibration-mode').addEventListener('change', function(e) {
            const mode = e.target.value;

            if (mode === 'ir-spectrum') {
                // Show spectrum view
                document.getElementById('three-js-canvas-container').style.display = 'none';
                document.getElementById('plotly-chart-container').style.display = 'block';
                document.getElementById('description-area').style.display = 'none';

                // Disable animation controls
                document.getElementById('btn-play').disabled = true;
                document.getElementById('btn-pause').disabled = true;
                document.getElementById('btn-reset').disabled = true;

                // Create spectrum if not already created
                if (!plotlyRendered) {
                    createIRSpectrum();
                }
            } else {
                // Show animation view
                document.getElementById('three-js-canvas-container').style.display = 'block';
                document.getElementById('plotly-chart-container').style.display = 'none';
                document.getElementById('description-area').style.display = 'flex';

                // Enable animation controls
                document.getElementById('btn-play').disabled = false;
                document.getElementById('btn-pause').disabled = false;
                document.getElementById('btn-reset').disabled = false;

                // Reset animation
                resetAnimation();

                // Update labels
                const config = modeConfigs[mode];
                const leftLabel = document.getElementById('label-left');
                const rightLabel = document.getElementById('label-right');

                leftLabel.textContent = config.leftText;
                leftLabel.style.color = config.color;
                rightLabel.textContent = config.rightText;
                rightLabel.style.color = config.color;
            }
        });

        document.getElementById('btn-play').addEventListener('click', function() {
            if (!animationId) {
                if (isPaused) {
                    isPaused = false;
                } else {
                    startTime = null;
                    pausedTime = 0;
                }
                animate(performance.now());
            }
        });

        document.getElementById('btn-pause').addEventListener('click', function() {
            if (animationId) {
                cancelAnimationFrame(animationId);
                animationId = null;
                isPaused = true;
                pausedTime = performance.now() - startTime;
            }
        });

        document.getElementById('btn-reset').addEventListener('click', function() {
            resetAnimation();
        });

        // Initialize
        initThreeJS();
        render();
    </script>
</body>
</html>
