            text-align: center;
        }

        #local-clock-wrapper, #solar-clock-wrapper {
            position: relative;
            display: inline-block;
        }

        #local-time-digital, #solar-time-digital {
            position: absolute;
            bottom: 10px;
            left: 50%;
            transform: translateX(-50%);
            background: white;
            border: 1px solid black;
            padding: 2px 5px;
            font-family: monospace;
            font-size: 14px;
        }

        #offset-display {
            text-align: center;
            margin-top: 20px;
            background: white;
            border: 1px solid black;
            padding: 2px 5px;
            display: inline-block;
        }

        h2 {
            margin: 0 0 10px 0;
            font-size: 18px;
        }
    </style>
</head>
<body>
    <div id="app-container">
        <div id="control-panel">
            <label for="date-slider">date</label>
            <input type="range" id="date-slider" min="0" max="364" value="340" step="1">
            <span id="date-display">Friday, 07 December</span>
            <button id="today-button">today</button>
        </div>

        <div id="visualization-panel">
            <div id="clocks-container">
                <div id="local-clock-group">
                    <h2>Local Time</h2>
                    <div id="local-clock-wrapper">
                        <canvas id="local-clock-canvas"></canvas>
                        <div id="local-time-digital">00:00:00</div>
                    </div>
                </div>

                <div id="solar-clock-group">
                    <h2>Solar Time</h2>
                    <div id="solar-clock-wrapper">
                        <canvas id="solar-clock-canvas"></canvas>
                        <div id="solar-time-digital">00:00:00</div>
                    </div>
                </div>
            </div>

            <div style="text-align: center;">
                <div id="offset-display">offset of solar time from local time: -46 minutes</div>
            </div>
        </div>
    </div>

    <script>
        let userLongitude = 0;
        let userLatitude = 51.5;
        let localSketch, solarSketch;
        let localTime, solarTime;

        // Get user location
        if (navigator.geolocation) {
            navigator.geolocation.getCurrentPosition(
                (position) => {
                    userLongitude = position.coords.longitude;
                    userLatitude = position.coords.latitude;
                },
                (error) => {
                    console.log("Geolocation error, using default coordinates");
                }
            );
        }

        // Date utilities
        function getDayOfYear(date) {
            const start = new Date(date.getFullYear(), 0, 0);
            const diff = date - start;
            const oneDay = 1000 * 60 * 60 * 24;
            return Math.floor(diff / oneDay) - 1;
        }

        function getDateFromDayOfYear(dayOfYear, year) {
            const date = new Date(year, 0, 1);
            date.setDate(date.getDate() + dayOfYear);
            return date;
        }

        function formatDate(date) {
            const days = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];
            const months = ['January', 'February', 'March', 'April', 'May', 'June', 'July', 'August', 'September', 'October', 'November', 'December'];
            return `${days[date.getDay()]}, ${date.getDate().toString().padStart(2, '0')} ${months[date.getMonth()]}`;
        }

        // Equation of Time calculation
        function calculateEoT(dayOfYear) {
            const B = (360 / 365.24) * (dayOfYear - 81) * Math.PI / 180;
            const EoT = 9.87 * Math.sin(2 * B) - 7.53 * Math.cos(B) - 1.5 * Math.sin(B);
            return EoT;
        }

        // Calculate solar time
        function calculateSolarTime(localDate, dayOfYear) {
            const EoT = calculateEoT(dayOfYear);
            const utcTime = localDate.getTime() + localDate.getTimezoneOffset() * 60000;
            const timeOffsetMinutes = EoT + 4 * userLongitude;
            const solarTimeMilliseconds = utcTime + timeOffsetMinutes * 60000;
            return new Date(solarTimeMilliseconds);
        }

        // Format time as HH:MM:SS
        function formatTime(date) {
            return date.toTimeString().split(' ')[0];
        }

        // p5.js sketch for clocks
        function createClockSketch(canvasId, faceColor) {
            return function(p) {
                let time;

                p.setup = function() {
                    const canvas = p.createCanvas(200, 200);
                    canvas.parent(canvasId);
                    p.angleMode(p.DEGREES);
                };

                p.draw = function() {
                    p.background(240);
                    p.translate(100, 100);

                    // Outer circle
                    p.strokeWeight(3);
                    p.stroke(0);
                    p.fill(faceColor);
                    p.circle(0, 0, 180);

                    // Hour markers
                    for (let i = 0; i < 12; i++) {
                        p.push();
                        p.rotate(i * 30);
                        p.strokeWeight(2);
                        p.line(0, -80, 0, -70);
                        p.pop();
                    }

                    // Minute markers
                    for (let i = 0; i < 60; i++) {
                        if (i % 5 !== 0) {
                            p.push();
                            p.rotate(i * 6);
                            p.strokeWeight(1);
                            p.line(0, -80, 0, -75);
                            p.pop();
                        }
                    }

                    // Numbers
                    p.textAlign(p.CENTER, p.CENTER);
                    p.textSize(16);
                    p.fill(0);
                    for (let i = 1; i <= 12; i++) {
                        p.push();
                        let angle = i * 30 - 90;
                        let x = 60 * p.cos(angle);
                        let y = 60 * p.sin(angle);
                        p.text(i, x, y);
                        p.pop();
                    }

                    if (time) {
                        // Hour hand
                        p.push();
                        p.rotate((time.getHours() % 12) * 30 + time.getMinutes() * 0.5 - 90);
                        p.strokeWeight(6);
                        p.stroke(0);
                        p.line(0, 0, 40, 0);
                        p.pop();

                        // Minute hand
                        p.push();
                        p.rotate(time.getMinutes() * 6 + time.getSeconds() * 0.1 - 90);
                        p.strokeWeight(4);
                        p.stroke(0);
                        p.line(0, 0, 60, 0);
                        p.pop();

                        // Second hand
                        p.push();
                        p.rotate(time.getSeconds() * 6 - 90);
                        p.strokeWeight(2);
                        p.stroke(255, 0, 0);
                        p.line(0, 0, 70, 0);
                        p.pop();
                    }

                    // Center pin
                    p.fill(0);
                    p.noStroke();
                    p.circle(0, 0, 10);
                };

                p.updateTime = function(newTime) {
                    time = newTime;
                };
            };
        }

        // Initialize clocks
        localSketch = new p5(createClockSketch('local-clock-wrapper', '#E6E6FA'));
        solarSketch = new p5(createClockSketch('solar-clock-wrapper', '#FAFAD2'));

        // Update function
        function update() {
            localTime = new Date();
            const dayOfYear = parseInt(document.getElementById('date-slider').value);
            solarTime = calculateSolarTime(localTime, dayOfYear);

            // Update clocks
            localSketch.updateTime(localTime);
            solarSketch.updateTime(solarTime);

            // Update digital displays
            document.getElementById('local-time-digital').textContent = formatTime(localTime);
            document.getElementById('solar-time-digital').textContent = formatTime(solarTime);

            // Update offset
            const offsetMinutes = Math.round((solarTime.getTime() - localTime.getTime()) / 60000);
            document.getElementById('offset-display').textContent = `offset of solar time from local time: ${offsetMinutes} minutes`;
        }

        // Event listeners
        document.getElementById('date-slider').addEventListener('input', function() {
            const dayOfYear = parseInt(this.value);
            const date = getDateFromDayOfYear(dayOfYear, new Date().getFullYear());
            document.getElementById('date-display').textContent = formatDate(date);
        });

        document.getElementById('today-button').addEventListener('click', function() {
            const today = new Date();
            const dayOfYear = getDayOfYear(today);
            document.getElementById('date-slider').value = dayOfYear;
            document.getElementById('date-display').textContent = formatDate(today);
        });

        // Initialize date display
        const initialDate = getDateFromDayOfYear(340, new Date().getFullYear());
        document.getElementById('date-display').textContent = formatDate(initialDate);

        // Start updates
        setInterval(update, 1000);
        update();
    </script>
</body>
</html>
