    <script>
        // State variables
        let magnetOrientations = [3, 2, 4, 5, 0]; // Default: [↓, ↑, ⊙, ⊗, →]
        let magnetStrength = 5;
        let plotType = 'stream';

        // Orientation mappings
        const orientationSymbols = ['→', '←', '↑', '↓', '⊙', '⊗'];
        const orientationVectors = [
            [1, 0, 0],   // →
            [-1, 0, 0],  // ←
