        // p5.js sketch
        new p5((p) => {
            const canvasWidth = 800;
            const canvasHeight = 600;
            const magnetSize = 40; // pixels
            const magnetSpacing = magnetSize * 1.2;

            p.setup = () => {
                p.createCanvas(canvasWidth, canvasHeight);
                p.parent('visualization-container');
                p.canvas.id = 'p5-canvas';
                redraw();
            };

            function redraw() {
                p.background(255);
                p.translate(canvasWidth / 2, canvasHeight / 2);

                if (plotType === 'stream') {
                    drawStreamPlot();
                } else {
                    drawDensityPlot();
                }

                drawMagnets();
            }

            function calculateField(x, y) {
                let Bx = 0, By = 0;
                const mu0 = 1; // Simplified constant

                for (let i = 0; i < 5; i++) {
                    const magnetX = (i - 2) * magnetSpacing;
                    const magnetY = 0;

                    const dx = x - magnetX;
                    const dy = y - magnetY;
                    const r2 = dx * dx + dy * dy;

                    if (r2 < magnetSize * magnetSize / 4) continue; // Skip inside magnets

                    const r = Math.sqrt(r2);
                    const r3 = r2 * r;
                    const r5 = r3 * r2;

                    const m = orientationVectors[magnetOrientations[i]];
                    const mx = m[0] * magnetStrength;
                    const my = m[1] * magnetStrength;

                    const mdotr = mx * dx + my * dy;

                    Bx += mu0 * (3 * mdotr * dx / r5 - mx / r3);
                    By += mu0 * (3 * mdotr * dy / r5 - my / r3);
                }

                return [Bx, By];
            }

            function drawStreamPlot() {
                p.stroke(0, 102, 204);
                p.strokeWeight(1);
                p.noFill();

                // Grid of starting points
                for (let x = -canvasWidth/2; x <= canvasWidth/2; x += 40) {
                    for (let y = -canvasHeight/2; y <= canvasHeight/2; y += 40) {
                        drawStreamLine(x, y);
                    }
                }
            }

            function drawStreamLine(startX, startY) {
                const maxSteps = 200;
                const stepSize = 2;
                const arrowSpacing = 30;
                let arrowCounter = 0;

                // Forward direction
                let x = startX, y = startY;
                p.beginShape();
                for (let i = 0; i < maxSteps; i++) {
                    const [Bx, By] = calculateField(x, y);
                    const B = Math.sqrt(Bx * Bx + By * By);

                    if (B < 0.01) break;

                    p.vertex(x, y);

                    arrowCounter += stepSize;
                    if (arrowCounter >= arrowSpacing) {
                        drawArrowhead(x, y, Bx/B, By/B);
                        arrowCounter = 0;
                    }

                    x += stepSize * Bx / B;
                    y += stepSize * By / B;

                    if (Math.abs(x) > canvasWidth/2 || Math.abs(y) > canvasHeight/2) break;
                }
                p.endShape();

                // Backward direction
                x = startX; y = startY;
                p.beginShape();
                for (let i = 0; i < maxSteps; i++) {
                    const [Bx, By] = calculateField(x, y);
                    const B = Math.sqrt(Bx * Bx + By * By);

                    if (B < 0.01) break;

                    p.vertex(x, y);

                    x -= stepSize * Bx / B;
                    y -= stepSize * By / B;

                    if (Math.abs(x) > canvasWidth/2 || Math.abs(y) > canvasHeight/2) break;
                }
                p.endShape();
            }

            function drawArrowhead(x, y, nx, ny) {
                const arrowSize = 6;
                p.push();
                p.translate(x, y);
                p.rotate(Math.atan2(ny, nx));
                p.line(0, 0, -arrowSize, -arrowSize/2);
                p.line(0, 0, -arrowSize, arrowSize/2);
                p.pop();
            }

            function drawDensityPlot() {
                const resolution = 5;
                let minB = Infinity, maxB = -Infinity;

                // First pass: find min/max
                for (let x = -canvasWidth/2; x <= canvasWidth/2; x += resolution) {
                    for (let y = -canvasHeight/2; y <= canvasHeight/2; y += resolution) {
                        const [Bx, By] = calculateField(x, y);
                        const B = Math.sqrt(Bx * Bx + By * By);
                        minB = Math.min(minB, B);
                        maxB = Math.max(maxB, B);
                    }
                }

                // Second pass: draw
                p.noStroke();
                for (let x = -canvasWidth/2; x <= canvasWidth/2; x += resolution) {
                    for (let y = -canvasHeight/2; y <= canvasHeight/2; y += resolution) {
                        const [Bx, By] = calculateField(x, y);
                        const B = Math.sqrt(Bx * Bx + By * By);
                        const normalized = (B - minB) / (maxB - minB);

                        // Custom colormap: light yellow to orange/red to dark blue
                        let c;
                        if (normalized < 0.5) {
                            c = p.lerpColor(p.color(255, 250, 205), p.color(255, 100, 0), normalized * 2);
                        } else {
                            c = p.lerpColor(p.color(255, 100, 0), p.color(0, 0, 139), (normalized - 0.5) * 2);
                        }

                        p.fill(c);
                        p.rect(x, y, resolution, resolution);
                    }
                }
            }

            function drawMagnets() {
                p.stroke(0);
                p.strokeWeight(2);
                p.fill(192);

                for (let i = 0; i < 5; i++) {
                    const x = (i - 2) * magnetSpacing;
                    const y = 0;

                    // Draw pseudo-3D cube
                    const s = magnetSize;
                    const offset = s * 0.2;

                    // Front face
                    p.fill(192);
                    p.rect(x - s/2, y - s/2, s, s);

                    // Top face
                    p.fill(220);
                    p.beginShape();
                    p.vertex(x - s/2, y - s/2);
                    p.vertex(x - s/2 + offset, y - s/2 - offset);
                    p.vertex(x + s/2 + offset, y - s/2 - offset);
                    p.vertex(x + s/2, y - s/2);
                    p.endShape(p.CLOSE);

                    // Right face
                    p.fill(160);
                    p.beginShape();
                    p.vertex(x + s/2, y - s/2);
                    p.vertex(x + s/2 + offset, y - s/2 - offset);
                    p.vertex(x + s/2 + offset, y + s/2 - offset);
                    p.vertex(x + s/2, y + s/2);
                    p.endShape(p.CLOSE);

                    // Draw orientation symbol
                    p.push();
                    p.translate(x, y);
                    p.fill(0, 102, 204);
                    p.noStroke();
                    p.textAlign(p.CENTER, p.CENTER);
                    p.textSize(24);

                    const orientation = magnetOrientations[i];
                    if (orientation < 4) {
                        // Draw arrow
                        p.stroke(0, 102, 204);
                        p.strokeWeight(3);
                        const arrowLength = s * 0.6;
                        const angles = [0, Math.PI, -Math.PI/2, Math.PI/2];
                        p.rotate(angles[orientation]);
                        p.line(-arrowLength/2, 0, arrowLength/2, 0);
                        p.line(arrowLength/2, 0, arrowLength/2 - 8, -8);
                        p.line(arrowLength/2, 0, arrowLength/2 - 8, 8);
                    } else {
                        // Draw circle with dot or cross
                        p.stroke(0, 102, 204);
                        p.strokeWeight(3);
                        p.noFill();
                        p.circle(0, 0, s * 0.6);

                        if (orientation === 4) {
                            // Dot
                            p.fill(0, 102, 204);
                            p.noStroke();
                            p.circle(0, 0, 8);
                        } else {
                            // Cross
                            const crossSize = s * 0.2;
                            p.line(-crossSize, -crossSize, crossSize, crossSize);
                            p.line(-crossSize, crossSize, crossSize, -crossSize);
                        }
                    }

                    p.pop();
                }
            }

            // Event handlers
            window.redraw = redraw;
        });

        // UI Event Handlers
        for (let i = 0; i < 5; i++) {
            document.getElementById(`magnet-orientation-up-${i}`).addEventListener('click', () => {
                magnetOrientations[i] = (magnetOrientations[i] + 1) % 6;
                document.getElementById(`magnet-orientation-display-${i}`).textContent = orientationSymbols[magnetOrientations[i]];
                window.redraw();
            });

            document.getElementById(`magnet-orientation-down-${i}`).addEventListener('click', () => {
                magnetOrientations[i] = (magnetOrientations[i] - 1 + 6) % 6;
                document.getElementById(`magnet-orientation-display-${i}`).textContent = orientationSymbols[magnetOrientations[i]];
                window.redraw();
            });
        }

        document.getElementById('slider-strength').addEventListener('input', (e) => {
            magnetStrength = parseInt(e.target.value);
            document.getElementById('slider-strength-value').textContent = magnetStrength;
            window.redraw();
        });

        document.getElementById('btn-stream-plot').addEventListener('click', () => {
            plotType = 'stream';
            document.getElementById('btn-stream-plot').classList.add('active');
            document.getElementById('btn-density-plot').classList.remove('active');
            window.redraw();
        });

        document.getElementById('btn-density-plot').addEventListener('click', () => {
            plotType = 'density';
            document.getElementById('btn-density-plot').classList.add('active');
            document.getElementById('btn-stream-plot').classList.remove('active');
            window.redraw();
        });
    </script>
</body>
</html>
