</head>
<body>
    <div id="container">
        <div id="controls">
            <button id="btn-diagram" class="active">diagram</button>
            <button id="btn-phases">phases</button>
            <label for="check-grid-lines">
                <input type="checkbox" id="check-grid-lines">
                grid lines
            </label>
            <label for="check-tie-lines">
                <input type="checkbox" id="check-tie-lines" checked>
                tie lines
            </label>
            <label for="check-alpha-phase">
                <input type="checkbox" id="check-alpha-phase" checked>
                alpha phase
            </label>
            <label for="check-beta-phase">
                <input type="checkbox" id="check-beta-phase" checked>
                beta phase
            </label>
        </div>
        <div id="visualization">
            <div id="canvas-container">
                <div id="info-box-main" class="info-box">
                    <h4>mass fractions</h4>
                    <div>\(x_A = 0.42\)</div>
                    <div>\(x_B = 0.47\)</div>
                    <div>\(x_C = 0.11\)</div>
                </div>
                <div id="info-box-alpha" class="info-box" style="display: none;">
                    <div class="phase-label alpha-label">alpha phase</div>
                    <h4>mass fractions</h4>
                    <div>\(x_A^\alpha = 0.00\)</div>
                    <div>\(x_B^\alpha = 0.00\)</div>
                    <div>\(x_C^\alpha = 0.00\)</div>
                </div>
                <div id="info-box-beta" class="info-box" style="display: none;">
                    <div class="phase-label beta-label">beta phase</div>
                    <h4>mass fractions</h4>
                    <div>\(x_A^\beta = 0.00\)</div>
                    <div>\(x_B^\beta = 0.00\)</div>
                    <div>\(x_C^\beta = 0.00\)</div>
                </div>
            </div>
        </div>
    </div>

    <script>
        // State variables
        let currentView = 'diagram';
        let showGridLines = false;
        let showTieLines = true;
        let showAlphaPhase = true;
        let showBetaPhase = true;
        let userPoint = { xA: 0.42, xB: 0.47, xC: 0.11 };

        // Canvas variables
        let canvas;
        let PA, PB, PC; // Triangle vertices

        // Phase data
        const alphaCurvePoints = [
            {xA: 0.10, xB: 0.85, xC: 0.05},
            {xA: 0.15, xB: 0.78, xC: 0.07},
            {xA: 0.20, xB: 0.70, xC: 0.10},
            {xA: 0.25, xB: 0.62, xC: 0.13},
            {xA: 0.30, xB: 0.53, xC: 0.17},
            {xA: 0.35, xB: 0.44, xC: 0.21},
            {xA: 0.40, xB: 0.35, xC: 0.25},
            {xA: 0.45, xB: 0.25, xC: 0.30},
            {xA: 0.48, xB: 0.17, xC: 0.35}
        ];

        const betaCurvePoints = [
            {xA: 0.48, xB: 0.17, xC: 0.35},
            {xA: 0.50, xB: 0.20, xC: 0.30},
            {xA: 0.55, xB: 0.25, xC: 0.20},
            {xA: 0.60, xB: 0.28, xC: 0.12},
            {xA: 0.65, xB: 0.30, xC: 0.05},
            {xA: 0.70, xB: 0.28, xC: 0.02},
            {xA: 0.75, xB: 0.24, xC: 0.01},
            {xA: 0.80, xB: 0.19, xC: 0.01},
            {xA: 0.85, xB: 0.14, xC: 0.01}
        ];

        const tieLines = [
            { alpha: {xA: 0.15, xB: 0.78, xC: 0.07}, beta: {xA: 0.75, xB: 0.24, xC: 0.01} },
            { alpha: {xA: 0.20, xB: 0.70, xC: 0.10}, beta: {xA: 0.70, xB: 0.28, xC: 0.02} },
            { alpha: {xA: 0.25, xB: 0.62, xC: 0.13}, beta: {xA: 0.65, xB: 0.30, xC: 0.05} },
            { alpha: {xA: 0.30, xB: 0.53, xC: 0.17}, beta: {xA: 0.60, xB: 0.28, xC: 0.12} },
            { alpha: {xA: 0.35, xB: 0.44, xC: 0.21}, beta: {xA: 0.55, xB: 0.25, xC: 0.20} },
            { alpha: {xA: 0.40, xB: 0.35, xC: 0.25}, beta: {xA: 0.50, xB: 0.20, xC: 0.30} }
        ];

        // Event listeners
        document.getElementById('btn-diagram').addEventListener('click', () => {
            currentView = 'diagram';
            document.getElementById('btn-diagram').classList.add('active');
            document.getElementById('btn-phases').classList.remove('active');
            updateControlsVisibility();
        });

        document.getElementById('btn-phases').addEventListener('click', () => {
            currentView = 'phases';
            document.getElementById('btn-phases').classList.add('active');
            document.getElementById('btn-diagram').classList.remove('active');
            updateControlsVisibility();
        });

        document.getElementById('check-grid-lines').addEventListener('change', (e) => {
            showGridLines = e.target.checked;
        });

        document.getElementById('check-tie-lines').addEventListener('change', (e) => {
            showTieLines = e.target.checked;
        });

        document.getElementById('check-alpha-phase').addEventListener('change', (e) => {
            showAlphaPhase = e.target.checked;
            updateInfoBoxes();
        });

        document.getElementById('check-beta-phase').addEventListener('change', (e) => {
            showBetaPhase = e.target.checked;
            updateInfoBoxes();
        });

        function updateControlsVisibility() {
            const checkboxes = document.querySelectorAll('#controls input[type="checkbox"]');
            const labels = document.querySelectorAll('#controls label');

            if (currentView === 'diagram') {
                checkboxes.forEach(cb => cb.style.display = 'inline');
                labels.forEach(label => label.style.display = 'flex');
                document.getElementById('info-box-main').style.display = 'block';
            } else {
                checkboxes.forEach(cb => cb.style.display = 'none');
                labels.forEach(label => label.style.display = 'none');
                document.getElementById('info-box-main').style.display = 'none';
                document.getElementById('info-box-alpha').style.display = 'none';
                document.getElementById('info-box-beta').style.display = 'none';
            }
        }

        function setup() {
            canvas = createCanvas(800, 700);
            canvas.parent('canvas-container');

            // Define triangle vertices
            const centerX = width / 2;
            const centerY = height / 2;
            const triangleSize = 300;

            PA = createVector(centerX, centerY - triangleSize);
            PB = createVector(centerX - triangleSize * Math.cos(PI/6), centerY + triangleSize * Math.sin(PI/6));
            PC = createVector(centerX + triangleSize * Math.cos(PI/6), centerY + triangleSize * Math.sin(PI/6));
        }

        function draw() {
            background(245);

            // Draw main triangle
            strokeWeight(2);
            stroke(0);
            noFill();
            triangle(PA.x, PA.y, PB.x, PB.y, PC.x, PC.y);

            // Draw vertex labels
            textAlign(CENTER, CENTER);
            textSize(20);
            fill(0, 136, 0);
            text('A', PA.x, PA.y - 25);
            fill(0, 0, 255);
            text('B', PB.x - 25, PB.y + 25);
            fill(255, 0, 0);
            text('C', PC.x + 25, PC.y + 25);

            // Draw axes ticks and numbers
            drawAxisTicks();

            if (currentView === 'diagram') {
                drawDiagramView();
            } else {
                drawPhasesView();
            }
        }

        function drawAxisTicks() {
            strokeWeight(1);
            textSize(12);

            for (let i = 1; i <= 9; i++) {
                const frac = i / 10;

                // A axis (BC edge)
                const a1 = p5.Vector.lerp(PB, PC, frac);
                stroke(0, 136, 0);
                line(a1.x, a1.y, a1.x, a1.y + 5);
                fill(0, 136, 0);
                text(i/10, a1.x, a1.y + 15);

                // B axis (AC edge)
                const b1 = p5.Vector.lerp(PC, PA, frac);
                stroke(0, 0, 255);
                line(b1.x, b1.y, b1.x - 5, b1.y);
                fill(0, 0, 255);
                text(i/10, b1.x - 15, b1.y);

                // C axis (AB edge)
                const c1 = p5.Vector.lerp(PA, PB, frac);
                stroke(255, 0, 0);
                line(c1.x, c1.y, c1.x + 5, c1.y);
                fill(255, 0, 0);
                text(i/10, c1.x + 15, c1.y);
            }
        }

        function drawDiagramView() {
            // Draw grid lines if enabled
            if (showGridLines) {
                drawGridLines();
            }

            // Draw phase envelope
            drawPhaseEnvelope();

            // Draw tie lines if enabled
            if (showTieLines) {
                drawTieLines();
            }

            // Draw user point
            const userPos = barycentricToCartesian(userPoint.xA, userPoint.xB, userPoint.xC);
            fill(0);
            noStroke();
            circle(userPos.x, userPos.y, 10);

            // Draw projection lines
            drawProjectionLines(userPos, userPoint, false);

            // Check if in two-phase region and draw phase points
            const inTwoPhase = isInPhaseEnvelope(userPoint);
            if (inTwoPhase) {
                const phases = getPhaseCompositions(userPoint);

                if (showAlphaPhase && phases.alpha) {
                    const alphaPos = barycentricToCartesian(phases.alpha.xA, phases.alpha.xB, phases.alpha.xC);
                    fill(255, 165, 0);
                    noStroke();
                    circle(alphaPos.x, alphaPos.y, 10);
                    drawProjectionLines(alphaPos, phases.alpha, true);
                }

                if (showBetaPhase && phases.beta) {
                    const betaPos = barycentricToCartesian(phases.beta.xA, phases.beta.xB, phases.beta.xC);
                    fill(255, 0, 255);
                    noStroke();
                    circle(betaPos.x, betaPos.y, 10);
                    drawProjectionLines(betaPos, phases.beta, true);
                }

                // Draw connecting line
                if (phases.alpha && phases.beta) {
                    const alphaPos = barycentricToCartesian(phases.alpha.xA, phases.alpha.xB, phases.alpha.xC);
                    const betaPos = barycentricToCartesian(phases.beta.xA, phases.beta.xB, phases.beta.xC);
                    stroke(0);
                    strokeWeight(1);
                    drawingContext.setLineDash([5, 5]);
                    line(alphaPos.x, alphaPos.y, betaPos.x, betaPos.y);
                    drawingContext.setLineDash([]);
                }
            }
        }

        function drawPhasesView() {
            // Fill two-phase region
            fill(212, 234, 252);
            noStroke();
            beginShape();
            alphaCurvePoints.forEach(p => {
                const pos = barycentricToCartesian(p.xA, p.xB, p.xC);
                vertex(pos.x, pos.y);
            });
            betaCurvePoints.slice().reverse().forEach(p => {
                const pos = barycentricToCartesian(p.xA, p.xB, p.xC);
                vertex(pos.x, pos.y);
            });
            endShape(CLOSE);

            // Fill one-phase region
            fill(251, 229, 230);
            beginShape();
            vertex(PA.x, PA.y);
            vertex(PB.x, PB.y);
            vertex(PC.x, PC.y);
            beginContour();
            alphaCurvePoints.forEach(p => {
                const pos = barycentricToCartesian(p.xA, p.xB, p.xC);
                vertex(pos.x, pos.y);
            });
            betaCurvePoints.slice().reverse().forEach(p => {
                const pos = barycentricToCartesian(p.xA, p.xB, p.xC);
                vertex(pos.x, pos.y);
            });
            endContour();
            endShape(CLOSE);

            // Draw phase envelope with labels
            drawPhaseEnvelope();

            // Draw labels
            textAlign(CENTER, CENTER);
            textSize(16);
            fill(0);
            text('one phase', width/2, height/2 - 150);
            text('two phases', width/2, height/2 + 50);

            // Draw plait point
            const plaitPoint = alphaCurvePoints[alphaCurvePoints.length - 1];
            const plaitPos = barycentricToCartesian(plaitPoint.xA, plaitPoint.xB, plaitPoint.xC);
            fill(0);
            noStroke();
            circle(plaitPos.x, plaitPos.y, 8);
            textAlign(LEFT, CENTER);
            text('plait point', plaitPos.x + 20, plaitPos.y - 20);

            // Draw curved arrow
            noFill();
            stroke(0);
            strokeWeight(2);
            drawingContext.beginPath();
            drawingContext.moveTo(plaitPos.x + 15, plaitPos.y - 15);
            drawingContext.quadraticCurveTo(plaitPos.x + 10, plaitPos.y - 5, plaitPos.x + 5, plaitPos.y);
            drawingContext.stroke();

            // Draw user point
            const userPos = barycentricToCartesian(userPoint.xA, userPoint.xB, userPoint.xC);
            fill(0);
            noStroke();
            circle(userPos.x, userPos.y, 10);
        }

        function drawGridLines() {
            stroke(200);
            strokeWeight(0.5);

            for (let i = 1; i <= 9; i++) {
                const frac = i / 10;

                // Lines parallel to each side
                const a1 = p5.Vector.lerp(PA, PB, frac);
                const a2 = p5.Vector.lerp(PA, PC, frac);
                line(a1.x, a1.y, a2.x, a2.y);

                const b1 = p5.Vector.lerp(PB, PA, frac);
                const b2 = p5.Vector.lerp(PB, PC, frac);
                line(b1.x, b1.y, b2.x, b2.y);

                const c1 = p5.Vector.lerp(PC, PA, frac);
                const c2 = p5.Vector.lerp(PC, PB, frac);
                line(c1.x, c1.y, c2.x, c2.y);
            }
        }

        function drawPhaseEnvelope() {
            strokeWeight(2);

            // Draw alpha curve
            stroke(255, 165, 0);
            noFill();
            beginShape();
            alphaCurvePoints.forEach(p => {
                const pos = barycentricToCartesian(p.xA, p.xB, p.xC);
                vertex(pos.x, pos.y);
            });
            endShape();

            // Draw beta curve
            stroke(255, 0, 255);
            beginShape();
            betaCurvePoints.forEach(p => {
                const pos = barycentricToCartesian(p.xA, p.xB, p.xC);
                vertex(pos.x, pos.y);
            });
            endShape();

            // Add labels in phases view
            if (currentView === 'phases') {
                textAlign(CENTER, CENTER);
                textSize(14);
                fill(255, 165, 0);
                const alphaLabelPos = barycentricToCartesian(0.25, 0.62, 0.13);
                text('alpha phase', alphaLabelPos.x - 50, alphaLabelPos.y);

                fill(255, 0, 255);
                const betaLabelPos = barycentricToCartesian(0.65, 0.30, 0.05);
                text('beta phase', betaLabelPos.x + 50, betaLabelPos.y);
            }
        }

        function drawTieLines() {
            stroke(150);
            strokeWeight(1);

            tieLines.forEach(tieLine => {
                const alphaPos = barycentricToCartesian(tieLine.alpha.xA, tieLine.alpha.xB, tieLine.alpha.xC);
                const betaPos = barycentricToCartesian(tieLine.beta.xA, tieLine.beta.xB, tieLine.beta.xC);
                line(alphaPos.x, alphaPos.y, betaPos.x, betaPos.y);
            });
        }

        function drawProjectionLines(pos, comp, dashed) {
            strokeWeight(1);

            if (dashed) {
                drawingContext.setLineDash([5, 5]);
            }

            // Project to A axis (BC edge)
            const projA = p5.Vector.lerp(PB, PC, comp.xA);
            stroke(0, 136, 0);
            line(pos.x, pos.y, projA.x, projA.y);
            drawArrowhead(pos.x, pos.y, projA.x, projA.y);

            // Project to B axis (AC edge)
            const projB = p5.Vector.lerp(PC, PA, comp.xB);
            stroke(0, 0, 255);
            line(pos.x, pos.y, projB.x, projB.y);
            drawArrowhead(pos.x, pos.y, projB.x, projB.y);

            // Project to C axis (AB edge)
            const projC = p5.Vector.lerp(PA, PB, comp.xC);
            stroke(255, 0, 0);
            line(pos.x, pos.y, projC.x, projC.y);
            drawArrowhead(pos.x, pos.y, projC.x, projC.y);

            if (dashed) {
                drawingContext.setLineDash([]);
            }
        }

        function drawArrowhead(x1, y1, x2, y2) {
            const angle = atan2(y2 - y1, x2 - x1);
            const arrowSize = 8;

            push();
            translate(x2, y2);
            rotate(angle);
            line(0, 0, -arrowSize, -arrowSize/2);
            line(0, 0, -arrowSize, arrowSize/2);
            pop();
        }

        function barycentricToCartesian(xA, xB, xC) {
            return createVector(
                xA * PA.x + xB * PB.x + xC * PC.x,
                xA * PA.y + xB * PB.y + xC * PC.y
            );
        }

        function cartesianToBarycentric(x, y) {
            // Solve the linear system
            const denom = (PB.y - PC.y) * (PA.x - PC.x) + (PC.x - PB.x) * (PA.y - PC.y);
            const xA = ((PB.y - PC.y) * (x - PC.x) + (PC.x - PB.x) * (y - PC.y)) / denom;
            const xB = ((PC.y - PA.y) * (x - PC.x) + (PA.x - PC.x) * (y - PC.y)) / denom;
            const xC = 1 - xA - xB;

            return { xA, xB, xC };
        }

        function isInTriangle(x, y) {
            const comp = cartesianToBarycentric(x, y);
            return comp.xA >= 0 && comp.xB >= 0 && comp.xC >= 0 && 
                   comp.xA <= 1 && comp.xB <= 1 && comp.xC <= 1;
        }

        function isInPhaseEnvelope(point) {
            // Simplified check - in real implementation would use proper point-in-polygon
            return point.xA > 0.1 && point.xA < 0.48 && point.xB > 0.17 && point.xB < 0.85;
        }

        function getPhaseCompositions(point) {
            if (!isInPhaseEnvelope(point)) return null;

            // Find closest tie line and interpolate
            let minDist = Infinity;
            let closestIndex = 0;

            for (let i = 0; i < tieLines.length; i++) {
                const tieLine = tieLines[i];
                const midPoint = {
                    xA: (tieLine.alpha.xA + tieLine.beta.xA) / 2,
                    xB: (tieLine.alpha.xB + tieLine.beta.xB) / 2,
                    xC: (tieLine.alpha.xC + tieLine.beta.xC) / 2
                };

                const dist = Math.sqrt(
                    Math.pow(point.xA - midPoint.xA, 2) +
                    Math.pow(point.xB - midPoint.xB, 2) +
                    Math.pow(point.xC - midPoint.xC, 2)
                );

                if (dist < minDist) {
                    minDist = dist;
                    closestIndex = i;
                }
            }

            return {
                alpha: tieLines[closestIndex].alpha,
                beta: tieLines[closestIndex].beta
            };
        }

        function mousePressed() {
            if (mouseX >= 0 && mouseX <= width && mouseY >= 0 && mouseY <= height) {
                if (isInTriangle(mouseX, mouseY)) {
                    userPoint = cartesianToBarycentric(mouseX, mouseY);
                    updateInfoBoxes();
                }
            }
        }

        function updateInfoBoxes() {
            // Update main info box
            const mainBox = document.getElementById('info-box-main');
            mainBox.innerHTML = `
                <h4>mass fractions</h4>
                <div>\\(x_A = ${userPoint.xA.toFixed(2)}\\)</div>
                <div>\\(x_B = ${userPoint.xB.toFixed(2)}\\)</div>
                <div>\\(x_C = ${userPoint.xC.toFixed(2)}\\)</div>
            `;

            const inTwoPhase = isInPhaseEnvelope(userPoint);

            if (inTwoPhase) {
                const phases = getPhaseCompositions(userPoint);

                // Update alpha info box
                if (phases.alpha) {
                    const alphaBox = document.getElementById('info-box-alpha');
                    alphaBox.innerHTML = `
                        <div class="phase-label alpha-label">alpha phase</div>
                        <h4>mass fractions</h4>
                        <div>\\(x_A^\\alpha = ${phases.alpha.xA.toFixed(2)}\\)</div>
                        <div>\\(x_B^\\alpha = ${phases.alpha.xB.toFixed(2)}\\)</div>
                        <div>\\(x_C^\\alpha = ${phases.alpha.xC.toFixed(2)}\\)</div>
                    `;
                    alphaBox.style.display = showAlphaPhase && currentView === 'diagram' ? 'block' : 'none';
                }

                // Update beta info box
                if (phases.beta) {
                    const betaBox = document.getElementById('info-box-beta');
                    betaBox.innerHTML = `
                        <div class="phase-label beta-label">beta phase</div>
                        <h4>mass fractions</h4>
                        <div>\\(x_A^\\beta = ${phases.beta.xA.toFixed(2)}\\)</div>
                        <div>\\(x_B^\\beta = ${phases.beta.xB.toFixed(2)}\\)</div>
                        <div>\\(x_C^\\beta = ${phases.beta.xC.toFixed(2)}\\)</div>
                    `;
                    betaBox.style.display = showBetaPhase && currentView === 'diagram' ? 'block' : 'none';
                }
            } else {
                document.getElementById('info-box-alpha').style.display = 'none';
                document.getElementById('info-box-beta').style.display = 'none';
            }

            // Re-render math
            MathJax.typeset();
        }
    </script>
</body>
