    <script>
        // Seeded random number generator
        class SeededRandom {
            constructor(seed) {
                this.seed = seed;
            }

            next() {
                this.seed = (this.seed * 9301 + 49297) % 233280;
                return this.seed / 233280;
            }
        }

        // Global variables
        let points = [];
        let results = {};
        let leftSketch, rightSketch;

        // Calculate distance between two points
        function distance(p1, p2) {
            return Math.sqrt(Math.pow(p1.x - p2.x, 2) + Math.pow(p1.y - p2.y, 2));
        }

        // Calculate total tour length
        function tourLength(tour) {
            let length = 0;
            for (let i = 0; i < tour.length; i++) {
                length += distance(tour[i], tour[(i + 1) % tour.length]);
            }
            return length;
        }

        // Nearest neighbor heuristic
        function nearestNeighbor(points) {
            if (points.length === 0) return [];
            let tour = [points[0]];
            let remaining = points.slice(1);

            while (remaining.length > 0) {
                let last = tour[tour.length - 1];
                let nearestIdx = 0;
                let nearestDist = distance(last, remaining[0]);

                for (let i = 1; i < remaining.length; i++) {
                    let dist = distance(last, remaining[i]);
                    if (dist < nearestDist) {
                        nearestDist = dist;
                        nearestIdx = i;
                    }
                }

                tour.push(remaining[nearestIdx]);
                remaining.splice(nearestIdx, 1);
            }

            return tour;
        }

        // 2-Opt algorithm
        function solveTwoOpt(points) {
            let tour = nearestNeighbor(points.slice());
            let improved = true;

            while (improved) {
                improved = false;
                for (let i = 0; i < tour.length - 1; i++) {
                    for (let j = i + 2; j < tour.length; j++) {
                        let newTour = tour.slice(0, i + 1)
                            .concat(tour.slice(i + 1, j + 1).reverse())
                            .concat(tour.slice(j + 1));

                        if (tourLength(newTour) < tourLength(tour)) {
                            tour = newTour;
                            improved = true;
                            break;
                        }
                    }
                    if (improved) break;
                }
            }

            return tour;
        }

        // 3-Opt algorithm (simplified)
        function solve3Opt(points) {
            let tour = nearestNeighbor(points.slice());
            let improved = true;

            while (improved) {
                improved = false;
                for (let i = 0; i < tour.length - 2; i++) {
                    for (let j = i + 1; j < tour.length - 1; j++) {
                        for (let k = j + 1; k < tour.length; k++) {
                            // Try different reconnection patterns
                            let segment1 = tour.slice(0, i + 1);
                            let segment2 = tour.slice(i + 1, j + 1);
                            let segment3 = tour.slice(j + 1, k + 1);
                            let segment4 = tour.slice(k + 1);

                            // Try reversing segment2
                            let newTour = segment1.concat(segment2.slice().reverse())
                                .concat(segment3).concat(segment4);

                            if (tourLength(newTour) < tourLength(tour)) {
                                tour = newTour;
                                improved = true;
                                break;
                            }
                        }
                        if (improved) break;
                    }
                    if (improved) break;
                }
            }

            return tour;
        }

        // Or-Opt algorithm
        function solveOrOpt(points) {
            let tour = nearestNeighbor(points.slice());
            let improved = true;

            while (improved) {
                improved = false;
                for (let segmentSize = 1; segmentSize <= 3; segmentSize++) {
                    for (let i = 0; i < tour.length; i++) {
                        let segment = [];
                        for (let s = 0; s < segmentSize; s++) {
                            segment.push(tour[(i + s) % tour.length]);
                        }

                        for (let j = 0; j < tour.length; j++) {
                            if (Math.abs(i - j) <= segmentSize) continue;

                            let newTour = [];
                            for (let k = 0; k < tour.length; k++) {
                                if (k === j) {
                                    newTour = newTour.concat(segment);
                                }
                                let inSegment = false;
                                for (let s = 0; s < segmentSize; s++) {
                                    if (k === (i + s) % tour.length) {
                                        inSegment = true;
                                        break;
                                    }
                                }
                                if (!inSegment) {
                                    newTour.push(tour[k]);
                                }
                            }

                            if (tourLength(newTour) < tourLength(tour)) {
                                tour = newTour;
                                improved = true;
                                break;
                            }
                        }
                        if (improved) break;
                    }
                    if (improved) break;
                }
            }

            return tour;
        }

        // CCA (simplified Christofides algorithm)
        function solveCCA(points) {
            // For simplicity, using 2-Opt with some variations
            return solveTwoOpt(points);
        }

        // OrZweig (2.5-Opt variant)
        function solveOrZweig(points) {
            // Implementing as a variant of 2-Opt
            let tour = nearestNeighbor(points.slice());
            let improved = true;

            while (improved) {
                improved = false;
                for (let i = 0; i < tour.length - 1; i++) {
                    for (let j = i + 2; j < tour.length; j++) {
                        let newTour = tour.slice(0, i + 1)
                            .concat(tour.slice(i + 1, j + 1).reverse())
                            .concat(tour.slice(j + 1));

                        if (tourLength(newTour) < tourLength(tour) - 0.00001) {
                            tour = newTour;
                            improved = true;
                            break;
                        }
                    }
                    if (improved) break;
                }
            }

            return tour;
        }

        // Generate random points
        function generatePoints(n, seed) {
            let rng = new SeededRandom(seed);
            let pts = [];
            for (let i = 0; i < n; i++) {
                pts.push({
                    x: rng.next(),
                    y: rng.next()
                });
            }
            return pts;
        }

        // Run all algorithms
        function runAlgorithms() {
            let seed = parseInt(document.getElementById('seed-input').value);
            let n = parseInt(document.getElementById('slider-points').value);

            points = generatePoints(n, seed);
            results = {};

            // Run each algorithm
            const algorithms = [
                { name: 'orzweig', func: solveOrZweig },
                { name: 'oropt', func: solveOrOpt },
                { name: 'twoopt', func: solveTwoOpt },
                { name: 'cca', func: solveCCA },
                { name: '3opt', func: solve3Opt }
            ];

            algorithms.forEach(algo => {
                let start = performance.now();
                let tour = algo.func(points);
                let end = performance.now();

                results[algo.name] = {
                    tour: tour,
                    length: tourLength(tour),
                    time: (end - start) / 1000
                };

                // Update table
                document.getElementById(`timing-${algo.name}`).textContent = 
                    results[algo.name].time.toFixed(6);
                document.getElementById(`result-${algo.name}`).textContent = 
                    results[algo.name].length.toFixed(5);
            });

            // Update winner
            let mathResults = ['orzweig', 'oropt', 'twoopt', 'cca'].map(m => results[m].length);
            let minMath = Math.min(...mathResults);
            let result3opt = results['3opt'].length;

            let winnerText = 'Tie';
            if (result3opt < minMath - 0.00001) {
                winnerText = '3-Opt wins';
            } else if (minMath < result3opt - 0.00001) {
                winnerText = 'Mathematica wins';
            }

            document.getElementById('winner-display').textContent = winnerText;

            // Update plots
            updatePlots();
        }

        // Create p5 sketch for TSP visualization
        function createTSPSketch(parentId, getDataFunc, lineColor) {
            return new p5(function(p) {
                p.setup = function() {
                    p.createCanvas(400, 400).parent(parentId);
                };

                p.draw = function() {
                    p.background(255);

                    let data = getDataFunc();
                    if (!data || !data.points || data.points.length === 0) return;

                    let margin = 40;
                    let plotSize = 400 - 2 * margin;

                    // Draw axes
                    p.stroke(0);
                    p.strokeWeight(1);
                    p.line(margin, margin + plotSize, margin + plotSize, margin + plotSize);
                    p.line(margin, margin, margin, margin + plotSize);

                    // Draw tick marks and labels
                    p.textAlign(p.CENTER, p.TOP);
                    for (let i = 0; i <= 5; i++) {
                        let x = margin + (i / 5) * plotSize;
                        let y = margin + plotSize;
                        p.line(x, y, x, y + 5);
                        p.text((i / 5).toFixed(1), x, y + 8);
                    }

                    p.textAlign(p.RIGHT, p.CENTER);
                    for (let i = 0; i <= 5; i++) {
                        let x = margin;
                        let y = margin + plotSize - (i / 5) * plotSize;
                        p.line(x - 5, y, x, y);
                        p.text((i / 5).toFixed(1), x - 8, y);
                    }

                    // Draw tour
                    if (data.tour && data.tour.length > 0) {
                        p.stroke(lineColor);
                        p.strokeWeight(2);
                        p.noFill();
                        p.beginShape();
                        for (let point of data.tour) {
                            let x = margin + point.x * plotSize;
                            let y = margin + plotSize - point.y * plotSize;
                            p.vertex(x, y);
                        }
                        p.endShape(p.CLOSE);
                    }

                    // Draw points
                    p.fill('red');
                    p.noStroke();
                    for (let point of data.points) {
                        let x = margin + point.x * plotSize;
                        let y = margin + plotSize - point.y * plotSize;
                        p.circle(x, y, 8);
                    }
                };
            });
        }

        // Update plots
        function updatePlots() {
            if (leftSketch) leftSketch.redraw();
            if (rightSketch) rightSketch.redraw();
        }

        // Initialize
        window.onload = function() {
            // Create sketches
            leftSketch = createTSPSketch('canvas-left-wrapper', function() {
                let method = document.querySelector('input[name="math-method"]:checked').value.toLowerCase();
                return {
                    points: points,
                    tour: results[method] ? results[method].tour : []
                };
            }, '#0072B2');

            rightSketch = createTSPSketch('canvas-right-wrapper', function() {
                return {
                    points: points,
                    tour: results['3opt'] ? results['3opt'].tour : []
                };
            }, '#E69F00');

            // Event listeners
            document.getElementById('btn-random-set').addEventListener('click', function() {
                let newSeed = Math.floor(Math.random() * 99999) + 1;
                document.getElementById('seed-input').value = newSeed;
                runAlgorithms();
            });

            document.getElementById('btn-reset-seed').addEventListener('click', function() {
                document.getElementById('seed-input').value = '0';
                runAlgorithms();
            });

            document.getElementById('slider-points').addEventListener('change', runAlgorithms);

            document.querySelectorAll('input[name="math-method"]').forEach(radio => {
                radio.addEventListener('change', function() {
                    document.getElementById('left-plot-title').textContent = this.value;
                    updatePlots();
                });
            });

            // Initial run
            runAlgorithms();
        };
    </script>
</body>
</html>
