                <input type="range" id="slider-initialization" min="0" max="65535" value="462" step="1">
                <span id="value-initialization">+ 462</span>
            </div>

            <!-- Max Steps Slider -->
            <div>
                <label for="slider-max-steps">max steps</label>
                <input type="range" id="slider-max-steps" min="1" max="50" value="9" step="1">
                <span id="value-max-steps">+ 9</span>
            </div>

            <!-- Checkboxes -->
            <div>
                <input type="checkbox" id="checkbox-cycle4">
                <label for="checkbox-cycle4">length 4 cycle</label>
            </div>
            <div>
                <input type="checkbox" id="checkbox-cycle2">
                <label for="checkbox-cycle2">length 2 cycles</label>
            </div>
            <div>
                <input type="checkbox" id="checkbox-cycle1">
                <label for="checkbox-cycle1">length 1 cycle points</label>
            </div>
            <div>
                <input type="checkbox" id="checkbox-all-points">
                <label for="checkbox-all-points">all map points</label>
            </div>
            <div>
                <input type="checkbox" id="checkbox-modify">
                <label for="checkbox-modify">modify initialization</label>
            </div>
        </div>

        <!-- Right Column: Visualization Area -->
        <div id="visualization-area">
            <p id="info-text">result of initial condition 462 does not repeat in 10 steps</p>
            <div id="canvas-container"></div>
        </div>
    </div>

    <script>
        // Global variables
        let initValue = 462;
        let maxSteps = 9;
        let path = [];
        let cycleLength = 0;
        let cycleStep = 0;

        // Checkbox states
        let showCycle4 = false;
        let showCycle2 = false;
        let showCycle1 = false;
        let showAllPoints = false;
        let modifyInit = false;

        // Pre-calculated data
        const cycle2Pairs = [[10922, 21845], [13107, 26214], [13653, 27306], [15339, 30678], 
                            [21844, 43691], [26213, 52427], [27305, 54611], [30677, 61355], [43690, 54613]];
        const cycle4Points = [43690, 54612, 32768, 49152];
        let fixedPoints = [];

        // Bit reverse function for 15 bits
        function bitReverse15(n) {
            let result = 0;
            for (let i = 0; i < 15; i++) {
                result = (result << 1) | (n & 1);
                n >>= 1;
            }
            return result;
        }

        // Automaton function
        function f(n) {
            return (n + bitReverse15(Math.floor(n / 2))) % 65536;
        }

        // Convert integer to 2D coordinates
        function intToCoord(n) {
            return {
                x: n & 255,
                y: (n >> 8) & 255
            };
        }

        // Calculate fixed points
        function calculateFixedPoints() {
            fixedPoints = [];
            for (let n = 0; n < 65536; n++) {
                if (bitReverse15(Math.floor(n / 2)) === 0) {
                    fixedPoints.push(n);
                }
            }
        }

        // Generate path and detect cycles
        function generatePath() {
            path = [initValue];
            let current = initValue;

            for (let i = 0; i <= maxSteps + 1; i++) {
                current = f(current);
                path.push(current);
            }

            // Cycle detection
            cycleLength = 0;
            cycleStep = 0;

            for (let i = path.length - 1; i > 0; i--) {
                for (let j = i - 1; j >= 0; j--) {
                    if (path[i] === path[j]) {
                        cycleLength = i - j;
                        cycleStep = i;
                        return;
                    }
                }
            }
        }

        // Update info text
        function updateInfoText() {
            const infoText = document.getElementById('info-text');
            if (cycleLength > 0) {
                infoText.textContent = `result of initial condition ${initValue} repeats in ${cycleLength} steps`;
            } else {
                infoText.textContent = `result of initial condition ${initValue} does not repeat in ${maxSteps + 1} steps`;
            }
        }

        // p5.js sketch
        function sketch(p) {
            p.setup = function() {
                const canvas = p.createCanvas(512, 512);
                canvas.parent('canvas-container');
                p.noLoop();
                calculateFixedPoints();
                generatePath();
                updateInfoText();
            };

            p.draw = function() {
                // Background
                p.background(224, 240, 224);

                // All map points overlay
                if (showAllPoints) {
                    p.stroke(128, 128, 128);
                    p.strokeWeight(1);
                    for (let n = 0; n < 65536; n++) {
                        const next = f(n);
                        const coord = intToCoord(next);
                        const x = p.map(coord.x, 0, 255, 0, p.width);
                        const y = p.map(coord.y, 0, 255, 0, p.height);
                        p.point(x, y);
                    }
                }

                // Draw automaton path
                p.stroke(128, 0, 128);
                p.strokeWeight(2);
                p.noFill();
                p.beginShape();
                for (let i = 0; i < path.length - 1; i++) {
                    const coord = intToCoord(path[i]);
                    const x = p.map(coord.x, 0, 255, 0, p.width);
                    const y = p.map(coord.y, 0, 255, 0, p.height);
                    p.vertex(x, y);
                }
                p.endShape();

                // Draw start and end points
                const startCoord = intToCoord(path[0]);
                const endCoord = intToCoord(path[path.length - 2]);

                p.noStroke();
                p.fill(255, 0, 0);
                p.circle(p.map(startCoord.x, 0, 255, 0, p.width), 
                        p.map(startCoord.y, 0, 255, 0, p.height), 8);

                p.fill(0, 0, 255);
                p.circle(p.map(endCoord.x, 0, 255, 0, p.width), 
                        p.map(endCoord.y, 0, 255, 0, p.height), 8);

                // Length 1 cycle points overlay
                if (showCycle1) {
                    p.fill(0, 255, 255);
                    p.noStroke();
                    for (let n of fixedPoints) {
                        const coord = intToCoord(n);
                        const x = p.map(coord.x, 0, 255, 0, p.width);
                        const y = p.map(coord.y, 0, 255, 0, p.height);
                        p.circle(x, y, 6);
                    }
                }

                // Length 2 cycles overlay
                if (showCycle2) {
                    p.stroke(0, 128, 0);
                    p.strokeWeight(2);
                    for (let pair of cycle2Pairs) {
                        const coord1 = intToCoord(pair[0]);
                        const coord2 = intToCoord(pair[1]);
                        p.line(p.map(coord1.x, 0, 255, 0, p.width),
                              p.map(coord1.y, 0, 255, 0, p.height),
                              p.map(coord2.x, 0, 255, 0, p.width),
                              p.map(coord2.y, 0, 255, 0, p.height));
                    }
                }

                // Length 4 cycle overlay
                if (showCycle4) {
                    const coords = cycle4Points.map(n => {
                        const c = intToCoord(n);
                        return {
                            x: p.map(c.x, 0, 255, 0, p.width),
                            y: p.map(c.y, 0, 255, 0, p.height)
                        };
                    });

                    // Draw cycle path
                    p.stroke(0, 128, 0);
                    p.strokeWeight(2);
                    for (let i = 0; i < 4; i++) {
                        const next = (i + 1) % 4;
                        p.line(coords[i].x, coords[i].y, coords[next].x, coords[next].y);
                    }

                    // Draw bounding box
                    const minX = Math.min(...coords.map(c => c.x));
                    const maxX = Math.max(...coords.map(c => c.x));
                    const minY = Math.min(...coords.map(c => c.y));
                    const maxY = Math.max(...coords.map(c => c.y));

                    p.stroke(255, 165, 0);
                    p.strokeWeight(2);
                    p.noFill();
                    p.rect(minX, minY, maxX - minX, maxY - minY);

                    // Draw perpendicular bisectors
                    p.stroke(0, 128, 0);
                    p.strokeWeight(1);
                    for (let i = 0; i < 4; i++) {
                        const next = (i + 1) % 4;
                        const midX = (coords[i].x + coords[next].x) / 2;
                        const midY = (coords[i].y + coords[next].y) / 2;
                        const dx = coords[next].x - coords[i].x;
                        const dy = coords[next].y - coords[i].y;
                        const len = Math.sqrt(dx * dx + dy * dy);
                        const perpX = -dy / len * 30;
                        const perpY = dx / len * 30;
                        p.line(midX - perpX, midY - perpY, midX + perpX, midY + perpY);
                    }
                }
            };
        }

        // Initialize p5.js
        new p5(sketch);

        // Event listeners
        document.getElementById('slider-initialization').addEventListener('input', function(e) {
            initValue = parseInt(e.target.value);
            document.getElementById('value-initialization').textContent = '+ ' + initValue;
            generatePath();
            updateInfoText();
            window.myp5.redraw();
        });

        document.getElementById('slider-max-steps').addEventListener('input', function(e) {
            maxSteps = parseInt(e.target.value);
            document.getElementById('value-max-steps').textContent = '+ ' + maxSteps;
            generatePath();
            updateInfoText();
            window.myp5.redraw();
        });

        document.getElementById('checkbox-cycle4').addEventListener('change', function(e) {
            showCycle4 = e.target.checked;
            window.myp5.redraw();
        });

        document.getElementById('checkbox-cycle2').addEventListener('change', function(e) {
            showCycle2 = e.target.checked;
            window.myp5.redraw();
        });

        document.getElementById('checkbox-cycle1').addEventListener('change', function(e) {
            showCycle1 = e.target.checked;
            window.myp5.redraw();
        });

        document.getElementById('checkbox-all-points').addEventListener('change', function(e) {
            showAllPoints = e.target.checked;
            window.myp5.redraw();
        });

        document.getElementById('checkbox-modify').addEventListener('change', function(e) {
            modifyInit = e.target.checked;
        });

        // Store p5 instance globally for redraw access
        window.myp5 = new p5(sketch);
    </script>
</body>
</html>
