                        'Cond': { initial: 0.005, Kp: -0.04, tau: 1.5 },
                        '1': { initial: 0.01, Kp: -0.03, tau: 1.2 },
                        '2': { initial: 0.01, Kp: -0.025, tau: 1.0 },
                        '3': { initial: 0.01, Kp: -0.015, tau: 0.9 },
                        '4': { initial: 0.01, Kp: -0.01, tau: 0.8 },
                        '5': { initial: 0.0053, Kp: -0.008, tau: 0.7 },
                        '6': { initial: 0.02, Kp: -0.005, tau: 0.8 },
                        '7': { initial: 0.03, Kp: -0.004, tau: 0.9 },
                        '8': { initial: 0.05, Kp: 0.001, tau: 1.0 },
                        '9': { initial: 0.10, Kp: 0.004, tau: 1.2 },
                        '10': { initial: 0.15, Kp: 0.008, tau: 1.5 },
                        'Reb': { initial: 0.18, Kp: 0.008, tau: 2.0 }
                    }
                },
                temperature: {
                    'Cond': { initial: 80.1, Kp: -1.5, tau: 1.8 },
                    '1': { initial: 80.8, Kp: -1.3, tau: 1.5 },
                    '2': { initial: 81.5, Kp: -1.1, tau: 1.3 },
                    '3': { initial: 82.2, Kp: -0.9, tau: 1.2 },
                    '4': { initial: 82.9, Kp: -0.8, tau: 1.1 },
                    '5': { initial: 83.6, Kp: -0.7, tau: 1.0 },
                    '6': { initial: 84.3, Kp: -0.6, tau: 1.1 },
                    '7': { initial: 85.0, Kp: -0.5, tau: 1.2 },
                    '8': { initial: 85.7, Kp: -0.4, tau: 1.3 },
                    '9': { initial: 86.4, Kp: -0.3, tau: 1.5 },
                    '10': { initial: 87.1, Kp: -0.2, tau: 1.8 },
                    'Reb': { initial: 87.8, Kp: -0.1, tau: 2.2 }
                },
                molar_hold_up: {
                    'Cond': { initial: 12.5, Kp: 1.2, tau: 2.5 },
                    '1': { initial: 0.8, Kp: 0.08, tau: 0.6 },
                    '2': { initial: 0.8, Kp: 0.08, tau: 0.6 },
                    '3': { initial: 0.8, Kp: 0.08, tau: 0.6 },
                    '4': { initial: 0.8, Kp: 0.08, tau: 0.6 },
                    '5': { initial: 0.8, Kp: 0.08, tau: 0.6 },
                    '6': { initial: 0.8, Kp: 0.08, tau: 0.6 },
                    '7': { initial: 0.8, Kp: 0.08, tau: 0.6 },
                    '8': { initial: 0.8, Kp: 0.08, tau: 0.6 },
                    '9': { initial: 0.8, Kp: 0.08, tau: 0.6 },
                    '10': { initial: 0.8, Kp: 0.08, tau: 0.6 },
                    'Reb': { initial: 15.0, Kp: -1.8, tau: 3.0 }
                }
            }
        };

        // Color mapping for components
        const componentColors = {
            benzene: '#FF0000',
            toluene: '#0000FF',
            pxylene: '#008B8B'
        };

        // Generate time array
        function generateTimeArray() {
            const time = [];
            for (let t = 0; t <= 20; t += 0.1) {
                time.push(Math.round(t * 10) / 10);
            }
            return time;
        }

        // Calculate FOPDT response
        function calculateFOPDT(time, params, M) {
            return time.map(t => {
                if (t < 10) {
                    return params.initial;
                } else {
                    return params.initial + params.Kp * M * (1 - Math.exp(-(t - 10) / params.tau));
                }
            });
        }

        // Get current control values
        function getControlValues() {
            const stepType = document.querySelector('input[name="step-type"]:checked').value;
            const stepPercent = parseFloat(document.getElementById('step-percent').value);
            const stage = document.querySelector('input[name="stage"]:checked').value;
            const plotType = document.querySelector('.plot-type-btn.active').id.replace('btn-', '').replace('-', '_');
            const component = document.querySelector('input[name="component"]:checked')?.value || 'benzene';

            return { stepType, stepPercent, stage, plotType, component };
        }

        // Update plot
        function updatePlot() {
            const { stepType, stepPercent, stage, plotType, component } = getControlValues();
            const time = generateTimeArray();

            let params, yLabel, lineColor;

            if (plotType === 'composition') {
                params = simulationParams[stepType][plotType][component][stage];
                yLabel = `${component} mole fraction`;
                lineColor = componentColors[component];
            } else if (plotType === 'temperature') {
                params = simulationParams[stepType][plotType][stage];
                yLabel = 'temperature (C)';
                lineColor = '#000000';
            } else if (plotType === 'molar_hold_up') {
                params = simulationParams[stepType]['molar_hold_up'][stage];
                yLabel = 'molar hold-up (kmol)';
                lineColor = '#000000';
            }

            const yData = calculateFOPDT(time, params, stepPercent);

            const trace = {
                x: time,
                y: yData,
                type: 'scatter',
                mode: 'lines',
                line: {
                    color: lineColor,
                    width: 2
                }
            };

            const layout = {
                xaxis: {
                    title: 'time in minutes',
                    gridcolor: '#CCCCCC',
                    range: [0, 20]
                },
                yaxis: {
                    title: yLabel,
                    gridcolor: '#CCCCCC'
                },
                margin: { l: 80, r: 20, t: 20, b: 50 },
                paper_bgcolor: 'rgba(0,0,0,0)',
                plot_bgcolor: 'rgba(0,0,0,0)'
            };

            Plotly.react('plot-container', [trace], layout);
        }

        // Handle plot type button clicks
        document.querySelectorAll('.plot-type-btn').forEach(btn => {
            btn.addEventListener('click', function() {
                document.querySelectorAll('.plot-type-btn').forEach(b => b.classList.remove('active'));
                this.classList.add('active');

                const componentSelector = document.getElementById('component-selector-container');
                if (this.id === 'btn-composition') {
                    componentSelector.style.display = 'flex';
                } else {
                    componentSelector.style.display = 'none';
                }

                updatePlot();
            });
        });

        // Add event listeners to all controls
        document.querySelectorAll('input[name="step-type"]').forEach(input => {
            input.addEventListener('change', updatePlot);
        });

        document.getElementById('step-percent').addEventListener('input', updatePlot);

        document.querySelectorAll('input[name="stage"]').forEach(input => {
            input.addEventListener('change', updatePlot);
        });

        document.querySelectorAll('input[name="component"]').forEach(input => {
            input.addEventListener('change', updatePlot);
        });

        // Initialize plot on page load
        updatePlot();
    </script>
</body>
</html>
