            </select>

            <label for="select-constellation">constellation</label>
            <select id="select-constellation">
                <option value="None">None</option>
            </select>

            <div class="checkbox-container">
                <label>
                    <input type="checkbox" id="check-sphere" checked>
                    celestial sphere
                </label>
                <label>
                    <input type="checkbox" id="check-stars" checked>
                    1000 brightest stars
                </label>
                <label>
                    <input type="checkbox" id="check-equator" checked>
                    celestial equator
                </label>
                <label>
                    <input type="checkbox" id="check-ecliptic" checked>
                    ecliptic
                </label>
                <label>
                    <input type="checkbox" id="check-zenith" checked>
                    zenith
                </label>
