        <div id="visualization-container"></div>
    </div>

    <script src="https://cdn.jsdelivr.net/npm/three@0.150.0/build/three.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/three@0.150.0/examples/js/controls/OrbitControls.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/three@0.150.0/examples/js/renderers/CSS2DRenderer.js"></script>

    <script>
        // Constellation data
        const constellationFamilies = {
            "Zodiac": ["Aries", "Taurus", "Gemini", "Cancer", "Leo", "Virgo", "Libra", "Scorpius", "Sagittarius", "Capricornus", "Aquarius", "Pisces"],
            "Ursa Major Family": ["Ursa Major", "Ursa Minor", "Draco", "Canes Venatici", "Boötes", "Corona Borealis"],
            "Perseus Family": ["Perseus", "Andromeda", "Cassiopeia", "Cepheus", "Cetus", "Auriga", "Lacerta", "Pegasus", "Triangulum"],
            "Hercules Family": ["Hercules", "Sagitta", "Aquila", "Lyra", "Cygnus", "Vulpecula", "Hydra", "Sextans", "Crater", "Corvus", "Ophiuchus", "Serpens", "Scutum", "Centaurus", "Lupus", "Corona Australis", "Ara"],
            "Orion Family": ["Orion", "Canis Major", "Canis Minor", "Lepus", "Monoceros"],
            "Bayer Group": ["Hydrus", "Tucana", "Grus", "Phoenix", "Dorado", "Pictor", "Volans", "Pavo", "Indus"],
            "La Caille Family": ["Norma", "Circinus", "Telescopium", "Microscopium", "Sculptor", "Fornax", "Caelum", "Horologium", "Octans", "Mensa", "Reticulum", "Pictor", "Antlia", "Pyxis", "Puppis", "Vela", "Carina"]
        };

        // Sample star data (simplified)
        const starData = [];
        for (let i = 0; i < 1000; i++) {
            starData.push({
                ra: Math.random() * 24,  // hours
                dec: (Math.random() - 0.5) * 180,  // degrees
                mag: Math.random() * 5
            });
        }

        // Sample constellation line data
        const constellationLines = {
            "Cygnus": [
                {ra: 20.37, dec: 40.26}, {ra: 20.77, dec: 33.97},
                {ra: 20.77, dec: 33.97}, {ra: 21.22, dec: 30.23},
                {ra: 20.77, dec: 33.97}, {ra: 19.51, dec: 27.96},
                {ra: 20.77, dec: 33.97}, {ra: 19.94, dec: 35.08}
            ]
        };

        // Three.js setup
        let scene, camera, renderer, labelRenderer, controls;
        let sphere, stars, equator, ecliptic, zenithGroup, ariesPoint, polarAxis;
        let constellationLinesMesh, constellationStarsMesh;

        function init() {
            // Scene
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0x000000);

            // Camera
            camera = new THREE.PerspectiveCamera(
                75,
                window.innerWidth / window.innerHeight,
                0.1,
                1000
            );
            camera.position.set(0, 0, 3);

            // Renderer
            renderer = new THREE.WebGLRenderer({ antialias: true });
            renderer.setSize(window.innerWidth - 300, window.innerHeight);
            document.getElementById('visualization-container').appendChild(renderer.domElement);

            // CSS2D Renderer for labels
            labelRenderer = new THREE.CSS2DRenderer();
            labelRenderer.setSize(window.innerWidth - 300, window.innerHeight);
            labelRenderer.domElement.style.position = 'absolute';
            labelRenderer.domElement.style.top = '0px';
            labelRenderer.domElement.style.pointerEvents = 'none';
            document.getElementById('visualization-container').appendChild(labelRenderer.domElement);

            // Controls
            controls = new THREE.OrbitControls(camera, renderer.domElement);
            controls.enableDamping = true;
            controls.dampingFactor = 0.05;

            // Create celestial sphere
            createCelestialSphere();

            // Create stars
            createStars();

            // Create equator and ecliptic
            createEquatorAndEcliptic();

            // Create zenith
            createZenith();

            // Create First Point of Aries
            createAriesPoint();

            // Create polar axis and labels
            createPolarAxis();

            // Create constellation objects
            createConstellationObjects();

            // Set up event listeners
            setupEventListeners();

            // Initialize constellation dropdown
            updateConstellationOptions();

            // Start animation
            animate();
        }

        function createCelestialSphere() {
            const geometry = new THREE.SphereGeometry(1, 32, 32);
            const colors = [];
            const color = new THREE.Color();

            for (let i = 0; i < geometry.attributes.position.count; i++) {
                const y = geometry.attributes.position.getY(i);
                const t = (y + 1) / 2; // Normalize to 0-1
                color.setHSL(0.05 - t * 0.25, 0.7, 0.5); // Orange to blue gradient
                colors.push(color.r, color.g, color.b);
            }

            geometry.setAttribute('color', new THREE.Float32BufferAttribute(colors, 3));

            const material = new THREE.MeshBasicMaterial({
                vertexColors: true,
                transparent: true,
                opacity: 0.4,
                side: THREE.DoubleSide
            });

            sphere = new THREE.Mesh(geometry, material);
            scene.add(sphere);
        }

        function createStars() {
            const positions = [];

            starData.forEach(star => {
                const ra = star.ra * 15 * Math.PI / 180; // Convert to radians
                const dec = star.dec * Math.PI / 180;

                const x = Math.cos(dec) * Math.cos(ra);
                const z = Math.cos(dec) * Math.sin(ra);
                const y = Math.sin(dec);

                positions.push(x, y, z);
            });

            const geometry = new THREE.BufferGeometry();
            geometry.setAttribute('position', new THREE.Float32BufferAttribute(positions, 3));

            const material = new THREE.PointsMaterial({
                color: 0xffffff,
                size: 0.02
            });

            stars = new THREE.Points(geometry, material);
            scene.add(stars);
        }

        function createEquatorAndEcliptic() {
            // Celestial equator
            const equatorPoints = [];
            for (let i = 0; i <= 100; i++) {
                const angle = (i / 100) * Math.PI * 2;
                equatorPoints.push(new THREE.Vector3(Math.cos(angle), 0, Math.sin(angle)));
            }

            const equatorGeometry = new THREE.BufferGeometry().setFromPoints(equatorPoints);
            const equatorMaterial = new THREE.LineBasicMaterial({ color: 0x0088ff });
            equator = new THREE.LineLoop(equatorGeometry, equatorMaterial);
            scene.add(equator);

            // Ecliptic
            const eclipticPoints = [];
            const tilt = 23.5 * Math.PI / 180;

            for (let i = 0; i <= 100; i++) {
                const angle = (i / 100) * Math.PI * 2;
                const x = Math.cos(angle);
                const y = Math.sin(angle) * Math.sin(tilt);
                const z = Math.sin(angle) * Math.cos(tilt);
                eclipticPoints.push(new THREE.Vector3(x, y, z));
            }

            const eclipticGeometry = new THREE.BufferGeometry().setFromPoints(eclipticPoints);
            const eclipticMaterial = new THREE.LineBasicMaterial({ color: 0xff00ff });
            ecliptic = new THREE.LineLoop(eclipticGeometry, eclipticMaterial);
            scene.add(ecliptic);
        }

        function createZenith() {
            zenithGroup = new THREE.Group();

            // Zenith point
            const pointGeometry = new THREE.SphereGeometry(0.03, 16, 16);
            const pointMaterial = new THREE.MeshBasicMaterial({ color: 0x000000 });
            const zenithPoint = new THREE.Mesh(pointGeometry, pointMaterial);

            // Line from origin to zenith
            const lineGeometry = new THREE.BufferGeometry().setFromPoints([
                new THREE.Vector3(0, 0, 0),
                new THREE.Vector3(0, 1, 0)
            ]);
            const lineMaterial = new THREE.LineBasicMaterial({ color: 0x000000 });
            const zenithLine = new THREE.Line(lineGeometry, lineMaterial);

            zenithGroup.add(zenithPoint);
            zenithGroup.add(zenithLine);
            scene.add(zenithGroup);

            updateZenithPosition();
        }

        function createAriesPoint() {
            const geometry = new THREE.SphereGeometry(0.03, 16, 16);
            const material = new THREE.MeshBasicMaterial({ color: 0xffff00 });
            ariesPoint = new THREE.Mesh(geometry, material);
            ariesPoint.position.set(1, 0, 0);
            scene.add(ariesPoint);
        }

        function createPolarAxis() {
            // Polar axis line
            const axisGeometry = new THREE.BufferGeometry().setFromPoints([
                new THREE.Vector3(0, -1.1, 0),
                new THREE.Vector3(0, 1.1, 0)
            ]);
            const axisMaterial = new THREE.LineBasicMaterial({ color: 0x000000 });
            polarAxis = new THREE.Line(axisGeometry, axisMaterial);
            scene.add(polarAxis);

            // North pole label
            const northDiv = document.createElement('div');
            northDiv.className = 'label2d';
            northDiv.textContent = 'N';
            const northLabel = new THREE.CSS2DObject(northDiv);
            northLabel.position.set(0, 1.15, 0);
            scene.add(northLabel);

            // South pole label
            const southDiv = document.createElement('div');
            southDiv.className = 'label2d';
            southDiv.textContent = 'S';
            const southLabel = new THREE.CSS2DObject(southDiv);
            southLabel.position.set(0, -1.15, 0);
            scene.add(southLabel);
        }

        function createConstellationObjects() {
            // Constellation lines
            const lineGeometry = new THREE.BufferGeometry();
            const lineMaterial = new THREE.LineBasicMaterial({ color: 0xff0000 });
            constellationLinesMesh = new THREE.LineSegments(lineGeometry, lineMaterial);
            scene.add(constellationLinesMesh);

            // Constellation stars
            const starsGeometry = new THREE.BufferGeometry();
            const starsMaterial = new THREE.PointsMaterial({ 
                color: 0xffff00, 
                size: 0.05 
            });
            constellationStarsMesh = new THREE.Points(starsGeometry, starsMaterial);
            scene.add(constellationStarsMesh);
        }

        function updateZenithPosition() {
            const ra = parseFloat(document.getElementById('slider-ra').value) * Math.PI / 180;
            const dec = parseFloat(document.getElementById('slider-dec').value) * Math.PI / 180;

            const x = Math.cos(dec) * Math.cos(ra);
            const z = Math.cos(dec) * Math.sin(ra);
            const y = Math.sin(dec);

            zenithGroup.children[0].position.set(x, y, z);
            zenithGroup.children[1].geometry.setFromPoints([
                new THREE.Vector3(0, 0, 0),
                new THREE.Vector3(x, y, z)
            ]);
        }

        function updateConstellationOptions() {
            const family = document.getElementById('select-family').value;
            const constellationSelect = document.getElementById('select-constellation');

            constellationSelect.innerHTML = '<option value="None">None</option>';

            if (family === 'all') {
                for (const fam in constellationFamilies) {
                    constellationFamilies[fam].forEach(constellation => {
                        const option = document.createElement('option');
                        option.value = constellation;
                        option.textContent = constellation;
                        constellationSelect.appendChild(option);
                    });
                }
                constellationSelect.value = 'None';
            } else {
                constellationFamilies[family].forEach(constellation => {
                    const option = document.createElement('option');
                    option.value = constellation;
                    option.textContent = constellation;
                    constellationSelect.appendChild(option);
                });
                constellationSelect.value = constellationFamilies[family][0];
            }

            updateConstellation();
        }

        function updateConstellation() {
            const selectedConstellation = document.getElementById('select-constellation').value;

            if (selectedConstellation === 'None' || !constellationLines[selectedConstellation]) {
                constellationLinesMesh.geometry.setFromPoints([]);
                constellationStarsMesh.geometry.setFromPoints([]);
                return;
            }

            const lines = constellationLines[selectedConstellation];
            const linePositions = [];
            const starPositions = [];

            for (let i = 0; i < lines.length; i++) {
                const ra = lines[i].ra * 15 * Math.PI / 180;
                const dec = lines[i].dec * Math.PI / 180;

                const x = Math.cos(dec) * Math.cos(ra);
                const z = Math.cos(dec) * Math.sin(ra);
                const y = Math.sin(dec);

                linePositions.push(x, y, z);

                if (i % 2 === 0) {
                    starPositions.push(x, y, z);
                }
            }

            constellationLinesMesh.geometry.setAttribute(
                'position', 
                new THREE.Float32BufferAttribute(linePositions, 3)
            );

            constellationStarsMesh.geometry.setAttribute(
                'position',
                new THREE.Float32BufferAttribute(starPositions, 3)
            );
        }

        function setupEventListeners() {
            // Family dropdown
            document.getElementById('select-family').addEventListener('change', updateConstellationOptions);

            // Constellation dropdown
            document.getElementById('select-constellation').addEventListener('change', updateConstellation);

            // Checkboxes
            document.getElementById('check-sphere').addEventListener('change', (e) => {
                sphere.visible = e.target.checked;
            });

            document.getElementById('check-stars').addEventListener('change', (e) => {
                stars.visible = e.target.checked;
            });

            document.getElementById('check-equator').addEventListener('change', (e) => {
                equator.visible = e.target.checked;
            });

            document.getElementById('check-ecliptic').addEventListener('change', (e) => {
                ecliptic.visible = e.target.checked;
            });

            document.getElementById('check-zenith').addEventListener('change', (e) => {
                zenithGroup.visible = e.target.checked;
            });

            document.getElementById('check-aries').addEventListener('change', (e) => {
                ariesPoint.visible = e.target.checked;
            });

            // Sliders
            document.getElementById('slider-ra').addEventListener('input', updateZenithPosition);
            document.getElementById('slider-dec').addEventListener('input', updateZenithPosition);

            // Window resize
            window.addEventListener('resize', onWindowResize);
        }

        function onWindowResize() {
            camera.aspect = (window.innerWidth - 300) / window.innerHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(window.innerWidth - 300, window.innerHeight);
            labelRenderer.setSize(window.innerWidth - 300, window.innerHeight);
        }

        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
            labelRenderer.render(scene, camera);
        }

        // Initialize when DOM is loaded
        init();

        // Set default constellation
        document.getElementById('select-constellation').value = 'Cygnus';
        updateConstellation();
    </script>
</body>
</html>
