    </style>
</head>
<body>
    <!-- Main container -->
    <div id="app-container">
        <!-- Left side: Control Panel -->
        <div id="control-panel">
            <!-- Coefficients Section -->
            <h3>coefficients</h3>
            <div class="control-group">
                <label for="slider-c1">c<sub>1</sub></label>
                <input type="range" id="slider-c1" min="-2" max="2" step="0.1" value="0">
                <span id="c1-value-display">0.0</span>
                <button id="reset-c1">+</button>
            </div>
            <div class="control-group">
                <label for="slider-c2">c<sub>2</sub></label>
                <input type="range" id="slider-c2" min="-2" max="2" step="0.1" value="0">
                <span id="c2-value-display">0.0</span>
                <button id="reset-c2">+</button>
            </div>

            <!-- Clipping Section -->
            <h3>clipping</h3>
            <div class="control-group">
                <input type="checkbox" id="checkbox-clipping">
                <label for="checkbox-clipping" style="display: inline; margin-left: 5px;"></label>
            </div>
            <div id="level-control-group" class="control-group" style="display: none;">
                <label for="slider-level">level</label>
                <input type="range" id="slider-level" min="0" max="1.5" step="0.05" value="0.7">
                <span id="level-value-display">0.7</span>
            </div>

            <!-- DFT Section -->
            <h3>DFT</h3>
            <div class="control-group">
                <label>scale</label>
                <button id="btn-linear" class="active">linear</button>
                <button id="btn-decibel">decibel</button>
            </div>
        </div>

        <!-- Right side: Visualization Area -->
        <div id="visualization-area">
            <div id="plot-signal"></div>
            <div id="plot-spectrum"></div>
        </div>
    </div>

    <!-- Plotly.js CDN -->
    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>

    <script>
        // Global variables
        let normalizedMag = [];

        // FFT implementation
        function fft(real, imag) {
            const n = real.length;
            if (n <= 1) return;

            // Bit reversal
            for (let i = 1, j = 0; i < n; i++) {
                let bit = n >> 1;
                for (; j & bit; bit >>= 1) {
                    j ^= bit;
                }
                j ^= bit;

                if (i < j) {
                    [real[i], real[j]] = [real[j], real[i]];
                    [imag[i], imag[j]] = [imag[j], imag[i]];
                }
            }

            // Cooley-Tukey FFT
            for (let len = 2; len <= n; len <<= 1) {
                const ang = 2 * Math.PI / len;
                const wlenr = Math.cos(ang);
                const wleni = Math.sin(ang);

                for (let i = 0; i < n; i += len) {
                    let wr = 1, wi = 0;

                    for (let j = 0; j < len / 2; j++) {
                        const u = i + j;
                        const v = u + len / 2;

                        const tr = wr * real[v] - wi * imag[v];
                        const ti = wr * imag[v] + wi * real[v];

                        real[v] = real[u] - tr;
                        imag[v] = imag[u] - ti;
                        real[u] += tr;
                        imag[u] += ti;

                        const tmp = wr;
                        wr = tmp * wlenr - wi * wleni;
                        wi = tmp * wleni + wi * wlenr;
                    }
                }
            }
        }

        function updateSimulation() {
            // Constants
            const f = 1000; // Hz
            const A = 1;
            const N = 2048;
            const fs = 32000; // Hz

            // Read control values
            const c1 = parseFloat(document.getElementById('slider-c1').value);
            const c2 = parseFloat(document.getElementById('slider-c2').value);
            const clippingEnabled = document.getElementById('checkbox-clipping').checked;
            const level = parseFloat(document.getElementById('slider-level').value);

            // Generate time vector
            const t = [];
            for (let i = 0; i < N; i++) {
                t.push(i / fs);
            }

            // Generate input and output signals
            const x = [];
            const y = [];
            for (let i = 0; i < N; i++) {
                const xi = A * Math.sin(2 * Math.PI * f * t[i]);
                x.push(xi);
                let yi = xi + c1 * xi * xi + c2 * xi * xi * xi;

                // Apply clipping if enabled
                if (clippingEnabled) {
                    yi = Math.max(-level, Math.min(level, yi));
                }

                y.push(yi);
            }

            // Update signal plot (show only first 5ms)
            const plotSamples = Math.floor(0.005 * fs);
            Plotly.react('plot-signal', [{
                x: t.slice(0, plotSamples),
                y: y.slice(0, plotSamples),
                type: 'scatter',
                mode: 'lines',
                line: { color: 'blue', width: 2 }
            }], {
                title: 'signal',
                xaxis: { title: 'time[s]', range: [0, 0.005], autorange: false },
                yaxis: { title: '', range: [-2, 2], autorange: false },
                margin: { l: 50, r: 20, b: 40, t: 40 }
            });

            // Perform FFT
            const real = [...y];
            const imag = new Array(N).fill(0);
            fft(real, imag);

            // Calculate magnitude spectrum
            const mag = [];
            for (let k = 0; k < N / 2; k++) {
                mag.push(Math.sqrt(real[k] * real[k] + imag[k] * imag[k]));
            }

            // Normalize spectrum
            normalizedMag = [];
            normalizedMag.push(mag[0] / N); // DC component
            for (let k = 1; k < N / 2; k++) {
                normalizedMag.push(2 * mag[k] / N);
            }

            // Update spectrum plot
            updateSpectrumScale();

            // Update value displays
            document.getElementById('c1-value-display').textContent = c1.toFixed(1);
            document.getElementById('c2-value-display').textContent = c2.toFixed(1);
            document.getElementById('level-value-display').textContent = level.toFixed(2);
        }

        function updateSpectrumScale() {
            const isLinear = document.getElementById('btn-linear').classList.contains('active');
            const N = 2048;
            const fs = 32000;

            // Create frequency vector
            const freq = [];
            for (let k = 0; k < N / 2; k++) {
                freq.push(k * fs / N);
            }

            // Process y-data based on scale
            let yData;
            let yAxisRange;

            if (isLinear) {
                yData = normalizedMag;
                yAxisRange = [0, 1.1];
            } else {
                yData = normalizedMag.map(val => {
                    if (val === 0) return -100;
                    const db = 20 * Math.log10(val);
                    return Math.max(-100, db);
                });
                yAxisRange = [-100, 0];
            }

            // Update spectrum plot
            Plotly.react('plot-spectrum', [{
                x: freq,
                y: yData,
                type: 'bar',
                width: 5,
                marker: { color: 'red' }
            }], {
                title: 'magnitude spectrum (DFT)',
                xaxis: { title: 'frequency[Hz]', range: [0, 8000], autorange: false },
                yaxis: { title: '', range: yAxisRange, autorange: false },
                margin: { l: 50, r: 20, b: 40, t: 40 }
            });
        }

        // Event listeners
        document.getElementById('slider-c1').addEventListener('input', updateSimulation);
        document.getElementById('slider-c2').addEventListener('input', updateSimulation);
        document.getElementById('slider-level').addEventListener('input', updateSimulation);

        document.getElementById('reset-c1').addEventListener('click', () => {
            document.getElementById('slider-c1').value = 0;
            updateSimulation();
        });

        document.getElementById('reset-c2').addEventListener('click', () => {
            document.getElementById('slider-c2').value = 0;
            updateSimulation();
        });

        document.getElementById('checkbox-clipping').addEventListener('change', (e) => {
            document.getElementById('level-control-group').style.display = 
                e.target.checked ? 'flex' : 'none';
            updateSimulation();
        });

        document.getElementById('btn-linear').addEventListener('click', () => {
            document.getElementById('btn-linear').classList.add('active');
            document.getElementById('btn-decibel').classList.remove('active');
            updateSpectrumScale();
        });

        document.getElementById('btn-decibel').addEventListener('click', () => {
            document.getElementById('btn-decibel').classList.add('active');
            document.getElementById('btn-linear').classList.remove('active');
            updateSpectrumScale();
        });

        // Initialize on load
        window.addEventListener('load', () => {
            updateSimulation();
        });
    </script>
</body>
</html>
