<body>
    <div id="main-container">
        <div id="control-panel">
            <h3>Parameters</h3>

            <div class="control-group">
                <label for="slider-ray-count">ray count</label>
                <div class="slider-container">
                    <input type="range" id="slider-ray-count" min="1" max="50" step="1" value="15">
                    <span class="slider-value" id="value-ray-count">15</span>
                </div>
            </div>

            <div class="control-group">
                <label for="slider-beam-width">beam width</label>
                <div class="slider-container">
                    <input type="range" id="slider-beam-width" min="0.1" max="2.0" step="0.01" value="1.25">
                    <span class="slider-value" id="value-beam-width">1.25</span>
                </div>
            </div>

            <div class="control-group">
                <label for="slider-incident-angle">incident angle (°)</label>
                <div class="slider-container">
                    <input type="range" id="slider-incident-angle" min="-90" max="90" step="1" value="-45">
                    <span class="slider-value" id="value-incident-angle">-45</span>
                </div>
            </div>

            <div class="control-group">
                <label for="slider-refraction-ratio">refraction ratio</label>
                <div class="slider-container">
                    <input type="range" id="slider-refraction-ratio" min="1.0" max="3.0" step="0.01" value="2.0">
                    <span class="slider-value" id="value-refraction-ratio">2.00</span>
                </div>
            </div>

            <h3>Ray Visibility</h3>

            <div class="checkbox-container">
                <input type="checkbox" id="checkbox-injected" checked>
                <label for="checkbox-injected">injected</label>
            </div>

            <div class="checkbox-container">
                <input type="checkbox" id="checkbox-refracted" checked>
                <label for="checkbox-refracted">refracted</label>
            </div>

            <div class="checkbox-container">
                <input type="checkbox" id="checkbox-reflected" checked>
                <label for="checkbox-reflected">reflected</label>
            </div>

            <div class="checkbox-container">
                <input type="checkbox" id="checkbox-ejected" checked>
                <label for="checkbox-ejected">ejected</label>
            </div>

            <h3>Additional Display</h3>

            <div class="checkbox-container">
                <input type="checkbox" id="checkbox-normals">
                <label for="checkbox-normals">normals</label>
            </div>

            <div class="checkbox-container">
                <input type="checkbox" id="checkbox-intersections">
                <label for="checkbox-intersections">intersections</label>
            </div>

            <div class="checkbox-container">
                <input type="checkbox" id="checkbox-beam-top" checked>
                <label for="checkbox-beam-top">beam top half</label>
            </div>

            <div class="checkbox-container">
                <input type="checkbox" id="checkbox-beam-bottom" checked>
                <label for="checkbox-beam-bottom">beam bottom half</label>
            </div>
        </div>

        <div id="canvas-container"></div>
    </div>

    <script>
        // Update slider value displays
        document.getElementById('slider-ray-count').addEventListener('input', function(e) {
            document.getElementById('value-ray-count').textContent = e.target.value;
        });

        document.getElementById('slider-beam-width').addEventListener('input', function(e) {
            document.getElementById('value-beam-width').textContent = parseFloat(e.target.value).toFixed(2);
        });

        document.getElementById('slider-incident-angle').addEventListener('input', function(e) {
            document.getElementById('value-incident-angle').textContent = e.target.value;
        });

        document.getElementById('slider-refraction-ratio').addEventListener('input', function(e) {
            document.getElementById('value-refraction-ratio').textContent = parseFloat(e.target.value).toFixed(2);
        });

        // p5.js sketch
        let sketch = function(p) {
            let scaleFactor;

            p.setup = function() {
                let container = document.getElementById('canvas-container');
                let canvas = p.createCanvas(container.offsetWidth, container.offsetHeight);
                canvas.parent('canvas-container');
                canvas.id('p5-canvas');
                scaleFactor = Math.min(p.width, p.height) / 5;
            };

            p.windowResized = function() {
                let container = document.getElementById('canvas-container');
                p.resizeCanvas(container.offsetWidth, container.offsetHeight);
                scaleFactor = Math.min(p.width, p.height) / 5;
            };

            p.draw = function() {
                p.background(255);

                // Set up coordinate system
                p.push();
                p.translate(p.width / 2, p.height / 2);
                p.scale(scaleFactor, -scaleFactor);

                // Draw axes
                p.strokeWeight(1 / scaleFactor);
                p.stroke(150);
                p.line(-2.5, 0, 2.5, 0);
                p.line(0, -2.5, 0, 2.5);

                // Draw tick marks and labels
                p.push();
                p.scale(1, -1);
                p.textAlign(p.CENTER, p.TOP);
                p.textSize(12 / scaleFactor);
                p.fill(0);
                for (let i = -2; i <= 2; i++) {
                    if (i !== 0) {
                        p.line(i, -0.05, i, 0.05);
                        p.text(i, i, 0.1);
                        p.line(-0.05, i, 0.05, i);
                        p.push();
                        p.translate(-0.2, i);
                        p.text(i, 0, 0);
                        p.pop();
                    }
                }
                p.pop();

                // Draw sphere
                p.noFill();
                p.stroke(0);
                p.strokeWeight(2 / scaleFactor);
                p.circle(0, 0, 2);

                // Get control values
                let rayCount = parseInt(document.getElementById('slider-ray-count').value);
                let beamWidth = parseFloat(document.getElementById('slider-beam-width').value);
                let incidentAngle = parseFloat(document.getElementById('slider-incident-angle').value) * Math.PI / 180;
                let refractionRatio = parseFloat(document.getElementById('slider-refraction-ratio').value);

                let showInjected = document.getElementById('checkbox-injected').checked;
                let showRefracted = document.getElementById('checkbox-refracted').checked;
                let showReflected = document.getElementById('checkbox-reflected').checked;
                let showEjected = document.getElementById('checkbox-ejected').checked;
                let showNormals = document.getElementById('checkbox-normals').checked;
                let showIntersections = document.getElementById('checkbox-intersections').checked;
                let showBeamTop = document.getElementById('checkbox-beam-top').checked;
                let showBeamBottom = document.getElementById('checkbox-beam-bottom').checked;

                // Ray tracing
                for (let i = 0; i < rayCount; i++) {
                    let t = rayCount === 1 ? 0.5 : i / (rayCount - 1);
                    let offset = (t - 0.5) * beamWidth;

                    // Check if this ray should be drawn
                    if (offset > 0 && !showBeamTop) continue;
                    if (offset < 0 && !showBeamBottom) continue;

                    // Calculate ray origin and direction
                    let rayDir = p.createVector(Math.cos(incidentAngle), Math.sin(incidentAngle));
                    let perpDir = p.createVector(-Math.sin(incidentAngle), Math.cos(incidentAngle));
                    let rayOrigin = p.createVector(-3 * rayDir.x, -3 * rayDir.y);
                    rayOrigin.add(p5.Vector.mult(perpDir, offset));

                    // First intersection with sphere
                    let intersection1 = raySphereIntersection(rayOrigin, rayDir, 1);
                    if (!intersection1) continue;

                    // Draw injected ray
                    if (showInjected) {
                        p.stroke(255, 0, 0);
                        p.strokeWeight(1 / scaleFactor);
                        p.line(rayOrigin.x, rayOrigin.y, intersection1.x, intersection1.y);
                    }

                    // First refraction
                    let normal1 = p5.Vector.normalize(intersection1);
                    let refractedDir = refract(rayDir, normal1, 1.0, refractionRatio);
                    if (!refractedDir) continue;

                    // Second intersection (from inside)
                    let intersection2 = raySphereIntersection(intersection1, refractedDir, 1, true);
                    if (!intersection2) continue;

                    // Draw refracted ray
                    if (showRefracted) {
                        p.stroke(0, 255, 0);
                        p.strokeWeight(1 / scaleFactor);
                        p.line(intersection1.x, intersection1.y, intersection2.x, intersection2.y);
                    }

                    // Reflection
                    let normal2 = p5.Vector.normalize(intersection2);
                    let reflectedDir = reflect(refractedDir, p5.Vector.mult(normal2, -1));

                    // Third intersection (exit point)
                    let intersection3 = raySphereIntersection(intersection2, reflectedDir, 1, true);
                    if (!intersection3) continue;

                    // Draw reflected ray
                    if (showReflected) {
                        p.stroke(0, 0, 255);
                        p.strokeWeight(1 / scaleFactor);
                        p.line(intersection2.x, intersection2.y, intersection3.x, intersection3.y);
                    }

                    // Second refraction (exit)
                    let normal3 = p5.Vector.normalize(intersection3);
                    let ejectedDir = refract(reflectedDir, p5.Vector.mult(normal3, -1), refractionRatio, 1.0);
                    if (!ejectedDir) continue;

                    // Draw ejected ray
                    if (showEjected) {
                        p.stroke(0);
                        p.strokeWeight(1 / scaleFactor);
                        let ejectedEnd = p5.Vector.add(intersection3, p5.Vector.mult(ejectedDir, 3));
                        p.line(intersection3.x, intersection3.y, ejectedEnd.x, ejectedEnd.y);
                    }

                    // Draw normals
                    if (showNormals) {
                        p.stroke(100);
                        p.strokeWeight(1 / scaleFactor);
                        p.drawingContext.setLineDash([5 / scaleFactor, 5 / scaleFactor]);

                        drawNormal(intersection1, normal1);
                        drawNormal(intersection2, normal2);
                        drawNormal(intersection3, normal3);

                        p.drawingContext.setLineDash([]);
                    }

                    // Draw intersections
                    if (showIntersections) {
                        p.noStroke();
                        p.fill(255, 0, 0);
                        p.circle(intersection1.x, intersection1.y, 0.05);
                        p.fill(0, 255, 0);
                        p.circle(intersection2.x, intersection2.y, 0.05);
                        p.fill(0, 0, 255);
                        p.circle(intersection3.x, intersection3.y, 0.05);
                    }
                }

                p.pop();
            };

            function raySphereIntersection(origin, direction, radius, fromInside = false) {
                let oc = origin;
                let a = direction.magSq();
                let b = 2 * p5.Vector.dot(oc, direction);
                let c = oc.magSq() - radius * radius;
                let discriminant = b * b - 4 * a * c;

                if (discriminant < 0) return null;

                let t1 = (-b - Math.sqrt(discriminant)) / (2 * a);
                let t2 = (-b + Math.sqrt(discriminant)) / (2 * a);

                let t = fromInside ? Math.max(t1, t2) : Math.min(t1, t2);
                if (t < 0.001) t = Math.max(t1, t2);
                if (t < 0.001) return null;

                return p5.Vector.add(origin, p5.Vector.mult(direction, t));
            }

            function refract(incident, normal, n1, n2) {
                let cosI = -p5.Vector.dot(incident, normal);
                let sinT2 = (n1 / n2) * (n1 / n2) * (1 - cosI * cosI);

                if (sinT2 > 1) return null; // Total internal reflection

                let cosT = Math.sqrt(1 - sinT2);
                return p5.Vector.add(
                    p5.Vector.mult(incident, n1 / n2),
                    p5.Vector.mult(normal, n1 / n2 * cosI - cosT)
                );
            }

            function reflect(incident, normal) {
                return p5.Vector.sub(incident, p5.Vector.mult(normal, 2 * p5.Vector.dot(incident, normal)));
            }

            function drawNormal(point, normal) {
                let normalEnd = p5.Vector.add(point, p5.Vector.mult(normal, 0.2));
                p.line(point.x, point.y, normalEnd.x, normalEnd.y);
            }
        };

        new p5(sketch);
    </script>
</body>
