    <script>
        // Helper function to calculate factorial
        function factorial(n) {
            if (n <= 1) return 1;
            let result = 1;
            for (let i = 2; i <= n; i++) {
                result *= i;
            }
            return result;
        }

        // Generate data for algorithms
        function generateData(maxN) {
            const x = [];
            for (let i = 1; i <= maxN; i++) {
                x.push(i);
            }

            const data = {
                bubble: x.map(n => n * n),
                selection: x.map(n => 0.25 * n * n),
                insertion: x.map(n => n * factorial(n)),
                merge: x.map(n => n * Math.log2(n)),
                quick: x.map(n => 1.2 * n * Math.log2(n)),
                heap: x.map(n => 1.5 * n * Math.log2(n)),
                bogo: x.map(n => n * factorial(n))
            };

            return { x, data };
        }

        // Initialize traces
        function createTraces(maxN) {
            const { x, data } = generateData(maxN);

            return [
                {
                    x: x,
                    y: data.bubble,
                    name: 'bubble',
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: 'green', width: 5 },
                    visible: document.getElementById('check-bubble').checked
                },
                {
                    x: x,
                    y: data.selection,
                    name: 'selection',
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: 'orange', dash: 'dot' },
                    visible: document.getElementById('check-selection').checked
                },
                {
                    x: x,
                    y: data.insertion,
                    name: 'insertion',
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: 'red', width: 2 },
                    visible: document.getElementById('check-insertion').checked || document.getElementById('check-bogo').checked
                },
                {
                    x: x,
                    y: data.merge,
                    name: 'merge',
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: 'blue', width: 2 },
                    visible: document.getElementById('check-merge').checked
                },
                {
                    x: x,
                    y: data.quick,
                    name: 'quick',
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: 'purple', width: 2 },
                    visible: document.getElementById('check-quick').checked
                },
                {
                    x: x,
                    y: data.heap,
                    name: 'heap',
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: 'cyan', width: 2 },
                    visible: document.getElementById('check-heap').checked
                }
            ];
        }

        // Create layout
        function createLayout() {
            const isFixed = document.getElementById('radio-range-fixed').checked;
            const isLinear = document.getElementById('radio-scale-linear').checked;

            const layout = {
                title: 'time complexity',
                xaxis: {
                    title: 'number of elements',
                    autorange: !isFixed,
                    range: isFixed ? [0, 20] : undefined
                },
                yaxis: {
                    title: 'operations',
                    type: isLinear ? 'linear' : 'log',
                    autorange: !isFixed,
                    range: isFixed ? [0, 500] : undefined
                },
                legend: {
                    orientation: 'h',
                    y: -0.2,
                    x: 0.5,
                    xanchor: 'center'
                }
            };

            return layout;
        }

        // Update plot
        function updatePlot() {
            const maxN = parseInt(document.getElementById('slider-elements').value);
            const traces = createTraces(maxN);
            const layout = createLayout();

            Plotly.react('plot-container', traces, layout);
        }

        // Initialize plot
        function initializePlot() {
            const maxN = parseInt(document.getElementById('slider-elements').value);
            const traces = createTraces(maxN);
            const layout = createLayout();

            Plotly.newPlot('plot-container', traces, layout);
        }

        // Event listeners
        document.getElementById('slider-elements').addEventListener('input', function() {
            document.getElementById('display-elements').textContent = '+ ' + this.value;
            updatePlot();
        });

        document.getElementById('radio-scale-linear').addEventListener('change', updatePlot);
        document.getElementById('radio-scale-logarithmic').addEventListener('change', updatePlot);
        document.getElementById('radio-range-fixed').addEventListener('change', updatePlot);
        document.getElementById('radio-range-automatic').addEventListener('change', updatePlot);

        // Algorithm checkbox listeners
        const checkboxes = ['bubble', 'selection', 'insertion', 'merge', 'quick', 'heap', 'bogo'];
        checkboxes.forEach(algo => {
            document.getElementById('check-' + algo).addEventListener('change', function() {
                const traces = document.getElementById('plot-container').data;

                if (algo === 'bubble') traces[0].visible = this.checked;
                else if (algo === 'selection') traces[1].visible = this.checked;
                else if (algo === 'insertion' || algo === 'bogo') {
                    traces[2].visible = document.getElementById('check-insertion').checked || 
                                       document.getElementById('check-bogo').checked;
                }
                else if (algo === 'merge') traces[3].visible = this.checked;
                else if (algo === 'quick') traces[4].visible = this.checked;
                else if (algo === 'heap') traces[5].visible = this.checked;

                updatePlot();
            });
        });

        // Initialize on load
        window.addEventListener('load', function() {
            // Configure MathJax
            window.MathJax = {
                tex: {
                    inlineMath: [['\\(', '\\)']],
                    displayMath: [['\\[', '\\]']]
                }
            };

            // Initialize plot
            initializePlot();
        });
    </script>
</body>
</html>
