    <title>Reflux Ratio vs Pinch Composition</title>
    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
    <style>
        body {
            margin: 0;
            padding: 20px;
            font-family: Arial, sans-serif;
            background-color: #ffffff;
        }

        #main-container {
            width: 550px;
            margin: 0 auto;
            background-color: #f0f0f0;
            padding: 20px;
            border: 1px solid #ccc;
            border-radius: 4px;
        }

        #control-panel {
            display: flex;
            align-items: center;
            margin-bottom: 20px;
            gap: 10px;
        }

        #control-panel label {
            flex-shrink: 0;
        }

        #slider-xD {
            flex-grow: 1;
            height: 5px;
        }

        #label-xD {
            flex-shrink: 0;
            min-width: 40px;
            text-align: right;
            font-weight: bold;
        }

        #plot {
            border: 1px solid #ccc;
            background-color: white;
        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <label for="slider-xD">distillate composition</label>
            <input type="range" id="slider-xD" min="0.80" max="1.00" step="0.01" value="0.84">
            <span id="label-xD">0.84</span>
        </div>
        <div id="plot"></div>
    </div>

    <script>
        // Constants
        const a = 9;
        const b = -0.6;

        // VLE Function
        function y(x) {
            return (a * x) / (1 + (a - 1) * x + b * x * (1 - x));
        }

        // Reflux Ratio Function
        function r(x, xD) {
            const yVal = y(x);
            return (xD - yVal) / (yVal - x);
        }

        // Main update function
        function updatePlot(xD) {
            // Data generation for curve
            const x_vals = [];
            const r_vals = [];

            for (let x = 0.001; x <= xD; x += 0.001) {
                const rVal = r(x, xD);
                // Only include positive, finite values up to 5
                if (rVal >= 0 && rVal < 5 && isFinite(rVal)) {
                    x_vals.push(x);
                    r_vals.push(rVal);
                }
            }

            // Tangent pinch point calculation
            const pinch_x = [];
            const pinch_y = [];

            // Search for local maximum
            for (let i = 1; i < r_vals.length - 1; i++) {
                if (r_vals[i-1] < r_vals[i] && r_vals[i] > r_vals[i+1]) {
                    pinch_x.push(x_vals[i]);
                    pinch_y.push(r_vals[i]);
                    break; // Only take the first maximum found
                }
            }

            // Define traces
            const traces = [
                {
                    x: x_vals,
                    y: r_vals,
                    mode: 'lines',
                    type: 'scatter',
                    line: {
                        color: '#8B0000',
                        width: 2.5
                    },
                    showlegend: false
                },
                {
                    x: pinch_x,
                    y: pinch_y,
                    mode: 'markers',
                    type: 'scatter',
                    marker: {
                        color: 'royalblue',
                        size: 10
                    },
                    showlegend: false
                }
            ];

            // Define layout
            const layout = {
                xaxis: {
                    range: [0, 1.0],
                    gridcolor: '#e0e0e0',
                    dtick: 0.2,
                    zeroline: true,
                    zerolinecolor: '#808080'
                },
                yaxis: {
                    range: [0, 3.0],
                    gridcolor: '#e0e0e0',
                    dtick: 0.5,
                    zeroline: true,
                    zerolinecolor: '#808080'
                },
                showlegend: false,
                margin: {l: 40, r: 20, b: 40, t: 20},
                plot_bgcolor: 'white',
                paper_bgcolor: 'white'
            };

            // Update plot
            Plotly.react('plot', traces, layout);
        }

        // Initialize on load
        document.addEventListener('DOMContentLoaded', function() {
            const slider = document.getElementById('slider-xD');
            const label = document.getElementById('label-xD');

            // Initial plot
            updatePlot(parseFloat(slider.value));

            // Slider event handler
            slider.addEventListener('input', function() {
                const value = parseFloat(this.value);
                label.textContent = value.toFixed(2);
                updatePlot(value);
            });
        });
    </script>
</body>
</html>
