    <script>
        let sceneObjects = [];
        let selectedTool = null;
        let activeObject = null;
        let interactionMode = null;
        let mouseOffset = {x: 0, y: 0};
        let initialAngle = 0;
        let canvasWidth = 800;
        let canvasHeight = 600;

        function setup() {
            let canvas = createCanvas(canvasWidth, canvasHeight);
            canvas.parent('drawing-canvas');
            textAlign(CENTER, CENTER);
        }

        function draw() {
            background(255);

            // Draw all scene objects
            for (let obj of sceneObjects) {
                push();
                translate(obj.x, obj.y);
                rotate(obj.rotation);

                if (obj.type === 'atom' || obj.type === 'charge') {
                    textSize(16);
                    fill(0);
                    text(obj.value, 0, 0);
                } else if (obj.type === 'structure') {
                    drawStructure(obj.value);
                }

                pop();
            }

            // Draw preview if tool is selected
            if (selectedTool && mouseX >= 0 && mouseX <= width && mouseY >= 0 && mouseY <= height) {
                push();
                translate(mouseX, mouseY);
                textSize(16);
                fill(0, 0, 0, 128);
                text(selectedTool.value, 0, 0);
                pop();
            }
        }

        function drawStructure(structureType) {
            stroke(0);
            strokeWeight(2);
            fill(0);
            textSize(14);

            switch(structureType) {
                case 'bond':
                    line(-20, 0, 20, 0);
                    break;
                case 'water':
                    // Draw O
                    text('O', 0, 0);
                    // Draw H atoms
                    text('H', -30, -20);
                    text('H', 30, -20);
                    // Draw bonds
                    line(-5, -5, -25, -15);
                    line(5, -5, 25, -15);
                    break;
                case 'cyclopropyl_ethane':
                    // Draw cyclopropyl ring
                    noFill();
                    triangle(-20, -10, 20, -10, 0, 20);
                    // Draw ethane connection
                    line(0, 20, 0, 40);
                    line(0, 40, 20, 50);
                    // Add C labels
                    fill(0);
                    text('C', -20, -10);
                    text('C', 20, -10);
                    text('C', 0, 20);
                    text('C', 0, 40);
                    text('C', 20, 50);
                    break;
                case 'borazine':
                    // Draw hexagon
                    noFill();
                    beginShape();
                    for (let i = 0; i < 6; i++) {
                        let angle = TWO_PI / 6 * i - PI/2;
                        let x = cos(angle) * 30;
                        let y = sin(angle) * 30;
                        vertex(x, y);
                    }
                    endShape(CLOSE);
                    // Add B and N labels alternating
                    fill(0);
                    for (let i = 0; i < 6; i++) {
                        let angle = TWO_PI / 6 * i - PI/2;
                        let x = cos(angle) * 30;
                        let y = sin(angle) * 30;
                        text(i % 2 === 0 ? 'B' : 'N', x, y);
                    }
                    break;
                case 'isopropylamine':
                    // Draw isopropyl group
                    line(0, 0, -20, -20);
                    line(0, 0, 20, -20);
                    line(0, 0, 0, 20);
                    // Add labels
                    text('C', 0, 0);
                    text('C', -20, -20);
                    text('C', 20, -20);
                    text('N', 0, 20);
                    text('H', -10, 35);
                    text('H', 10, 35);
                    break;
            }
        }

        function mousePressed() {
            if (mouseX >= 0 && mouseX <= width && mouseY >= 0 && mouseY <= height) {
                if (selectedTool) {
                    // Add new object
                    sceneObjects.push({
                        type: selectedTool.type,
                        value: selectedTool.value,
                        x: mouseX,
                        y: mouseY,
                        rotation: 0
                    });

                    // Deselect tool
                    document.querySelector('.selected')?.classList.remove('selected');
                    selectedTool = null;
                } else {
                    // Check for object interaction
                    for (let i = sceneObjects.length - 1; i >= 0; i--) {
                        let obj = sceneObjects[i];
                        let dx = mouseX - obj.x;
                        let dy = mouseY - obj.y;
                        let dist = sqrt(dx * dx + dy * dy);

                        // Simple hit detection (within 40 pixels)
                        if (dist < 40) {
                            activeObject = obj;

                            if (dist < 25) {
                                interactionMode = 'rotate';
                                initialAngle = atan2(dy, dx) - obj.rotation;
                            } else {
                                interactionMode = 'translate';
                                mouseOffset.x = mouseX - obj.x;
                                mouseOffset.y = mouseY - obj.y;
                            }
                            break;
                        }
                    }
                }
            }
        }

        function mouseDragged() {
            if (activeObject && interactionMode) {
                if (interactionMode === 'translate') {
                    activeObject.x = mouseX - mouseOffset.x;
                    activeObject.y = mouseY - mouseOffset.y;
                } else if (interactionMode === 'rotate') {
                    let dx = mouseX - activeObject.x;
                    let dy = mouseY - activeObject.y;
                    activeObject.rotation = atan2(dy, dx) - initialAngle;
                }
            }
        }

        function mouseReleased() {
            activeObject = null;
            interactionMode = null;
        }

        // Toolbar button handlers
        document.querySelectorAll('#toolbar button').forEach(button => {
            button.addEventListener('click', function() {
                // Remove previous selection
                document.querySelector('.selected')?.classList.remove('selected');

                // Add selection to this button
                this.classList.add('selected');

                // Parse button ID to get type and value
                let id = this.id;
                let value = this.textContent;
                let type;

                if (id.includes('charge')) {
                    type = 'charge';
                } else if (id.includes('element')) {
                    type = 'atom';
                }

                selectedTool = {type, value};
            });
        });

        // Undo button handler
        document.getElementById('btn-undo').addEventListener('click', function() {
            if (sceneObjects.length > 0) {
                sceneObjects.pop();
            }
        });

        // Add button handler
        document.getElementById('btn-add').addEventListener('click', function() {
            let select = document.getElementById('select-structure');
            let selectedValue = select.value;

            sceneObjects.push({
                type: 'structure',
                value: selectedValue,
                x: canvasWidth / 2,
                y: canvasHeight / 2,
                rotation: 0
            });
        });
    </script>
</body>
</html>
