                <option value="-1.5707963267948966" selected>-π/2</option>
                <option value="-1.0471975511965976">-π/3</option>
                <option value="0">0</option>
                <option value="1.0471975511965976">π/3</option>
                <option value="1.5707963267948966">π/2</option>
                <option value="2.0943951023931953">2π/3</option>
                <option value="3.141592653589793">π</option>
            </select>
        </div>
    </div>

    <div id="formula-display">\(y(x,t) = -1.50 \sin(1.40x - 1.88t)\)</div>

    <div id="canvas-container"></div>

    <script>
        // MathJax configuration
        window.MathJax = {
            tex: {
                inlineMath: [['\\(', '\\)']],
                displayMath: [['\\[', '\\]']]
            }
        };

        // Global variables for wave parameters
        let amplitude = 1.5;
        let frequency = 0.3;
        let wavelength = 4.5;
        let time = 2.5;
        let phase = -Math.PI/2;
        let k, omega;

        // p5.js sketch
        function setup() {
            let canvas = createCanvas(800, 400);
            canvas.parent('canvas-container');
            updateWaveParameters();
        }

        function draw() {
            background(255);

            // Set up coordinate system
            push();
            translate(50, height/2);
            scale(1, -1);

            const pixelsPerUnit = 40;

            // Draw grid
            strokeWeight(0.5);
            stroke(200, 255, 200);
            for (let x = 1; x <= 16; x++) {
                line(x * pixelsPerUnit, -150, x * pixelsPerUnit, 150);
            }

            // Draw axes
            strokeWeight(2);
            stroke(0);
            line(0, 0, 700, 0); // x-axis
            line(0, -150, 0, 150); // y-axis

            // Draw axis arrows
            fill(0);
            noStroke();
            triangle(700, 0, 690, 5, 690, -5); // x-axis arrow
            triangle(0, 150, -5, 140, 5, 140); // y-axis arrow

            // Draw axis labels
            push();
            scale(1, -1);
            textAlign(CENTER);
            text('x', 700, 20);
            text('y', -20, -140);
            pop();

            // Draw tick marks and labels
            stroke(0);
            strokeWeight(1);
            for (let x = 1; x <= 16; x++) {
                line(x * pixelsPerUnit, -5, x * pixelsPerUnit, 5);
                push();
                scale(1, -1);
                textAlign(CENTER);
                text(x, x * pixelsPerUnit, 20);
                pop();
            }

            // Draw wave
            strokeWeight(2);
            stroke(0, 0, 255);
            noFill();
            beginShape();
            for (let px = 0; px < 700; px++) {
                let x = px / pixelsPerUnit;
                let y = amplitude * Math.cos(omega * time - k * x + phase);
                vertex(px, y * pixelsPerUnit);
            }
            endShape();

            // Draw points and vectors
            for (let x = 1; x <= 16; x++) {
                let y = amplitude * Math.cos(omega * time - k * x + phase);
                let v = -amplitude * omega * Math.sin(omega * time - k * x + phase);
                let a = -amplitude * omega * omega * Math.cos(omega * time - k * x + phase);

                // Draw point
                fill(0);
                noStroke();
                circle(x * pixelsPerUnit, y * pixelsPerUnit, 6);

                // Draw velocity vector
                drawArrow(x * pixelsPerUnit, y * pixelsPerUnit, v * 15, color(255, 0, 0), 'v');

                // Draw acceleration vector
                drawArrow(x * pixelsPerUnit, y * pixelsPerUnit, a * 5, color(0, 0, 0), 'a');
            }

            pop();
        }

        function drawArrow(baseX, baseY, vectorY, arrowColor, label) {
            push();
            stroke(arrowColor);
            strokeWeight(1.5);

            if (Math.abs(vectorY) > 0.1) {
                line(baseX, baseY, baseX, baseY + vectorY);

                // Draw arrowhead
                fill(arrowColor);
                noStroke();
                if (vectorY > 0) {
                    triangle(baseX, baseY + vectorY, baseX - 3, baseY + vectorY - 6, baseX + 3, baseY + vectorY - 6);
                } else {
                    triangle(baseX, baseY + vectorY, baseX - 3, baseY + vectorY + 6, baseX + 3, baseY + vectorY + 6);
                }

                // Draw label
                push();
                scale(1, -1);
                textAlign(CENTER);
                fill(arrowColor);
                text(label, baseX + 10, -(baseY + vectorY/2));
                pop();
            }

            pop();
        }

        function updateWaveParameters() {
            k = 2 * Math.PI / wavelength;
            omega = 2 * Math.PI * frequency;
            updateFormula();
        }

        function updateFormula() {
            let formulaStr = '';
            const A = amplitude.toFixed(2);
            const kStr = k.toFixed(2);
            const omegaStr = omega.toFixed(2);

            if (Math.abs(phase - Math.PI/2) < 0.01) {
                formulaStr = `y(x,t) = ${A} \\sin(${kStr}x - ${omegaStr}t)`;
            } else if (Math.abs(phase + Math.PI/2) < 0.01) {
                formulaStr = `y(x,t) = -${A} \\sin(${kStr}x - ${omegaStr}t)`;
            } else if (Math.abs(phase - Math.PI) < 0.01) {
                formulaStr = `y(x,t) = -${A} \\cos(${kStr}x - ${omegaStr}t)`;
            } else if (Math.abs(phase) < 0.01) {
                formulaStr = `y(x,t) = ${A} \\cos(${kStr}x - ${omegaStr}t)`;
            } else {
                const phiStr = phase.toFixed(2);
                formulaStr = `y(x,t) = ${A} \\cos(${omegaStr}t - ${kStr}x + ${phiStr})`;
            }

            document.getElementById('formula-display').innerHTML = '\\(' + formulaStr + '\\)';
            MathJax.typesetPromise();
        }

        // Event listeners
        document.getElementById('slider-amplitude').addEventListener('input', function(e) {
            amplitude = parseFloat(e.target.value);
            document.getElementById('value-amplitude').textContent = amplitude.toFixed(2);
            updateWaveParameters();
        });

        document.getElementById('slider-frequency').addEventListener('input', function(e) {
            frequency = parseFloat(e.target.value);
            document.getElementById('value-frequency').textContent = frequency.toFixed(2);
            updateWaveParameters();
        });

        document.getElementById('slider-wavelength').addEventListener('input', function(e) {
            wavelength = parseFloat(e.target.value);
            document.getElementById('value-wavelength').textContent = wavelength.toFixed(2);
            updateWaveParameters();
        });

        document.getElementById('slider-time').addEventListener('input', function(e) {
            time = parseFloat(e.target.value);
            document.getElementById('value-time').textContent = time.toFixed(2);
            updateFormula();
        });

        document.getElementById('select-phase').addEventListener('change', function(e) {
            phase = parseFloat(e.target.value);
            updateWaveParameters();
        });
    </script>
</body>
</html>
