    <script>
        // MathJax configuration
        window.MathJax = {
            tex: {
                inlineMath: [['\\(', '\\)']],
                displayMath: [['\\[', '\\]']]
            }
        };

        let epsilon = 0;
        let canvasWidth = 700;
        let canvasHeight = 500;
        let originX, originY;
        let pixelsPerUnit = 30;

        function setup() {
            let canvas = createCanvas(canvasWidth, canvasHeight);
            canvas.parent('canvas-container');

            originX = width * 0.7;
            originY = height / 2;

            // Setup slider event
            document.getElementById('slider-eccentricity').addEventListener('input', function() {
                epsilon = parseFloat(this.value);
                document.getElementById('value-eccentricity').textContent = epsilon.toFixed(1);
                updateEnergyDisplay();
                redraw();
            });

            noLoop();
            updateEnergyDisplay();
        }

        function draw() {
            background(240);

            // Set up coordinate system
            push();
            translate(originX, originY);
            scale(1, -1);

            // Draw axes
            stroke(0);
            strokeWeight(1);
            line(-originX, 0, width - originX, 0); // x-axis
            line(0, -originY, 0, originY); // y-axis

            // Reset scale for text
            push();
            scale(1, -1);

            // Draw axis labels
            fill(0);
            noStroke();
            textAlign(CENTER, TOP);
            text("Global'x", (width - originX) / 2, 10);

            textAlign(RIGHT, CENTER);
            text("Global'y", -10, -height/2 + 20);

            // Draw tick marks and labels
            for (let i = -15; i <= 5; i++) {
                if (i !== 0) {
                    let x = i * pixelsPerUnit;
                    stroke(0);
                    strokeWeight(1);
                    line(x, -5, x, 5);

                    noStroke();
                    textAlign(CENTER, TOP);
                    text(i, x, 10);
                }
            }

            for (let i = -7; i <= 7; i++) {
                if (i !== 0) {
                    let y = i * pixelsPerUnit;
                    stroke(0);
                    strokeWeight(1);
                    line(-5, -y, 5, -y);

                    noStroke();
                    textAlign(RIGHT, CENTER);
                    text(i, -10, -y);
                }
            }
            pop();

            // Draw central body
            fill(0);
            noStroke();
            circle(0, 0, 6);

            // Draw orbit
            stroke(74, 128, 181);
            strokeWeight(2);
            noFill();

            beginShape();

            let p = 2; // semi-latus rectum

            if (epsilon === 0) {
                // Circle special case
                for (let theta = 0; theta <= TWO_PI; theta += 0.01) {
                    let x = (2 * cos(theta) - 2) * pixelsPerUnit;
                    let y = 2 * sin(theta) * pixelsPerUnit;
                    vertex(x, y);
                }
            } else if (epsilon < 1) {
                // Ellipse
                for (let theta = 0; theta <= TWO_PI; theta += 0.01) {
                    let r = p / (1 + epsilon * cos(theta));
                    let x = r * cos(theta) * pixelsPerUnit;
                    let y = r * sin(theta) * pixelsPerUnit;
                    vertex(x, y);
                }
            } else if (epsilon === 1) {
                // Parabola
                for (let theta = -2.2; theta <= 2.2; theta += 0.01) {
                    let r = p / (1 + epsilon * cos(theta));
                    let x = r * cos(theta) * pixelsPerUnit;
                    let y = r * sin(theta) * pixelsPerUnit;
                    vertex(x, y);
                }
            } else {
                // Hyperbola
                let maxTheta = acos(-1/epsilon);
                for (let theta = -maxTheta + 0.01; theta <= maxTheta - 0.01; theta += 0.01) {
                    let r = p / (1 + epsilon * cos(theta));
                    let x = r * cos(theta) * pixelsPerUnit;
                    let y = r * sin(theta) * pixelsPerUnit;
                    vertex(x, y);
                }
            }

            endShape();

            pop();
        }

        function updateEnergyDisplay() {
            let energyValue = document.getElementById('energy-value');

            if (epsilon === 0) {
                energyValue.innerHTML = '\\(-\\frac{1}{4}\\)';
                MathJax.typeset([energyValue]);
            } else {
                let E = -0.25 * (1 - epsilon * epsilon);
                energyValue.innerHTML = E.toFixed(2);
            }
        }

        // Initial render
        window.addEventListener('load', function() {
            setTimeout(function() {
                redraw();
            }, 100);
        });
    </script>
</body>
</html>
