    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/mathjs/11.11.0/math.min.js"></script>
    <script>
        // Global state
        const N = 64;
        const solutions = new Set();
        const conflicts = new Array(N);
        let currentAlgorithm = 'heuristic';
        let currentPlotMode = 'conflicts';

        // Initialize problem instance
        function initProblem() {
            // Clear and add two random solutions
            solutions.clear();
            while (solutions.size < 2) {
                solutions.add(Math.floor(Math.random() * N));
            }

            // Calculate conflicts for each state
            for (let i = 0; i < N; i++) {
                if (solutions.has(i)) {
                    conflicts[i] = 0;
                } else {
                    conflicts[i] = calculateMinHammingDistance(i);
                }
            }
        }

        // Calculate Hamming distance to nearest solution
        function calculateMinHammingDistance(state) {
            let minDist = Infinity;
            for (let sol of solutions) {
                let dist = 0;
                let xor = state ^ sol;
                while (xor) {
                    dist += xor & 1;
                    xor >>= 1;
                }
                minDist = Math.min(minDist, dist);
            }
            return minDist;
        }

        // Algorithm implementations
        function computeGroverState(steps) {
            const state = math.complex(new Array(N).fill(1/Math.sqrt(N)));

            if (steps === 0) return state;

            // Oracle matrix U_w
            const oracle = math.diag(new Array(N).fill(1).map((v, i) => solutions.has(i) ? -1 : 1));

            // Diffusion operator U_s = 2*P_0 - I
            const P0 = math.multiply(1/N, math.ones(N, N));
            const I = math.identity(N);
            const diffusion = math.subtract(math.multiply(2, P0), I);

            // Grover operator G = U_s * U_w
            const grover = math.multiply(diffusion, oracle);

            // Apply Grover operator k times
            let current = state;
            for (let k = 0; k < steps; k++) {
                current = math.multiply(grover, current);
            }

            return current;
        }

        function computeAdiabaticState(s) {
            // H(s) = (1-s)*H_0 + s*H_p
            const P0 = math.multiply(1/N, math.ones(N, N));
            const H0 = math.multiply(-1, P0);
            const Hp = math.diag(conflicts);

            const H = math.add(
                math.multiply(1-s, H0),
                math.multiply(s, Hp)
            );

            // Find ground state (eigenvector with smallest eigenvalue)
            const result = math.eigs(H);
            let minIdx = 0;
            let minVal = result.values[0];
            for (let i = 1; i < result.values.length; i++) {
                if (result.values[i] < minVal) {
                    minVal = result.values[i];
                    minIdx = i;
                }
            }

            return result.eigenvectors.column(minIdx);
        }

        function computeDiscreteAdiabaticState(currentStep, totalSteps) {
            let state = math.complex(new Array(N).fill(1/Math.sqrt(N)));

            for (let k = 0; k < currentStep; k++) {
                const s = k / totalSteps;

                // H(s) = (1-s)*H_0 + s*H_p
                const P0 = math.multiply(1/N, math.ones(N, N));
                const H0 = math.multiply(-1, P0);
                const Hp = math.diag(conflicts);

                const H = math.add(
                    math.multiply(1-s, H0),
                    math.multiply(s, Hp)
                );

                // U_k = exp(-i * H)
                const iH = math.multiply(math.complex(0, -1), H);
                const U = math.expm(iH);

                state = math.multiply(U, state);
            }

            return state;
        }

        // Main update function
        function update() {
            const totalSteps = parseInt(document.getElementById('slider-total-steps').value);
            const currentStep = parseInt(document.getElementById('slider-current-step').value);
            const s = currentStep / totalSteps;

            // Compute quantum state
            let state;
            switch (currentAlgorithm) {
                case 'unstructured':
                    state = computeGroverState(currentStep);
                    break;
                case 'adiabatic':
                    state = computeAdiabaticState(s);
                    break;
                case 'discrete-adiabatic':
                    state = computeDiscreteAdiabaticState(currentStep, totalSteps);
                    break;
                case 'heuristic':
                    state = computeDiscreteAdiabaticState(currentStep, totalSteps);
                    break;
            }

            // Update plots
            updateAmplitudePlot(state, currentStep);
            updateProbabilityPlot(state, currentStep);
        }

        function updateAmplitudePlot(state, currentStep) {
            const real = [];
            const imag = [];
            const colors = [];
            const sizes = [];
            const texts = [];

            // Normalize conflicts for coloring
            const maxConflict = Math.max(...conflicts.filter((_, i) => !solutions.has(i)));

            for (let i = 0; i < N; i++) {
                const amp = math.complex(state[i]);
                real.push(amp.re);
                imag.push(amp.im);
                texts.push(`State ${i}<br>Conflicts: ${conflicts[i]}`);

                if (solutions.has(i)) {
                    colors.push('black');
                    sizes.push(15);
                } else {
                    // Color based on conflict number
                    const normalizedConflict = conflicts[i] / maxConflict;
                    const r = Math.floor(255 * normalizedConflict);
                    const b = Math.floor(255 * (1 - normalizedConflict));
                    colors.push(`rgb(${r}, 100, ${b})`);
                    sizes.push(8);

                    // Mark one non-solution as red at step 0
                    if (currentStep === 0 && i === 0 && !solutions.has(i)) {
                        colors[i] = 'red';
                    }
                }
            }

            const data = [{
                x: real,
                y: imag,
                mode: 'markers',
                type: 'scattergl',
                marker: {
                    color: colors,
                    size: sizes
                },
                text: texts,
                hoverinfo: 'text'
            }];

            const maxVal = Math.max(...real.map(Math.abs), ...imag.map(Math.abs)) * 1.1;

            const layout = {
                xaxis: { title: 'Re', range: [-maxVal, maxVal] },
                yaxis: { title: 'Im', range: [-maxVal, maxVal] },
                margin: { t: 20 }
            };

            Plotly.newPlot('plot-amplitudes', data, layout);
        }

        function updateProbabilityPlot(state, currentStep) {
            let xData, yData, xLabel;

            if (currentPlotMode === 'conflicts' || currentAlgorithm !== 'unstructured') {
                // Group by conflict number
                const probByConflict = {};
                for (let i = 0; i < N; i++) {
                    const prob = math.abs(math.complex(state[i])) ** 2;
                    const c = conflicts[i];
                    probByConflict[c] = (probByConflict[c] || 0) + prob;
                }

                xData = Object.keys(probByConflict).map(Number).sort((a, b) => a - b);
                yData = xData.map(c => probByConflict[c]);
                xLabel = currentPlotMode === 'conflicts' ? 'number of conflicts' : 'eigenvalue';
            } else {
                // Unstructured eigenvalue mode
                xLabel = 'arg(eigenvalue)';

                // For Grover, we'll show the two eigenvalues of the Grover operator
                // This is a simplified visualization
                const angle1 = 2 * Math.asin(2 * Math.sqrt(solutions.size * (N - solutions.size)) / N);
                const angle2 = -angle1;

                // Project current state onto eigenvectors (simplified)
                const prob1 = 0.5 + 0.5 * Math.cos(currentStep * angle1);
                const prob2 = 1 - prob1;

                xData = [angle1, angle2];
                yData = [prob1, prob2];
            }

            const data = [{
                x: xData,
                y: yData,
                type: 'bar',
                marker: { color: 'blue' },
                width: 0.01
            }];

            const layout = {
                xaxis: { title: xLabel },
                yaxis: { title: 'probability', range: [0, 1] },
                margin: { t: 20 },
                bargap: 0
            };

            Plotly.newPlot('plot-probability', data, layout);
        }

        // Event handlers
        document.getElementById('btn-unstructured').addEventListener('click', () => selectAlgorithm('unstructured', 5));
        document.getElementById('btn-adiabatic').addEventListener('click', () => selectAlgorithm('adiabatic', 200));
        document.getElementById('btn-discrete-adiabatic').addEventListener('click', () => selectAlgorithm('discrete-adiabatic', 200));
        document.getElementById('btn-heuristic').addEventListener('click', () => selectAlgorithm('heuristic', 10));

        function selectAlgorithm(algorithm, defaultSteps) {
            currentAlgorithm = algorithm;

            // Update button styles
            document.querySelectorAll('.control-group button').forEach(btn => {
                if (btn.id.startsWith('btn-') && btn.id !== 'btn-conflicts' && btn.id !== 'btn-eigenvalues') {
                    btn.classList.remove('active');
                }
            });
            document.getElementById(`btn-${algorithm.replace('-', '-')}`).classList.add('active');

            // Update sliders
            document.getElementById('slider-total-steps').value = defaultSteps;
            document.getElementById('display-total-steps').textContent = defaultSteps;
            document.getElementById('slider-current-step').max = defaultSteps;
            document.getElementById('slider-current-step').value = 0;
            document.getElementById('display-current-step').textContent = 0;
            document.getElementById('current-step-label').textContent = `current step (0 to ${defaultSteps})`;

            update();
        }

        document.getElementById('slider-total-steps').addEventListener('input', (e) => {
            const value = e.target.value;
            document.getElementById('display-total-steps').textContent = value;
            document.getElementById('slider-current-step').max = value;

            const currentStep = parseInt(document.getElementById('slider-current-step').value);
            if (currentStep > value) {
                document.getElementById('slider-current-step').value = value;
                document.getElementById('display-current-step').textContent = value;
            }

            document.getElementById('current-step-label').textContent = `current step (0 to ${value})`;
            update();
        });

        document.getElementById('slider-current-step').addEventListener('input', (e) => {
            document.getElementById('display-current-step').textContent = e.target.value;
            update();
        });

        document.getElementById('btn-conflicts').addEventListener('click', () => {
            currentPlotMode = 'conflicts';
            document.getElementById('btn-conflicts').classList.add('active');
            document.getElementById('btn-eigenvalues').classList.remove('active');
            update();
        });

        document.getElementById('btn-eigenvalues').addEventListener('click', () => {
            currentPlotMode = 'eigenvalues';
            document.getElementById('btn-eigenvalues').classList.add('active');
            document.getElementById('btn-conflicts').classList.remove('active');
            update();
        });

        // Initialize
        initProblem();
        update();
    </script>
</body>
</html>
