</head>
<body>
    <div id="main-container">
        <div id="controls-container">
            <div id="control-group-pressure">
                <label for="slider-pressure">pressure (bar)</label>
                <input type="range" id="slider-pressure" min="0.01" max="0.20" step="0.005" value="0.08">
                <span id="value-pressure">0.08</span>
            </div>
            <div id="control-group-temperature" style="display: none;">
                <label for="slider-temperature">temperature (K)</label>
                <input type="range" id="slider-temperature" min="450" max="490" step="1" value="475">
                <span id="value-temperature">475</span>
            </div>
            <select id="select-plot">
                <option value="vs_temp">fugacity versus temperature</option>
                <option value="vs_press">fugacity versus pressure</option>
            </select>
            <div id="checkbox-container" style="display: none;">
                <input type="checkbox" id="checkbox-high-pressure" checked>
                <label for="checkbox-high-pressure">high pressure</label>
            </div>
        </div>
        <div id="plot-container"></div>
    </div>

    <script>
        // Get DOM elements
        const selectPlot = document.getElementById('select-plot');
        const sliderPressure = document.getElementById('slider-pressure');
        const valuePressure = document.getElementById('value-pressure');
        const sliderTemperature = document.getElementById('slider-temperature');
        const valueTemperature = document.getElementById('value-temperature');
        const checkboxHighPressure = document.getElementById('checkbox-high-pressure');
        const controlGroupPressure = document.getElementById('control-group-pressure');
        const controlGroupTemperature = document.getElementById('control-group-temperature');
        const checkboxContainer = document.getElementById('checkbox-container');
        const plotContainer = document.getElementById('plot-container');

        // Update value displays
        sliderPressure.addEventListener('input', function() {
            valuePressure.textContent = parseFloat(this.value).toFixed(2);
            updatePlot();
        });

        sliderTemperature.addEventListener('input', function() {
            valueTemperature.textContent = this.value;
            updatePlot();
        });

        // Handle plot type change
        selectPlot.addEventListener('change', function() {
            if (this.value === 'vs_temp') {
                controlGroupPressure.style.display = 'block';
                controlGroupTemperature.style.display = 'none';
                checkboxContainer.style.display = 'none';
            } else {
                controlGroupPressure.style.display = 'none';
                controlGroupTemperature.style.display = 'block';
                checkboxContainer.style.display = 'block';
            }
            updatePlot();
        });

        // Handle checkbox change
        checkboxHighPressure.addEventListener('change', updatePlot);

        // Main update function
        function updatePlot() {
            if (selectPlot.value === 'vs_temp') {
                plotFugacityVsTemperature();
            } else {
                plotFugacityVsPressure();
            }
        }

        function plotFugacityVsTemperature() {
            const P = parseFloat(sliderPressure.value);

            // Calculate saturation temperature
            const Tsat_C = 5818 / (8.65 - Math.log10(P)) - 230;

            // Generate temperature points
            const temps = [];
            for (let T = 280; T <= 400; T += 1) {
                temps.push(T);
            }

            // Calculate fugacities
            const f_sat = temps.map(T => Math.pow(10, 8.65 - 5818 / (230 + T)));

            // Create traces
            const traces = [];

            // Stable liquid
            traces.push({
                x: temps.filter(T => T <= Tsat_C),
                y: f_sat.filter((f, i) => temps[i] <= Tsat_C),
                mode: 'lines',
                line: { color: '#0000FF', width: 2 },
                name: 'Stable Liquid',
                showlegend: false
            });

            // Stable vapor
            traces.push({
                x: temps.filter(T => T >= Tsat_C),
                y: temps.filter(T => T >= Tsat_C).map(() => P),
                mode: 'lines',
                line: { color: '#0000FF', width: 2 },
                name: 'Stable Vapor',
                showlegend: false
            });

            // Metastable liquid
            traces.push({
                x: temps.filter(T => T > Tsat_C),
                y: f_sat.filter((f, i) => temps[i] > Tsat_C),
                mode: 'lines',
                line: { color: '#A0A0FF', width: 2, dash: 'dash' },
                name: 'Metastable Liquid',
                showlegend: false
            });

            // Metastable vapor
            traces.push({
                x: temps.filter(T => T < Tsat_C),
                y: temps.filter(T => T < Tsat_C).map(() => P),
                mode: 'lines',
                line: { color: '#A0A0FF', width: 2, dash: 'dash' },
                name: 'Metastable Vapor',
                showlegend: false
            });

            // Saturation point
            traces.push({
                x: [Tsat_C],
                y: [P],
                mode: 'markers',
                marker: { color: '#000000', size: 8 },
                name: 'Saturation',
                showlegend: false
            });

            // Indicator line
            traces.push({
                x: [Tsat_C, Tsat_C],
                y: [0, P],
                mode: 'lines',
                line: { color: '#000000', width: 1, dash: 'dash' },
                name: 'Indicator',
                showlegend: false
            });

            // Layout
            const layout = {
                title: 'Fugacity versus Temperature',
                xaxis: {
                    title: 'temperature (°C)',
                    range: [280, 400],
                    gridcolor: '#e0e0e0'
                },
                yaxis: {
                    title: 'fugacity (bar)',
                    range: [0, 0.22],
                    gridcolor: '#e0e0e0'
                },
                paper_bgcolor: 'white',
                plot_bgcolor: 'white',
                font: {
                    family: 'sans-serif',
                    size: 14
                },
                annotations: [
                    {
                        x: 310,
                        y: 0.01,
                        text: 'liquid',
                        showarrow: false,
                        font: { size: 12 }
                    },
                    {
                        x: 390,
                        y: P,
                        text: 'vapor',
                        showarrow: false,
                        font: { size: 12 }
                    },
                    {
                        x: Tsat_C,
                        y: P + 0.01,
                        text: 'T<sup>sat</sup>',
                        showarrow: false,
                        font: { size: 12 }
                    }
                ]
            };

            Plotly.react(plotContainer, traces, layout);
        }

        function plotFugacityVsPressure() {
            const T = parseFloat(sliderTemperature.value);
            const useRealGas = checkboxHighPressure.checked;

            // Calculate saturation pressure (in MPa)
            const Psat = Math.pow(10, 10.83 - 5047 / T) / 1000; // Convert to MPa

            // Generate pressure points
            const pressures = [];
            for (let P = 0; P <= 3.0; P += 0.01) {
                pressures.push(P);
            }

            // Calculate fugacities
            const f_V = pressures.map(P => {
                if (useRealGas) {
                    return P * Math.exp(-25 * P / T);
                } else {
                    return P;
                }
            });

            // Calculate saturation fugacity
            const f_sat = useRealGas ? Psat * Math.exp(-25 * Psat / T) : Psat;

            // Create traces
            const traces = [];

            // Stable vapor
            traces.push({
                x: pressures.filter(P => P <= Psat),
                y: f_V.filter((f, i) => pressures[i] <= Psat),
                mode: 'lines',
                line: { color: '#0000FF', width: 2 },
                name: 'Stable Vapor',
                showlegend: false
            });

            // Stable liquid
            traces.push({
                x: pressures.filter(P => P >= Psat),
                y: pressures.filter(P => P >= Psat).map(() => f_sat),
                mode: 'lines',
                line: { color: '#0000FF', width: 2 },
                name: 'Stable Liquid',
                showlegend: false
            });

            // Metastable vapor
            traces.push({
                x: pressures.filter(P => P > Psat),
                y: f_V.filter((f, i) => pressures[i] > Psat),
                mode: 'lines',
                line: { color: '#A0A0FF', width: 2, dash: 'dash' },
                name: 'Metastable Vapor',
                showlegend: false
            });

            // Metastable liquid
            traces.push({
                x: pressures.filter(P => P < Psat),
                y: pressures.filter(P => P < Psat).map(() => f_sat),
                mode: 'lines',
                line: { color: '#A0A0FF', width: 2, dash: 'dash' },
                name: 'Metastable Liquid',
                showlegend: false
            });

            // Saturation point
            traces.push({
                x: [Psat],
                y: [f_sat],
                mode: 'markers',
                marker: { color: '#000000', size: 8 },
                name: 'Saturation',
                showlegend: false
            });

            // Indicator line
            traces.push({
                x: [Psat, Psat],
                y: [0, f_sat],
                mode: 'lines',
                line: { color: '#000000', width: 1, dash: 'dash' },
                name: 'Indicator',
                showlegend: false
            });

            // Layout
            const layout = {
                title: 'Fugacity versus Pressure',
                xaxis: {
                    title: 'pressure (MPa)',
                    range: [0, 3.0],
                    gridcolor: '#e0e0e0'
                },
                yaxis: {
                    title: 'fugacity (MPa)',
                    range: [0, 3.0],
                    gridcolor: '#e0e0e0'
                },
                paper_bgcolor: 'white',
                plot_bgcolor: 'white',
                font: {
                    family: 'sans-serif',
                    size: 14
                },
                annotations: [
                    {
                        x: 0.5,
                        y: 0.25,
                        text: 'vapor',
                        showarrow: false,
                        font: { size: 12 }
                    },
                    {
                        x: 2.7,
                        y: f_sat,
                        text: 'liquid',
                        showarrow: false,
                        font: { size: 12 }
                    },
                    {
                        x: Psat,
                        y: f_sat + 0.1,
                        text: 'P<sup>sat</sup>',
                        showarrow: false,
                        font: { size: 12 }
                    }
                ]
            };

            Plotly.react(plotContainer, traces, layout);
        }

        // Initial plot
        updatePlot();
    </script>
</body>
</html>
