    <script>
        // Global variables
        let theta = 0.44879;
        let R = 150;
        let vectorC = {x: R * 0.3, y: R * 0.7};
        let isDragging = false;
        let sketch;

        // Calculate vectors a and b based on theta
        function calculateVectors() {
            return {
                a: {x: R * Math.sin(theta), y: R * Math.cos(theta)},
                b: {x: -R * Math.sin(theta), y: R * Math.cos(theta)}
            };
        }

        // Calculate optimal d vector
        function calculateOptimalD(c, b) {
            let cNorm = Math.sqrt(c.x * c.x + c.y * c.y) / R;
            if (cNorm >= 1) cNorm = 0.999;

            let scale = Math.sqrt(1 - cNorm * cNorm);
            return {x: b.x * scale, y: b.y * scale};
        }

        // Calculate errors
        function calculateErrors(c, a, dOpt, b) {
            let dCA = 0.5 * ((c.x - a.x)**2 + (c.y - a.y)**2) / (R * R);
            let dDB = 0.5 * ((dOpt.x - b.x)**2 + (dOpt.y - b.y)**2) / (R * R);
            return {dCA, dDB};
        }

        // Update plot
        function updatePlot() {
            let vectors = calculateVectors();
            let dOpt = calculateOptimalD(vectorC, vectors.b);
            let errors = calculateErrors(vectorC, vectors.a, dOpt, vectors.b);

            // Generate boundary curve
            let xData = [];
            let yData = [];
            let sin2theta = Math.sin(2 * theta);
            let maxX = sin2theta * sin2theta;

            for (let i = 0; i <= 100; i++) {
                let x = maxX * i / 100;
                let y = Math.pow(sin2theta - Math.sqrt(x), 2);
                xData.push(x);
                yData.push(y);
            }

            // Prepare traces
            let traces = [
                {
                    x: xData,
                    y: yData,
                    mode: 'lines',
                    name: 'Boundary',
                    line: {color: 'blue', width: 2}
                },
                {
                    x: [errors.dCA],
                    y: [errors.dDB],
                    mode: 'markers',
                    name: 'Current',
                    marker: {color: 'red', size: 10}
                }
            ];

            // Add optimal point if checkbox is checked
            if (document.getElementById('checkbox-optimal-pair').checked) {
                let optVal = Math.pow(sin2theta / 2, 2);
                traces.push({
                    x: [optVal],
                    y: [optVal],
                    mode: 'markers',
                    name: 'Optimal',
                    marker: {color: 'green', size: 10}
                });
            }

            let layout = {
                title: 'Measurement Error Trade-off',
                xaxis: {title: 'D(C, A)', range: [0, 1.5]},
                yaxis: {title: 'D(C, B)', range: [0, 1.5]},
                showlegend: false
            };

            Plotly.react('plot-error', traces, layout);
        }

        // p5.js sketch
        let p5Sketch = function(p) {
            p.setup = function() {
                let canvas = p.createCanvas(400, 400);
                canvas.parent('canvas-container');
                p.strokeWeight(2);
                p.textAlign(p.CENTER, p.CENTER);
            };

            p.draw = function() {
                p.background(255);
                p.translate(200, 350);
                p.scale(1, -1);

                // Draw axis
                p.stroke(0);
                p.strokeWeight(1);
                p.line(-200, 0, 200, 0);

                // Draw semi-circle
                p.noFill();
                p.arc(0, 0, 2 * R, 2 * R, 0, p.PI);

                let vectors = calculateVectors();
                let dOpt = calculateOptimalD(vectorC, vectors.b);

                // Draw vectors
                drawVector(p, {x: 0, y: 0}, vectors.a, 'blue', 'a');
                drawVector(p, {x: 0, y: 0}, vectors.b, 'red', 'b');
                drawVector(p, {x: 0, y: 0}, vectorC, 'green', 'c');

                // Optional visualizations
                if (document.getElementById('checkbox-metric-circle').checked) {
                    p.push();
                    p.stroke(100, 100, 255);
                    p.strokeWeight(1);
                    p.drawingContext.setLineDash([5, 5]);
                    let radius = p.dist(vectorC.x, vectorC.y, vectors.a.x, vectors.a.y);
                    p.noFill();
                    p.circle(vectors.a.x, vectors.a.y, 2 * radius);
                    p.pop();
                }

                if (document.getElementById('checkbox-metric-region').checked || 
                    document.getElementById('checkbox-first-order-region').checked) {
                    p.push();
                    p.strokeWeight(1);
                    p.drawingContext.setLineDash([5, 5]);
                    p.noFill();

                    // Green arc for c
                    p.stroke(0, 200, 0);
                    let cRadius = p.dist(0, 0, vectorC.x, vectorC.y);
                    p.arc(0, 0, 2 * cRadius, 2 * cRadius, 0, p.PI);

                    // Orange arc for d
                    p.stroke(255, 165, 0);
                    let dRadius = p.dist(0, 0, dOpt.x, dOpt.y);
                    p.arc(0, 0, 2 * dRadius, 2 * dRadius, 0, p.PI);
                    p.pop();
                }

                if (document.getElementById('checkbox-first-order-vec').checked) {
                    p.push();
                    p.stroke(255, 165, 0);
                    p.strokeWeight(2);
                    p.drawingContext.setLineDash([5, 5]);
                    drawVector(p, vectors.b, dOpt, 'orange', 'd_err');
                    p.pop();
                }

                if (document.getElementById('checkbox-optimal-pair').checked) {
                    let sin2theta = Math.sin(2 * theta);
                    let optScale = sin2theta / 2;
                    let cOpt = {x: vectors.a.x * optScale, y: vectors.a.y * optScale};
                    let dOpt2 = {x: vectors.b.x * optScale, y: vectors.b.y * optScale};

                    p.push();
                    p.strokeWeight(2);
                    p.drawingContext.setLineDash([3, 3]);
                    drawVector(p, {x: 0, y: 0}, cOpt, 'darkgreen', 'c_opt');
                    drawVector(p, {x: 0, y: 0}, dOpt2, 'darkred', 'd_opt');
                    p.pop();
                }
            };

            p.mousePressed = function() {
                let mx = p.mouseX - 200;
                let my = 350 - p.mouseY;
                let dist = p.dist(mx, my, vectorC.x, vectorC.y);
                if (dist < 20) {
                    isDragging = true;
                }
            };

            p.mouseDragged = function() {
                if (isDragging) {
                    let mx = p.mouseX - 200;
                    let my = 350 - p.mouseY;
                    let dist = Math.sqrt(mx * mx + my * my);
                    if (dist <= R && my >= 0) {
                        vectorC.x = mx;
                        vectorC.y = my;
                        updatePlot();
                    }
                }
            };

            p.mouseReleased = function() {
                isDragging = false;
            };

            function drawVector(p, from, to, color, label) {
                p.push();
                p.stroke(color);
                p.fill(color);
                p.line(from.x, from.y, to.x, to.y);

                // Arrowhead
                let angle = p.atan2(to.y - from.y, to.x - from.x);
                p.push();
                p.translate(to.x, to.y);
                p.rotate(angle);
                p.triangle(0, 0, -10, 5, -10, -5);
                p.pop();

                // Label
                p.push();
                p.scale(1, -1);
                p.noStroke();
                p.text(label, to.x + 15, -to.y);
                p.pop();

                p.pop();
            }
        };

        // Initialize
        sketch = new p5(p5Sketch);

        // Event listeners
        document.getElementById('slider-theta').addEventListener('input', function(e) {
            theta = parseFloat(e.target.value);
            let thetaDeg = theta * 180 / Math.PI;
            document.getElementById('span-theta-value').textContent = thetaDeg.toFixed(1) + '°';
            document.getElementById('span-sinsq-theta-value').textContent = 
                (Math.sin(theta) * Math.sin(theta)).toFixed(6);
            updatePlot();
        });

        // Checkbox listeners
        ['checkbox-metric-circle', 'checkbox-metric-region', 'checkbox-first-order-vec', 
         'checkbox-first-order-region', 'checkbox-optimal-pair', 'checkbox-optimal-region'].forEach(id => {
            document.getElementById(id).addEventListener('change', updatePlot);
        });

        // Initial plot
        updatePlot();
    </script>
</body>
</html>
